<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js" defer></script>
<script defer>
  /* Global state variables */
  const canvasSize = 400;
  const DEFAULT_Q = 2;
  const DEFAULT_P = 0.0; // start at start config

  let sliderQ, sliderP, btnReset;
  let valueQ, valueP, phaseSpaceTitle;

  // Robot parameters
  const robotBase = { x: canvasSize / 2, y: canvasSize / 2 };
  const link1Len = 75, link2Len = 50;
  const linkRadius = 10;
  const obstaclesDefault = [
    { x: 200, y: 100, radius: 15 },
    { x: 200, y 300, radius: 15 }
  ];
  let obstacles = [];

  // Configurations
  const startConfigDefault = { q1: -1.5, q2: 0.5 };
  const goalConfigDefault = { q1: 1.5, q2: 1.0 };
  let startConfig = { ...startConfigDefault };
  let goalConfig = { ...goalConfigDefault };

  // Interaction flags
  let isDraggingObstacle = [false, false];
  let isDraggingStart = false;
  let isDraggingGoal = false;

  // Phase space
  let gridN = 0;                           // grid resolution
  let cellSize = 0;
  let grid = [];                           // 2D array of cell objects
  let path = [];                           // array of config objects from start to goal
  pathLength = 0;
  let currentPathIndex = 0;                // index in path corresponding to slider P

  /* Utility functions */
  function map(val, inMin, inMax, outMin, outMax) {
    return (val - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
  }

  function clamp(val, min, max) {
    return Math.max(min, Math.min(max, val));
  }

  function lerp(a, b, t) { return a + t * (b - a); }

  function angleBetween(a, b) {
    const dq1 = a.q1 - b.q1;
    const dq2 = a.q2 - b.q2;
    return Math.hypot(dq1, dq2);
  }

  function nearestIndex(q) {
    // Map angle [-PI, PI] to [0, gridN-1]
    return Math.round(map(q + Math.PI, 0, 2 * Math.PI, 0, gridN - 1));
  }

  function cellFromAngles(q1, q2) {
    const i = nearestIndex(q1);
    const j = nearestIndex(q2);
    return { i, j, idx: i * gridN + j };
  }

  function collisionForConfig(q1, q2) {
    // compute positions of link ends
    const x1 = robotBase.x + link1Len * Math.cos(q1);
    const y1 = robotBase.y + link1Len * Math.sin(q1);
    const x2 = x1 + link2Len * Math.cos(q1 + q2);
    const y2 = y1 + link2Len * Math.sin(q1 + q2);

    // helper to compute minimal distance from point to segment
    function segPointDist(px, py, ax, ay, bx, by) {
      const abx = bx - ax, aby = by - ay;
      const apx = px - ax, apy = py - ay;
      const ab2 = abx * abx + aby * aby;
      const t = ab2 === 0 ? 0 : clamp((apx * abx + apy * aby) / ab2, 0, 1);
      const cx = ax + t * abx;
      const cy = ay + t * aby;
      return Math.hypot(px - cx, py - cy);
    }

    // Check link1 against each obstacle
    for (const obs of obstacles) {
      const d1 = segPointDist(obs.x, obs.y, robotBase.x, robotBase.y, x1, y1);
      if (d1 < linkRadius + obs.radius) return true;

      // Check link2
      const d2 = segPointDist(obs.x, obs.y, x1, y1, x2, y2);
      if (d2 < linkRadius + obs.radius) return true;
    }
    return false;
  }

  function computeGridAndPath() {
    gridN = Math.pow(2, DEFAULT_Q) * 10; // N = 10 * 2^Q
    cellSize = canvasSize / gridN;
    grid = [];

    // Build grid
    for (let i = 0; i < gridN; i++) {
      const row = [];
      const q1 = map(i, 0, gridN - 1, -Math.PI, Math.PI);
      for (let j = 0; j < gridN; j++) {
        const q2 = map(j, 0, gridN - 1, -Math.PI, Math.PI);
        const coll = collisionForConfig(q1, q2);
        row.push({ q1, q2, coll, dist: Infinity, parent: null });
      }
      grid.push(row);
    }

    // Find goal cell
    const { i: goalI, j: goalJ } = cellFromAngles(goalConfig.q1, goalConfig.q2);
    const goalCell = grid[goalI][goalJ];
    // BFS
    const queue = [];
    goalCell.dist = 0;
    queue.push({ i: goalI, j: goalJ });

    const dirs = [
      { di: -1, dj: 0 }, { di: 1, dj: 0 },
      { di: 0, dj: -1 }, { di: 0, dj: 1 }
    ];

    while (queue.length) {
      const { i, j } = queue.shift();
      const currCell = grid[i][j];
      for (const { di, dj } of dirs) {
        const ni = i + di, nj = j + dj;
        if (ni < 0 || ni >= gridN || nj < 0 || nj >= gridN) continue;
        const neigh = grid[ni][nj];
        if (neigh.coll || neigh.dist !== Infinity) continue;
        neigh.dist = currCell.dist + 1;
        neigh.parent = { i, j };
        queue.push({ i: ni, j: nj });
      }
    }

    // Path reconstruction from start to goal
    const startIdx = cellFromAngles(startConfig.q1, startConfig.q2);
    let curCell = grid[startIdx.i][startIdx.j];
    if (curCell.coll) {
      path = []; // No path
      pathLength = Infinity;
      return;
    }
    path = [];
    let cell = curCell;
    while (cell || (cell && (!cell.parent && !cell.q1 === null))) {
      path.push({ q1: cell.q1, q2: cell.q2 });
      if (!cell.parent) break;
      cell = grid[cell.parent.i][cell.parent.j];
    }
    path.reverse(); // start -> goal

    // Compute path length
    pathLength = 0;
    for (let k = 0; k < path.length - 1; k++) {
      pathLength += angleBetween(path[k], path[k + 1]);
    }

    // Update title
    phaseSpaceTitle.textContent = `robot phase space, path length = ${pathLength.toFixed(2)} rads`;
  }

  function updateCurrentPathIndex() {
    // sliderP: 0->1
    if (path.length === 0) { currentPathIndex = 0; return; }
    const idx = sliderP.value * (path.length - 1);
    currentPathIndex = Math.floor(idx + 0.5);
  }

  function getCurrentAngles() {
    if (path.length === 0) return { q1: startConfig.q1, q2: startConfig.q2 };
    const idx = sliderP.value * (path.length - 1);
    const i1 = Math.floor(idx);
    const i2 = Math.min(i1 + 1, path.length - 1);
    const t = idx - i1;
    const a1 = path[i1], a2 = path[i2];
    return { q1: lerp(a1.q1, a2.q1, t), q2: lerp(a1.q2, a2.q2, t) };
  }

  /* DOM and p5 setup */
  window.addEventListener('DOMContentLoaded', () => {
    sliderQ = document.getElementById('slider-q');
    sliderP = document.getElementById('slider-p');
    btnReset = document.getElementById('btn-reset');
    valueQ = document.getElementById('value-q');
    valueP = document.getElementById('value-p');
    phaseSpaceTitle = document.getElementById('phase-space-title');

    // Initialize sliders
    sliderQ.min = 1; sliderQ.max = 7; sliderQ.step = 1; sliderQ.value = DEFAULT_Q;
    sliderP.min = 0; sliderP.max = 1; sliderP.step = 0.01; sliderP.value = DEFAULT_P;
    valueQ.textContent = sliderQ.value;
    valueP.textContent = parseFloat(sliderP.value).toFixed(2);

    // Reset button
    btnReset.addEventListener('click', () => {
      sliderQ.value = DEFAULT_Q;
      sliderP.value = DEFAULT_P;
      valueQ.textContent = sliderQ.value;
      valueP.textContent = parseFloat(sliderP.value).toFixed(2);
      obstacles = obstaclesDefault.map(o => ({ ...o }));
      startConfig = { ...startConfigDefault };
      goalConfig = { ...goalConfigDefault };
      computeGridAndPath();
    });

    // Slider events
    sliderQ.addEventListener('input', () => {
      valueQ.textContent = sliderQ.value;
      DEFAULT_Q = parseInt(sliderQ.value);
      computeGridAndPath();
    });

    sliderP.addEventListener('input', () => {
      valueP.textContent = parseFloat(sliderP.value).toFixed(2);
      updateCurrentPathIndex();
    });

    // Initialize obstacles
    obstacles = obstaclesDefault.map(o => ({ ...o }));
    computeGridAndPath();
  });

  /* Workspace p5 sketch */
  const workspaceSketch = new p5((s) => {
    s.setup = function () {
      const canvas = s.createCanvas(canvasSize, canvasSize);
      canvas.parent('canvas-workspace');
      s.angleMode(s.RADIANS);
    };
    s.draw = function () {
      s.background('#F0F0F0');

      // Draw obstacles
      s.noStroke();
      for (const obs of obstacles) {
        s.fill(139, 69, 19); // brown
        s.ellipse(obs.x, obs.y, obs.radius * 2);
      }

      // Draw robot
      const angles = getCurrentAngles();
      const { q1, q2 } = angles;
      s.push();
      s.translate(robotBase.x, robotBase.y);
      s.stroke(0);
      s.fill(200);
      // Link1
      s.push();
      s.rotate(q1);
      s.rectMode(s.CENTER);
      s.rect(0, 0, link1Len, linkRadius * 2);
      // Link2
      s.translate(link1Len, 0);
      s.rotate(q2);
      s.rect(0, 0, link2Len, linkRadius * 2);
      // Text on link2
      s.fill(0);
      s.textSize(12);
      s.textAlign(s.CENTER, s.CENTER);
      s.text('Robot Arm', 0, 0);
      s.pop();
      s.pop();

      // Display angles
      s.fill(0);
      s.noStroke();
      s.textSize(12);
      s.text(`θ1 = ${q1.toFixed(2)} rad`, 10, 20);
      s.text(`θ2 = ${q2.toFixed(2)} rad`, 10, 35);
    };
  }, document.getElementById('canvas-workspace'));

  /* Phase space p5 sketch */
  const phaseSpaceSketch = new p5((s) => {
    s.setup = function () {
      const canvas = s.createCanvas(canvasSize, canvasSize);
      canvas.parent('canvas-phase-space');
      s.noSmooth();
    };
    s.draw = function () {
      s.background(255);

      // Draw grid cells
      for (let i = 0; i < gridN; i++) {
        for (let j = 0; j < gridN; j++) {
          const cell = grid[i][j];
          const x = i * cellSize;
          const y = j * cellSize;
          if (cell.coll) {
            s.fill('#CC0000');
          } else {
            const maxDist = Math.max(...grid.flat().map(c => c.dist));
            const gray = map(cell.dist, 0, maxDist, 255, 0);
            s.fill(gray);
          }
          s.stroke(200);
          s.rect(x, y, cellSize, cellSize);
        }
      }

      // Draw path
      if (path.length > 1) {
        s.stroke('#00FF00');
        s.strokeWeight(2);
        s.noFill();
        s.beginShape();
        for (const p of path) {
          const { i, j } = cellFromAngles(p.q1, p.q2);
          const x = i * cellSize + cellSize / 2;
          const y = j * cellSize + cellSize / 2;
          s.vertex(x, y);
        }
        s.endShape();
      }

      // Draw start and goal markers
      function drawMarker(cfg, color) {
        const { i, j } = cellFromAngles(cfg.q1, cfg.q2);
        const x = i * cellSize + cellSize / 2;
        const y = j * cellSize + cellSize / 2;
        s.fill(color);
        s.stroke(0);
        s.ellipse(x, y, 12, 12);
        s.stroke(255);
        s.line(x - 3, y, x + 3, y);
        s.line(x, y - 3, x, y + 3);
      }
      drawMarker(startConfig, '#00FF00');
      drawMarker(goalConfig, '#00FF00');

      // Current position marker
      if (path.length > 0) {
        const cfg = path[currentPathIndex];
        const { i, j } = cellFromAngles(cfg.q1, cfg.q2);
        const x = i * cellSize + cellSize / 2;
        const y = j * cellSize + cellSize / 2;
        s.fill('#0000FF');
        s.noStroke();
        s.ellipse(x, y, 6, 6);
      }
    };

    s.mousePressed = function () {
      // Detect obstacles drag
      const mx = s.mouseX;
      const my = s.mouseY;
      for (let k = 0; k < obstacles.length; k++) {
        const obs = obstacles[k];
        const d = s.dist(mx, my, obs.x, obs.y);
        if (d < 15 + 5) {
          isDraggingObstacle[k] = true;
        }
      }

      // Detect start/goal markers
      const distStart = s.dist(mx, my,
        cellFromAngles(startConfig.q1, startConfig.q2).i * cellSize + cellSize / 2,
        cellFromAngles(startConfig.q1, startConfig.q2).j * cellSize + cellSize / 2);
      if (distStart < 6) { isDraggingStart = true; return; }

      const distGoal = s.dist(mx, my,
        cellFromAngles(goalConfig.q1, goalConfig.q2).i * cellSize + cellSize / 2,
        cellFromAngles(goalConfig.q1, goalConfig.q2).j * cellSize + cellSize / 2);
      if (distGoal < 6) { isDraggingGoal = true; return; }
    };

    s.mouseDragged = function () {
      if (isDraggingObstacle.some(v => v)) {
        for (let k = 0; k < obstacles.length; k++) {
          if (isDraggingObstacle[k]) {
            obstacles[k].x = s.mouseX;
            obstacles[k].y = s.mouseY;
          }
        }
        computeGridAndPath();
      }

      if (isDraggingStart) {
        const i = Math.floor(s.mouseX / cellSize);
        const j = Math.floor(s.mouseY / cellSize);
        if (i >= 0 && i < gridN && j >= 0 && j < gridN) {
          const cfg = grid[i][j];
          if (!cfg.coll) {
            startConfig.q1 = cfg.q1;
            startConfig.q2 = cfg.q2;
            computeGridAndPath();
          }
        }
      }

      if (isDraggingGoal) {
        const i = Math.floor(s.mouseX / cellSize);
        const j = Math.floor(s.mouseY / cellSize);
        if (i >= 0 && i < gridN && j >= 0 && j < gridN) {
          const cfg = grid[i][j];
          if (!cfg.coll) {
            goalConfig.q1 = cfg.q1;
            goalConfig.q2 = cfg.q2;
            computeGridAndPath();
          }
        }
      }
    };

    s.mouseReleased = function () {
      isDraggingObstacle = [false, false];
      isDraggingStart = false;
      isDraggingGoal = false;
    };
  }, document.getElementById('canvas-phase-space'));
</script>
</head>
