<!-- Include p5.js -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>
<script>
/*-----------------------------------------------
 Global State Variables
-----------------------------------------------*/
let dimension = '2D';          // '2D' or '3D'
let algorithm = 'prim';       // 'prim' or 'kruskal'
let animationState = 'stopped'; // 'stopped', 'playing', 'paused'
let seed = 525;
let numPoints = 40;
let points = [];              // p5.Vector array
let mstEdges = [];            // Array of edges {i, j, weight}
let stepIndex = 0;            // Number of edges currently drawn
let sketchInstance = null;    // Reference to the p5.js instance

/*-----------------------------------------------
 Utility Functions
-----------------------------------------------*/
function setActive(button, isActive) {
  if (isActive) button.classList.add('active');
  else button.classList.remove('active');
}

function updateButtonStates() {
  // Play/Reset enabled when not playing; Pause enabled only when playing
  const playBtn = document.getElementById('btn-play');
  const pauseBtn = document.getElementById('btn-pause');
  const resetBtn = document.getElementById('btn-reset');
  if (animationState === 'playing') {
    playBtn.disabled = true;
    pauseBtn.disabled = false;
  } else {
    playBtn.disabled = false;
    pauseBtn.disabled = true;
  }
  resetBtn.disabled = false;
}

function setSliderValue(sliderId, spanId, defaultValue) {
  const slider = document.getElementById(sliderId);
  slider.value = defaultValue;
  document.getElementById(spanId).textContent = defaultValue;
}

function debounce(fn, delay){
  let timer;
  return function(...args){
    clearTimeout(timer);
    timer = setTimeout(()=>fn.apply(this,args),delay);
  }
}

/*-----------------------------------------------
 Core Algorithms
-----------------------------------------------*/
function generatePoints() {
  p5.randomSeed(seed);
  points = [];
  const w = 600, h = 600;
  if (dimension === '2D') {
    for (let i = 0; i < numPoints; i++) {
      const x = p5.random(50, w - 50);
      const y = p5.random(50, h - 50);
      points.push(p5.createVector(x, y));
    }
  } else {
    // 3D
    const margin = 150;
    for (let i = 0; i < numPoints; i++) {
      const x = p5.random(-margin, margin);
      const y = p5.random(-margin, margin);
      const z = p5.random(-margin, margin);
      points.push(p5.createVector(x, y, z));
    }
  }
}

function computePrimEdges() {
  const visited = new Set();
  visited.add(0);
  const edges = [];
  while (visited.size < points.length) {
    let best = null;
    for (let vi of visited) {
      for (let uj = 0; uj < points.length; uj++) {
        if (visited.has(uj)) continue;
        const dist = p5.dist(
          points[vi].x, points[vi].y, points[vi].z,
          points[uj].x, points[uj].y, points[uj].z
        );
        if (!best || dist < best.weight) {
          best = { i: vi, j: uj, weight: dist };
        }
      }
    }
    if (best) {
      edges.push(best);
      visited.add(best.j);
    }
  }
  return edges;
}

function computeKruskalEdges() {
  const allEdges = [];
  for (let i = 0; i < points.length; i++) {
    for (let j = i + 1; j < points.length; j++) {
      const dist = p5.dist(
        points[i].x, points[i].y, points[i].z,
        points[j].x, points[j].y, points[j].z
      );
      allEdges.push({ i, j, weight: dist });
    }
  }
  allEdges.sort((a, b) => a.weight - b.weight);
  // Union-Find
  const parent = Array.from({ length: points.length }, (_, idx) => idx);
  const find = (x) => {
    if (parent[x] !== x) parent[x] = find(parent[x]);
    return parent[x];
  };
  const unite = (a, b) => {
    const ra = find(a), rb = find(b);
    if (ra !== rb) parent[rb] = ra;
  };
  const mst = [];
  for (let e of allEdges) {
    if (find(e.i) !== find(e.j)) {
      unite(e.i, e.j);
      mst.push(e);
      if (mst.length === points.length - 1) break;
    }
  }
  return mst;
}

/*-----------------------------------------------
 Animation / Reset Logic
-----------------------------------------------*/
function resetAll() {
  animationState = 'stopped';
  stepIndex = 0;
  generatePoints();
  mstEdges = algorithm === 'prim' ? computePrimEdges() : computeKruskalEdges();
  updateButtonStates();
}

function resetAnimationOnly() {
  animationState = 'stopped';
  stepIndex = 0;
  mstEdges = algorithm === 'prim' ? computePrimEdges() : computeKruskalEdges();
  updateButtonStates();
}

function startAnimation() {
  if (stepIndex >= mstEdges.length) return;
  animationState = 'playing';
  updateButtonStates();
}

function pauseAnimation() {
  animationState = 'paused';
  updateButtonStates();
}

function restartAnimation() {
  stepIndex = 0;
  animationState = 'stopped';
  updateButtonStates();
}

/*-----------------------------------------------
 p5.js Sketch
-----------------------------------------------*/
function createSketch() {
  if (sketchInstance) {
    sketchInstance.remove();
    sketchInstance = null;
  }
  sketchInstance = new p5((p) => {
    p.setup = () => {
      const w = 600, h = 600;
      if (dimension === '2D') p.createCanvas(w, h);
      else p.createCanvas(w, h, p.WEBGL);
      p.noLoop();
    };

    p.draw = () => {
      p.background('#E0E0E0');
      p.strokeWeight(2);
      if (dimension === '2D') {
        // Draw edges
        p.stroke('#FF0000');
        for (let e of mstEdges.slice(0, stepIndex)) {
          const a = points[e.i];
          const b = points[e.j];
          p.line(a.x, a.y, b.x, b.y);
        }
        // Draw points
        p.noStroke();
        p.fill('#0000FF');
        for (let pt of points) {
          p.ellipse(pt.x, pt.y, 8, 8);
        }
      } else {
        // 3D
        p.rotateY(p.frameCount * 0.005);
        p.stroke('#FF0000');
        for (let e of mstEdges.slice(0, stepIndex)) {
          const a = points[e.i];
          const b = points[e.j];
          p.line(a.x, a.y, a.z, b.x, b.y, b.z);
        }
        p.noStroke();
        p.fill('#0000FF');
        for (let pt of points) {
          p.push();
          p.translate(pt.x, pt.y, pt.z);
          p.sphere(8);
          p.pop();
        }
        // Draw cube wireframe
        p.noFill();
        p.stroke('#888888');
        p.box(350);
      }
    };

    p.keyPressed = () => {
      // Optional: Allow space to toggle play/pause
      if (p.key === ' ') {
        if (animationState === 'playing') pauseAnimation();
        else if (animationState !== 'stopped') startAnimation();
      }
    };

    p.frameRate(30);
  }, 'canvas-container');
}

/*-----------------------------------------------
 Event Listeners
-----------------------------------------------*/
document.addEventListener('DOMContentLoaded', () => {
  // Set defaults
  setSliderValue('slider-seed', 'seed-value', 525);
  setSliderValue('slider-number', 'number-value', 40);

  // Dimension Buttons
  document.getElementById('btn-2d').addEventListener('click', () => {
    if (dimension !== '2D') {
      dimension = '2D';
      setActive(document.getElementById('btn-2d'), true);
      setActive(document.getElementById('btn-3d'), false);
      resetAll();    // full reset including points and edges
      createSketch();
    }
  });
  document.getElementById('btn-3d').addEventListener('click', () => {
    if (dimension !== '3D') {
      dimension = '3D';
      setActive(document.getElementById('btn-3d'), true);
      setActive(document.getElementById('btn-2d'), false);
      resetAll();
      createSketch();
    }
  });

  // Seed slider
  const seedSlider = document.getElementById('slider-seed');
  const seedSpan = document.getElementById('seed-value');
  seedSlider.addEventListener('input', debounce(() => {
    seed = Number(seedSlider.value);
    seedSpan.textContent = seed;
    resetAll(); // full reset
  }, 300));

  // Number slider
  const numSlider = document.getElementById('slider-number');
  const numSpan = document.getElementById('number-value');
  numSlider.addEventListener('input', debounce(() => {
    numPoints = Number(numSlider.value);
    numSpan.textContent = numPoints;
    resetAll(); // full reset
  }, 300));

  // Algorithm Buttons
  document.getElementById('btn-prim').addEventListener('click', () => {
    if (algorithm !== 'prim') {
      algorithm = 'prim';
      setActive(document.getElementById('btn-prim'), true);
      setActive(document.getElementById('btn-kruskal'), false);
      resetAnimationOnly(); // partial reset
    }
  });
  document.getElementById('btn-kruskal').addEventListener('click', () => {
    if (algorithm !== 'kruskal') {
      algorithm = 'kruskal';
      setActive(document.getElementById('btn-kruskal'), true);
      setActive(document.getElementById('btn-prim'), false);
      resetAnimationOnly(); // partial reset
    }
  });

  // Process Buttons
  document.getElementById('btn-play').addEventListener('click', () => startAnimation());
  document.getElementById('btn-pause').addEventListener('click', () => pauseAnimation());
  document.getElementById('btn-reset').addEventListener('click', () => {
    restartAnimation();
    // Force a redraw to clear edges
    sketchInstance.redraw();
  });

  // Animation loop handled in p5 draw
  // We'll increment stepIndex on each frame when playing
  createSketch(); // initial sketch
  resetAll();     // generate initial points and edges
});

/*-----------------------------------------------
 p5.js Draw Loop Enhancements (playing logic)
-----------------------------------------------*/
p5.prototype.defaultSetup = p5.prototype.setup;
p5.prototype.defaultDraw = p5.prototype.draw;
p5.prototype.setup = function() {
  this.defaultSetup.apply(this, arguments);
};
p5.prototype.draw = function() {
  this.defaultDraw.apply(this, arguments);
  // Increment frame-based step for playing
  if (animationState === 'playing') {
    if (frameCount % 10 === 0) {
      stepIndex++;
      if (stepIndex >= mstEdges.length) {
        // Finished
        animationState = 'stopped';
        updateButtonStates();
      }
      this.redraw();
    }
  }
};

</script>
</body>
</html>
