<body>
<div id="main-container">
    <div id="decorative-plus-btn">+</div>
    <div id="control-panel">
        <!-- Method selection -->
        <div>
            <label for="select-method">method</label>
            <select id="select-method">
                <option value="estimation">estimation</option>
                <option value="place values">place values</option>
            </select>
        </div>
        <hr>
        <!-- First number slider -->
        <div>
            <label>first number</label>
            <div class="slider-container">
                <label for="slider-num1">numerical value</label>
                <input type="range" id="slider-num1" min="0" max="20" step="0.1" value="1.1">
                <span>+</span>
            </div>
        </div>
        <hr>
        <!-- Second number controls -->
        <div>
            <label>multiply by</label>
            <div class="toggle-buttons">
                <label>integers only</label>
                <button id="btn-int-yes" class="active">yes</button>
                <button id="btn-int-no" class="inactive">no</button>
            </div>
            <div class="slider-container">
                <label for="slider-num2">numerical value</label>
                <input type="range" id="slider-num2" min="0" max="20" step="1" value="2">
                <span>+</span>
            </div>
        </div>
    </div>
    <div id="output-display">
        <!-- Initial content generated by script -->
    </div>
</div>
<script>
    // Utility functions
    function intStr(num){
        const s = num.toString();
        return s.includes('.') ? s.replace('.','') : s;
    }
    function factor(num){
        const s = num.toString();
        if(s.includes('.')){
            const dec = s.split('.')[1];
            return Math.pow(10, dec.length);
        }
        return 1;
    }
    function renderIntWithDot(str, fac){
        return fac===1 ? str : str+'.';
    }
    // Main display update
    function updateDisplay(){
        const method = document.getElementById('select-method').value;
        const num1 = parseFloat(document.getElementById('slider-num1').value);
        const num2 = parseFloat(document.getElementById('slider-num2').value);

        const num1_str = num1.toString();
        const num2_str = num2.toString();

        const num1_int = intStr(num1);
        const num2_int = intStr(num2);
        const num1_factor = factor(num1);
        const num2_factor = factor(num2);
        const product_of_ints = parseInt(num1_int,10)*parseInt(num2_int,10);
        const product_of_factors = num1_factor * num2_factor;
        const num1_round = Math.round(num1);
        const num2_round = Math.round(num2);
        const product_of_rounded = num1_round * num2_round;
        const product = num1 * num2;

        const intStr1 = renderIntWithDot(num1_int, num1_factor);
        const intStr2 = renderIntWithDot(num2_int, num2_factor);

        const steps = [];
        if(method==='estimation'){
            steps.push(`start with: ${num1_str} × ${num2_str}`);
            steps.push(`convert to: ${intStr1} × ${intStr2} = ${product_of_ints}.`);
            steps.push(`estimate as: ${num1_round} × ${num2_round} = ${product_of_rounded}`);
            steps.push(`result is: ${product}`);
        } else if(method==='place values'){
            steps.push(`start with: ${num1_str} × ${num2_str}`);
            steps.push(`rewrite as: (${intStr1} ÷ ${num1_factor}) × (${intStr2} ÷ ${num2_factor})`);
            steps.push(`= (${intStr1} × ${intStr2}) ÷ (${num1_factor} × ${num2_factor})`);
            steps.push(`= ${product_of_ints}. ÷ ${product_of_factors}`);
            steps.push(`= ${product}`);
        }
        const outputDiv = document.getElementById('output-display');
        outputDiv.innerHTML = steps.map(s=>`<p>${s}</p>`).join('');
    }

    // Event listeners
    document.getElementById('select-method').addEventListener('change', updateDisplay);
    document.getElementById('slider-num1').addEventListener('input', updateDisplay);
    document.getElementById('slider-num2').addEventListener('input', updateDisplay);

    document.getElementById('btn-int-yes').addEventListener('click', function(){
        this.classList.add('active');
        this.classList.remove('inactive');
        document.getElementById('btn-int-no').classList.add('inactive');
        document.getElementById('btn-int-no').classList.remove('active');
        const slider = document.getElementById('slider-num2');
        slider.step = 1;
        slider.value = Math.round(parseFloat(slider.value));
        updateDisplay();
    });

    document.getElementById('btn-int-no').addEventListener('click', function(){
        this.classList.add('active');
        this.classList.remove('inactive');
        document.getElementById('btn-int-yes').classList.add('inactive');
        document.getElementById('btn-int-yes').classList.remove('active');
        document.getElementById('slider-num2').step = 0.1;
        updateDisplay();
    });

    // Initialize active/inactive states
    document.getElementById('btn-int-yes').classList.add('active');
    document.getElementById('btn-int-no').classList.add('inactive');

    // Initial display
    updateDisplay();
</script>
</body>
</html>
