</head>
<body>
<div id="demo-container">
  <div id="control-panel">
    <select id="select-vibration-mode">
      <option value="symmetric-stretch" selected>symmetrical stretch</option>
      <option value="asymmetric-stretch">stretch (triply degenerate)</option>
      <option value="bending-e">bend (doubly degenerate)</option>
      <option value="bending-f2">bend (triply degenerate)</option>
      <option value="ir-spectrum">IR spectrum</option>
    </select>
    <span>run</span>
    <button id="btn-play">►</button>
    <button id="btn-pause">||</button>
    <button id="btn-reset">|◄</button>
  </div>
  <div id="visualization-area">
    <div id="three-js-canvas-container"></div>
    <div id="plotly-chart-container" class="hidden"></div>
  </div>
  <div id="description-area">
    <p id="label-left" style="color:#0000ff;">stretching A₁ Raman active 2916 cm⁻¹</p>
    <p id="label-right" style="color:#0000ff;">no change in the molecular dipole moment</p>
  </div>
</div>

<!-- CDNs -->
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r152/three.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r152/examples/js/controls/OrbitControls.min.js"></script>

<script>
(() => {
  /*----------------------------------------------
    GLOBAL VARIABLES & CONSTANTS
  ----------------------------------------------*/
  const container = document.getElementById('three-js-canvas-container');
  const plotContainer = document.getElementById('plotly-chart-container');
  const selectMode = document.getElementById('select-vibration-mode');
  const btnPlay = document.getElementById('btn-play');
  const btnPause = document.getElementById('btn-pause');
  const btnReset = document.getElementById('btn-reset');
  const labelLeft = document.getElementById('label-left');
  const labelRight = document.getElementById('label-right');

  const renderer = new THREE.WebGLRenderer({ antialias:true, preserveDrawingBuffer:true });
  renderer.setSize(container.clientWidth, container.clientHeight);
  container.appendChild(renderer.domElement);

  // Hamilton: Two cameras and two controls
  const leftCamera = new THREE.PerspectiveCamera(45, 0.5, 0.1, 1000);
  leftCamera.position.set(5,5,5);
  const rightCamera = new THREE.PerspectiveCamera(45, 0.5, 0.1, 1000);
  rightCamera.position.set(5,5,5);

  const leftControls = new THREE.OrbitControls(leftCamera, renderer.domElement);
  const rightControls = new THREE.OrbitControls(rightCamera, renderer.domElement);

  const sceneMolecule = new THREE.Scene();
  const sceneVectors = new THREE.Scene();

  const ambientLight = new THREE.AmbientLight(0xffffff, 0.7);
  const dirLight = new THREE.DirectionalLight(0xffffff, 0.5);
  dirLight.position.set(5,10,7.5);
  sceneMolecule.add(ambientLight, dirLight);
  sceneVectors.add(dirLight.clone());

  // Base geometry
  const atomRadius = 0.2;
  const bondRadius = 0.05;
  const atomCGeo = new THREE.SphereGeometry(atomRadius, 32, 32);
  const atomHGeo = new THREE.SphereGeometry(atomRadius, 32, 32);
  const bondGeo = new THREE.CylinderGeometry(bondRadius, bondRadius, 1, 12, 1);
  const bondMat = new THREE.MeshBasicMaterial({ color:0x888888});
  const atomCMat = new THREE.MeshLambertMaterial({ color:0x000000});
  const atomHMat = new THREE.MeshLambertMaterial({ color:0x66ccff});

  // Initial positions (tetrahedral, scaled for visibility)
  const carbonPos = new THREE.Vector3(0,0,0);
  const hPositions0 = [
    new THREE.Vector3(1,1,1).normalize().multiplyScalar(1.09),
    new THREE.Vector3(1,-1,-1).normalize().multiplyScalar(1.09),
    new THREE.Vector3(-1,1,-1).normalize().multiplyScalar(1.09),
    new THREE.Vector3(-1,-1,1).normalize().multiplyScalar(1.09)
  ];

  // Store atoms & bonds for easy reference
  const atoms = [];
  const bonds = [];

  // Create Carbon atom
  const carbon = new THREE.Mesh(atomCGeo, atomCMat);
  carbon.position.copy(carbonPos);
  sceneMolecule.add(carbon);
  atoms.push(carbon);

  // Create Hydrogen atoms
  const hMeshes = [];
  for(let i=0;i<4;i++){
    const hMesh = new THREE.Mesh(atomHGeo, atomHMat);
    hMesh.position.copy(hPositions0[i]);
    sceneMolecule.add(hMesh);
    hMeshes.push(hMesh);
  }

  // Create bonds
  function createBond(geo, mat, from, to){
    const bond = new THREE.Mesh(geo.clone(), mat.clone());
    updateBond(bond, from, to);
    sceneMolecule.add(bond);
    bonds.push(bond);
  }
  for(let i=0;i<4;i++){
    createBond(bondGeo, bondMat, carbonPos, hPositions0[i]);
  }

  // Vector Scene: arrows and net dipole
  const arrowHelpers = [];
  const arrowLengthScale = 0.5; // Scale factor to visualise dipole magnitudes
  const arrowMat = new THREE.MeshLambertMaterial({ color:0x00ff00 });

  function createArrow(dir, len, color){
    const arrowHelper = new THREE.ArrowHelper(dir.clone().normalize(), new THREE.Vector3(0,0,0), len, color, 0.1, 0.05);
    sceneVectors.add(arrowHelper);
    return arrowHelper;
  }

  // Individual dipole vectors (4)
  for(let i=0;i<4;i++){
    const dir = hPositions0[i].clone().normalize();
    const len = dir.length(); // will be updated
    const arrow = createArrow(dir, len, 0x0000ff);
    arrowHelpers.push(arrow);
  }

  // Net dipole vector - initially hidden
  let netDipole = new THREE.Vector3(0,0,0);
  const netArrow = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,0), 0, 0xff0000, 0.2, 0.1);
  netArrow.visible = false;
  sceneVectors.add(netArrow);

  // Animation state
  let animationPlaying = false;
  let animationStartTime = null;
  let elapsedTime = 0;
  const amplitude = 0.1; // max displacement relative to bond length
  const frequency = 0.3; // oscillations per second

  // Mode configurations
  const modes = {
    'symmetric-stretch': {
      name: 'stretching A₁ Raman active 2916 cm⁻¹',
      dipole: 'no change in the molecular dipole moment',
      color: 0x0000ff,
      irActive: false,
      // displacement vectors (per H) function
      getDisplace: (phase)=>hPositions0.map(dir=>dir.clone().normalize())
    },
    'asymmetric-stretch': {
      name: 'stretching F₂ IR active 3019 cm⁻¹',
      dipole: 'change in the molecular dipole moment',
      color: 0xff0000,
      irActive: true,
      getDisplace: (phase)=>[
        hPositions0[0].clone().normalize(),                  // outward
        hPositions0[1].clone().normalize(),                  // outward
        hPositions0[2].clone().normalize().multiplyScalar(-1), // inward
        hPositions0[3].clone().normalize().multiplyScalar(-1)  // inward
      ]
    },
    'bending-e': {
      name: 'bending E Raman active 1534 cm⁻¹',
      dipole: 'no change in the molecular dipole moment',
      color: 0x0000ff,
      irActive: false,
      getDisplace: (phase)=>[
        new THREE.Vector3(0,0,1), // example perpendicular
        new THREE.Vector3(0,1,0),
        new THREE.Vector3(-1,0,0),
        new THREE.Vector3(0,-1,0)
      ]
    },
    'bending-f2': {
      name: 'bending F₂ IR active 1306 cm⁻¹',
      dipole: 'change in the molecular dipole moment',
      color: 0xff0000,
      irActive: true,
      getDisplace: (phase)=>[
        new THREE.Vector3(0,1,1).normalize(),
        new THREE.Vector3(1,0,1).normalize(),
        new THREE.Vector3(-1,0,-1).normalize(),
        new THREE.Vector3(0,-1,-1).normalize()
      ]
    }
  };

  /*----------------------------------------------
    UTILS
  ----------------------------------------------*/
  function updateBond(bond, from, to){
    const direction = new THREE.Vector3().subVectors(to, from);
    const length = direction.length();
    bond.scale.set(1, length, 1);
    // Align Y axis to direction
    const quaternion = new THREE.Quaternion().setFromUnitVectors(
        new THREE.Vector3(0,1,0), direction.clone().normalize()
    );
    bond.setRotationFromQuaternion(quaternion);
    // Position at midpoint
    const mid = new THREE.Vector3().addVectors(from, to).multiplyScalar(0.5);
    bond.position.copy(mid);
  }

  function updateScene(){
    const modeKey = selectMode.value;
    const mode = modes[modeKey];
    const phase = elapsedTime * Math.PI * frequency * 2; // 2πf
    const dispVecs = mode.getDisplace(phase);

    // Update H positions
    for(let i=0;i<4;i++){
      const delta = dispVecs[i].clone().multiplyScalar(amplitude);
      hMeshes[i].position.copy(hPositions0[i].clone().add(delta));
    }

    // Update bonds
    for(let i=0;i<4;i++){
      updateBond(bonds[i], carbonPos, hMeshes[i].position);
    }

    // Update individual dipole arrows
    for(let i=0;i<4;i++){
      const vec = new THREE.Vector3().subVectors(hMeshes[i].position, carbonPos);
      const len = vec.length() * arrowLengthScale;
      arrowHelpers[i].setDirection(vec.clone().normalize());
      arrowHelpers[i].setLength(len);
    }

    // Net dipole
    netDipole.set(0,0,0);
    for(let i=0;i<4;i++){
      const vec = new THREE.Vector3().subVectors(hMeshes[i].position, carbonPos);
      netDipole.add(vec);
    }
    const netLen = netDipole.length() * arrowLengthScale;
    if(mode.irActive && netLen > 0.05){
      netArrow.visible = true;
      netArrow.setDirection(netDipole.clone().normalize());
      netArrow.setLength(netLen);
    } else {
      netArrow.visible = false;
    }
  }

  function animate(now){
    if(!animationPlaying) return;
    if (!animationStartTime) animationStartTime = now;
    elapsedTime = (now - animationStartTime)/1000; // seconds
    updateScene();
    renderer.setViewport(0,0,container.clientWidth/2,container.clientHeight);
    renderer.setScissor(0,0,container.clientWidth/2,container.clientHeight);
    renderer.clear();
    renderer.render(sceneMolecule, leftCamera);
    leftControls.update();

    renderer.setViewport(container.clientWidth/2,0,container.clientWidth/2,container.clientHeight);
    renderer.setScissor(container.clientWidth/2,0,container.clientWidth/2,container.clientHeight);
    renderer.render(sceneVectors, rightCamera);
    rightControls.update();

    requestAnimationFrame(animate);
  }

  /*----------------------------------------------
    EVENT HANDLERS
  ----------------------------------------------*/
  selectMode.addEventListener('change', () => {
    const modeKey = selectMode.value;
    if (modeKey === 'ir-spectrum') {
      // Hide 3D view and description, show plot
      container.classList.add('hidden');
      document.getElementById('description-area').classList.add('hidden');
      plotContainer.classList.remove('hidden');
      btnPlay.disabled = true;
      btnPause.disabled = true;
      btnReset.disabled = true;
      if (!plotContainer.dataset.rendered) {
        // generate plot
        generateSpectrumPlot();
        plotContainer.dataset.rendered = "true";
      }
      stopAnimation();
    } else {
      // Show 3D view and description
      container.classList.remove('hidden');
      document.getElementById('description-area').classList.remove('hidden');
      plotContainer.classList.add('hidden');
      btnPlay.disabled = false;
      btnPause.disabled = false;
      btnReset.disabled = false;
      stopAnimation();
      // update labels
      const mode = modes[modeKey];
      labelLeft.textContent = mode.name;
      labelLeft.style.color = `#${mode.color.toString(16).padStart(6,'0')}`;
      labelRight.textContent = mode.dipole;
      labelRight.style.color = `#${mode.color.toString(16).padStart(6,'0')}`;
    }
  });

  btnPlay.addEventListener('click', () => {
    if (animationPlaying) return;
    animationPlaying = true;
    animationStartTime = null;
    requestAnimationFrame(animate);
  });

  btnPause.addEventListener('click', () => { animationPlaying = false; });

  btnReset.addEventListener('click', () => { stopAnimation(); resetPositions(); });

  window.addEventListener('resize', () => {
    const width = container.clientWidth;
    const height = container.clientHeight;
    renderer.setSize(width, height);
    leftCamera.aspect = width / height;
    leftCamera.updateProjectionMatrix();
    rightCamera.aspect = width / height;
    rightCamera.updateProjectionMatrix();
  });

  function stopAnimation(){
    animationPlaying = false;
    animationStartTime = null;
  }

  function resetPositions(){
    // reset H positions and arrows
    for(let i=0;i<4;i++){
      hMeshes[i].position.copy(hPositions0[i]);
    }
    for(let i=0;i<4;i++){
      arrowHelpers[i].setDirection(new THREE.Vector3(0,0,1));
      arrowHelpers[i].setLength(1);
    }
    netArrow.visible = false;
    elapsedTime = 0;
  }

  /*----------------------------------------------
    IR SPECTRUM PLOT
  ----------------------------------------------*/
  function generateSpectrumPlot(){
    const x = [];
    const y = [];
    const nPoints = 500;
    const minW = 400;
    const maxW = 4000;
    const noise = ()=> (Math.random() - 0.5) * 0.02;
    for(let i=0;i<nPoints;i++){
      const w = minW + (maxW-minW)*i/(nPoints-1);
      x.push(w);
      // baseline
      let trans = 0.95 + noise();
      // main peaks
      const addPeak = (center, width, depth)=>{
        const gauss = Math.exp(-((w-center)**2)/(2*width**2));
        trans -= depth * gauss;
      };
      addPeak(1306,20,0.8);
      addPeak(3019,20,0.8);
      // small rotating grid noise around peaks
      if(Math.abs(w-1306)<5){ trans -= 0.05 * Math.sin(10*(w-1306)); }
      if(Math.abs(w-3019)<5){ trans -= 0.05 * Math.sin(10*(w-3019)); }
      // clamp
      trans = Math.max(0, Math.min(1, trans));
      y.push(trans);
    }

    const data = [{x:x,y:y,type:'scatter',mode:'lines',name:'Transmittance'}];

    // Offscreen images for annotations
    const image1 = createOffscreenMethaneImage('bending-f2', 0.2);
    const image2 = createOffscreenMethaneImage('asymmetric-stretch', 0.2);

    const layout = {
      title: 'Methane IR Spectrum',
      xaxis:{title:'wavenumber (cm⁻¹)',range:[minW,maxW]},
      yaxis:{title:'Transmittance',range:[0,1.1]},
      annotations:[
