      ]
    };
    Plotly.newPlot(plotContainer, data, layout, {displayModeBar:false});
  }

  function createOffscreenMethaneImage(modeKey, amplitude){
    const width = 120, height = 120;
    const offCanvas = document.createElement('canvas');
    offCanvas.width = width;
    offCanvas.height = height;
    const offRenderer = new THREE.WebGLRenderer({ canvas: offCanvas, antialias:true, preserveDrawingBuffer:true });
    offRenderer.setSize(width, height);
    offRenderer.setClearColor(0xfafafa);

    const scene = new THREE.Scene();
    const cam = new THREE.PerspectiveCamera(45, width/height, 0.1, 100);
    cam.position.set(2,2,2);
    cam.lookAt(0,0,0);

    const light = new THREE.AmbientLight(0xffffff, 0.8);
    scene.add(light);
    const dir = new THREE.DirectionalLight(0xffffff, 0.5);
    dir.position.set(1,1,1);
    scene.add(dir);

    const carbon = new THREE.Mesh(atomCGeo, atomCMat);
    carbon.position.set(0,0,0);
    scene.add(carbon);

    const hMeshes=[];
    for(let i=0;i<4;i++){
      const h = new THREE.Mesh(atomHGeo, atomHMat);
      const base = hPositions0[i];
      const disp = modes[modeKey].getDisplace(0).map(v=>v.clone().multiplyScalar(amplitude)).find((v,j)=>j===i);
      h.position.copy( base.clone().add(disp) );
      scene.add(h);
      hMeshes.push(h);
    }
    // Bonds
    for(let i=0;i<4;i++){
      createBond(bondGeo, bondMat, carbonPos, hPositions0[i].clone().add( modes[modeKey].getDisplace(0)[i].clone().multiplyScalar(amplitude) ));
    }

    offRenderer.render(scene, cam);
    const dataUrl = offCanvas.toDataURL('image/png');
    offRenderer.dispose();
    return dataUrl;
  }

  /*----------------------------------------------
    INIT
  ----------------------------------------------*/
  // Trigger initial change to set UI state correctly
  selectMode.dispatchEvent(new Event('change'));

})();
</script>
