    cursor:pointer; user-select:none;
  }
  #btn-reset:hover {background:#e6e6e6;}

  #canvas-material-bar {border:1px solid #ccc;background:#fff;}
  #div-plot {width:600px; height:400px;}
</style>
</head>
<body>
<div id="main-container">
  <!-- Control Panel -->
  <div id="control-panel">
    <label for="slider-time">time (seconds)</label>
    <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
    <span id="span-time-value">9.8</span>
    <button id="btn-reset">+</button>
  </div>

  <!-- Material Visualization -->
  <canvas id="canvas-material-bar" width="600" height="120"></canvas>

  <!-- Plot Area -->
  <div id="div-plot"></div>
</div>

<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script>
  // Constants
  const canvas = document.getElementById('canvas-material-bar');
  const ctx = canvas.getContext('2d');
  const slider = document.getElementById('slider-time');
  const span = document.getElementById('span-time-value');
  const resetBtn = document.getElementById('btn-reset');
  const plotDiv = document.getElementById('div-plot');
  const mmPerPx = canvas.width / 16; // 1mm = 37.5px

  function stefanDistance(t) {
    return 0.155 * Math.sqrt(t); // mm
  }

  function drawMaterialBar(t) {
    const s = stefanDistance(t);
    const glassWidthPx = 6 * mmPerPx; // 6mm
    const waterWidthPx = Math.max(0, s) * mmPerPx;
    const iceWidthPx = canvas.width - glassWidthPx - waterWidthPx;

    // Clear
    ctx.clearRect(0,0,canvas.width,canvas.height);

    // Glass block
    const glassGrad = ctx.createLinearGradient(0,0,glassWidthPx,0);
    glassGrad.addColorStop(0,'#FF0000');
    glassGrad.addColorStop(1,'#FFFFFF');
    ctx.fillStyle = glassGrad;
    ctx.fillRect(0,20,glassWidthPx,40);

    // Melted water
    ctx.fillStyle = '#00BFFF';
    ctx.fillRect(glassWidthPx,20,waterWidthPx,40);

    // Ice block
    ctx.fillStyle = '#003366';
    ctx.fillRect(glassWidthPx + waterWidthPx,20,iceWidthPx,40);

    // Axis
    ctx.strokeStyle = '#000';
    ctx.lineWidth = 1;
    ctx.beginPath();
    ctx.moveTo(0,80);
    ctx.lineTo(canvas.width,80);
    ctx.stroke();

    // Axis labels (0,5,10,15)
    ctx.fillStyle = '#000';
    ctx.font = '12px Arial';
    ctx.textAlign = 'center';
    [0,5,10,15].forEach(val=>{
      const x = val * mmPerPx;
      ctx.fillText(val.toString(), x, 95);
    });
  }

  function initPlot() {
    const xValues = [];
    const yValues = [];
    const step = 0.1;
    for(let t=0; t<=100; t+=step){
      xValues.push(parseFloat(t.toFixed(2)));
      yValues.push(stefanDistance(t));
    }
    const trace = {
      x: xValues,
      y: yValues,
      mode: 'lines',
      line: {color: '#FFA500'},
      name: 'Stefan distance'
    };
    const layout = {
      title: 'Stefan distance versus time',
      xaxis: {title: 'time (s)', range: [0, 100]},
      yaxis: {title: 'distance (mm)', range: [0, 1.6]},
      shapes: []
    };
    Plotly.newPlot(plotDiv, [trace], layout);
  }

  function updatePlot(t) {
    const s = stefanDistance(t);
    const shape = {
      type: 'line',
      x0: t, y0: 0,
      x1: t, y1: s,
      line: {color: '#000000', width: 2, dash: 'dot'}
    };
    Plotly.relayout(plotDiv, {shapes: [shape]});
  }

  function masterUpdate(t) {
    const tNum = Number(t);
    span.textContent = tNum.toFixed(1);
    drawMaterialBar(tNum);
    updatePlot(tNum);
  }

  // Event listeners
  slider.addEventListener('input', () => masterUpdate(slider.value));
  resetBtn.addEventListener('click', () => {
    slider.value = 0;
    slider.dispatchEvent(new Event('input'));
  });

  // Initial load
  window.addEventListener('load', () => {
    initPlot();
    masterUpdate(slider.value);
  });
</script>
</body>
</html>
