/* =================== TRIANGLE COORDS =================== */
const canvasSize = 600;
const P_A = {x: canvasSize/2, y: 100};
const P_B = {x: 100, y: 500};
const P_C = {x: canvasSize-100, y: 500};

/* =================== DATA (SAMPLE) =================== */
const envelopePolygon = [
  {xA:0.35, xB:0.35, xC:0.3},
  {xA:0.6 , xB:0.2 , xC:0.2},
  {xA:0.2 , xB:0.6 , xC:0.2}
];
const alphaRef = envelopePolygon[0];
const betaRef = envelopePolygon[1];
const tieLines = [
  {alpha:{xA:0.35,xB:0.35,xC:0.3}, beta:{xA:0.6,xB:0.2,xC:0.2}}
];

/* =================== HELPER FUNCTIONS =================== */
function baryToPixel(p){
  const px = p.xA*P_A.x + p.xB*P_B.x + p.xC*P_C.x;
  const py = p.xA*P_A.y + p.xB*P_B.y + p.xC*P_C.y;
  return {x:px, y:py};
}
function pixelToBary(px,py){
  // Solve for xA, xB with xC = 1 - xA - xB
  const A = [
    [P_A.x - P_C.x, P_B.x - P_C.x],
    [P_A.y - P_C.y, P_B.y - P_C.y]
  ];
  const B = [px - P_C.x, py - P_C.y];
  const det = A[0][0]*A[1][1] - A[0][1]*A[1][0];
  const xA = (B[0]*A[1][1] - B[1]*A[0][1]) / det;
  const xB = (A[0][0]*B[1] - A[1][0]*B[0]) / det;
  const xC = 1 - xA - xB;
  return {xA, xB, xC};
}
function insideTriangle(p){
  return p.xA>=0 && p.xB>=0 && p.xC>=0 && p.xA<=1 && p.xB<=1 && p.xC<=1;
}
function pointInPolygon(poly, p){
  // poly: array of {xA,xB,xC}
  let crossings = 0;
  const n = poly.length;
  for(let i=0;i<n;i++){
    const a = poly[i];
    const b = poly[(i+1)%n];
    // edges in barycentric space, treat as 2D by dropping xC (since xC = 1 - xA - xB)
    const ax = a.xA, ay = a.xB;
    const bx = b.xA, by = b.xB;
    const px = p.xA, py = p.xB;
    if(((ay>py)!=(by>py)) && (px < (bx-ax)*(py-ay)/(by-ay)+ax)){
      crossings++;
    }
  }
  return crossings%2===1;
}
