/* Utility for dashed lines (p5 doesn't have a built-in dashedLine) */
p5.prototype.dashedLine = function(x1, y1, x2, y2, dashLength = 5){
  const dx = x2 - x1;
  const dy = y2 - y1;
  const distance = Math.sqrt(dx*dx + dy*dy);
  const dashCount = Math.floor(distance / dashLength);
  const dashX = dx / dashCount;
  const dashY = dy / dashCount;
  for (let i = 0; i < dashCount; i++) {
    if (i % 2 === 0) {
      this.line(x1 + i * dashX, y1 + i * dashY,
                x1 + (i + 1) * dashX, y1 + (i + 1) * dashY);
    }
  }
};

</script>
</body>
