    box-sizing: border-box;
  }
  #info-text {
    margin-bottom: 10px;
    font-weight: bold;
  }
</style>
</head>
<body>

<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<div id="main-container">

  <!-- Left Column: Control Panel -->
  <div id="control-panel">
    <!-- Initialization Slider -->
    <div>
      <label for="slider-initialization">initialization</label>
      <input type="range" id="slider-initialization" min="0" max="65535" step="1" value="462">
      <span id="value-initialization">+ 462</span>
    </div>

    <!-- Max Steps Slider -->
    <div>
      <label for="slider-max-steps">max steps</label>
      <input type="range" id="slider-max-steps" min="1" max="50" step="1" value="9">
      <span id="value-max-steps">+ 9</span>
    </div>

    <!-- Checkboxes -->
    <div>
      <input type="checkbox" id="checkbox-cycle4">
      <label for="checkbox-cycle4">length 4 cycle</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-cycle2">
      <label for="checkbox-cycle2">length 2 cycles</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-cycle1">
      <label for="checkbox-cycle1">length 1 cycle points</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-all-points">
      <label for="checkbox-all-points">all map points</label>
    </div>
    <div>
      <input type="checkbox" id="checkbox-modify">
      <label for="checkbox-modify">modify initialization</label>
    </div>
  </div>

  <!-- Right Column: Visualization Area -->
  <div id="visualization-area">
    <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
    <div id="canvas-container"></div> <!-- p5.js will attach canvas here -->
  </div>

</div>

<script>
/* ---------- Global State Variables ---------- */
let initVal = 462;
let maxSteps = 9;
let showAllPoints = false;
let showCycle4 = false;
let showCycle2 = false;
let showCycle1 = false;
let modifyInit = false; // flag for modify initialization (no visual effect)
let seq = []; // sequence of n values including n_{maxSteps+1}
let cycleInfo = ''; // info text about cycle detection
let fixedPoints = []; // length-1 cycle points
const canvasSize = 512;
const gridSize = 256;

/* ---------- Helper Functions ---------- */
function bitReverse15(v) {
  // Reverse lowest 15 bits
  let rev = 0;
  for (let i = 0; i < 15; i++) {
    rev = (rev << 1) | ((v >> i) & 1);
  }
  return rev;
}

function f(n) {
  const half = Math.floor(n / 2);
  const rev = bitReverse15(half);
  return (n + rev) & 0xFFFF; // modulo 65536
}

function computeSequence() {
  seq = [];
  let n = initVal;
  seq.push(n);
  for (let i = 0; i < maxSteps; i++) {
    n = f(n);
    seq.push(n);
  }
  // compute one more step to allow detection up to maxSteps+1
  n = f(n);
  seq.push(n);
}

function detectCycle() {
  const seen = new Map();
  for (let i = 0; i < seq.length; i++) {
    const val = seq[i];
    if (seen.has(val)) {
      const firstIdx = seen.get(val);
      const cycleLen = i - firstIdx;
      cycleInfo = `result of initial condition ${initVal} repeats in ${cycleLen} steps`;
      return;
    }
    seen.set(val, i);
  }
  cycleInfo = `result of initial condition ${initVal} does not repeat in ${maxSteps + 1} steps`;
}

function updateInfoText() {
  const p = document.getElementById('info-text');
  p.textContent = cycleInfo;
}

function updateValueDisplays() {
  document.getElementById('value-initialization').textContent = `+ ${initVal}`;
  document.getElementById('value-max-steps').textContent = `+ ${maxSteps}`;
}

function computeFixedPoints() {
  fixedPoints = [];
  for (let n = 0; n <= 0xFFFF; n++) {
    if (bitReverse15(Math.floor(n / 2)) === 0) {
      fixedPoints.push(n);
    }
  }
}

function coordFromN(n) {
  const x = n & 0xFF;
  const y = (n >> 8) & 0xFF;
  return {x, y};
}

function canvasCoord(pt) {
  return {
    x: pt.x * 2,
    y: pt.y * 2
  };
}

/* ---------- Event Handlers ---------- */
function onInitChange(evt) {
  initVal = parseInt(evt.target.value, 10);
  updateValueDisplays();
  updateSimulation();
}

function onMaxStepsChange(evt) {
  maxSteps = parseInt(evt.target.value, 10);
  updateValueDisplays();
  updateSimulation();
}

function onCheckboxChange(evt) {
  const id = evt.target.id;
  switch(id) {
    case 'checkbox-all-points': showAllPoints = evt.target.checked; break;
    case 'checkbox-cycle4': showCycle4 = evt.target.checked; break;
    case 'checkbox-cycle2': showCycle2 = evt.target.checked; break;
    case 'checkbox-cycle1': showCycle1 = evt.target.checked; break;
    case 'checkbox-modify': modifyInit = evt.target.checked; break;
  }
  redraw();
}

function updateSimulation() {
  computeSequence();
  detectCycle();
  updateInfoText();
  redraw();
}

/* ---------- p5.js Sketch ---------- */
let sketch = new p5(function(p) {
  p.setup = function() {
    const canvas = p.createCanvas(canvasSize, canvasSize);
    canvas.parent('canvas-container');
    p.noLoop();
  };

  p.draw = function() {
    // Background
    p.background('#e0f0e0');

    // All map points overlay
    if (showAllPoints) {
      p.noStroke();
      p.fill('#808080');
      for (let n = 0; n <= 0xFFFF; n++) {
        const next = f(n);
        const c = coordFromN(next);
        const cc = canvasCoord(c);
        p.point(cc.x, cc.y);
      }
    }

    // Draw fixed points if selected
    if (showCycle1) {
      p.noStroke();
      p.fill('#00FFFF');
      fixedPoints.forEach(n => {
        const c = coordFromN(n);
        const cc = canvasCoord(c);
        p.ellipse(cc.x, cc.y, 3, 3);
      });
    }

    // Length 2 cycles overlay
    if (showCycle2) {
      const pairs = [
        [10922, 21845], [13107, 26214], [13653, 27306],
        [15339, 30678], [21844, 43691], [26213, 52427],
        [27305, 54611], [30677, 61355], [43690, 54613]
      ];
      p.stroke('#008000');
      p.strokeWeight(1);
      pairs.forEach(pair => {
        const p1 = coordFromN(pair[0]);
        const p2 = coordFromN(pair[1]);
        const c1 = canvasCoord(p1);
        const c2 = canvasCoord(p2);
        p.line(c1.x, c1.y, c2.x, c2.y);
      });
    }

    // Length 4 cycle overlay
    if (showCycle4) {
      const cycle4 = [43690, 54612, 32768, 49152];
      const pts = cycle4.map(n => canvasCoord(coordFromN(n)));
      p.stroke('#008000');
      p.strokeWeight(1);
      for (let i = 0; i < pts.length; i++) {
        const a = pts[i];
        const b = pts[(i + 1) % pts.length];
        p.line(a.x, a.y, b.x, b.y);
      }
      // Bounding box
      const xs = pts.map(p => p.x);
      const ys = pts.map(p => p.y);
      const minX = Math.min(...xs), minY = Math.min(...ys);
      const maxX = Math.max(...xs), maxY = Math.max(...ys);
      p.noFill();
      p.stroke('#FFA500');
      p.strokeWeight(2);
      p.rect(minX, minY, maxX - minX, maxY - minY);

      // Perpendicular bisectors (short segments)
      p.stroke('#008000');
      p.strokeWeight(1);
      for (let i = 0; i < pts.length; i++) {
        const a = pts[i];
        const b = pts[(i + 1) % pts.length];
        const mx = (a.x + b.x) / 2;
        const my = (a.y + b.y) / 2;
        const dx = b.x - a.x;
        const dy = b.y - a.y;
        // Normalized perpendicular vector
        let nx = -dy;
        let ny = dx;
        const len = Math.sqrt(nx*nx + ny*ny);
        if (len === 0) continue;
        nx /= len; ny /= len;
        const segLen = 25;
        const start = {x: mx - nx*segLen, y: my - ny*segLen};
        const end = {x: mx + nx*segLen, y: my + ny*segLen};
        p.line(start.x, start.y, end.x, end.y);
      }
    }

    // Automaton path
    const pathPts = seq.map(n => canvasCoord(coordFromN(n)));
    p.noFill();
    p.stroke('#800080');
    p.strokeWeight(2);
    p.beginShape();
    pathPts.forEach(p => p.vertex(p.x, p.y));
    p.endShape();

    // Start point
    const start = pathPts[0];
    p.noStroke();
    p.fill('#FF0000');
    p.ellipse(start.x, start.y, 6, 6);

    // End point
    const end = pathPts[pathPts.length - 1];
    p.fill('#0000FF');
    p.ellipse(end.x, end.y, 6, 6);
  };
});

/* ---------- Initialize ---------- */
// Set slider attributes and event listeners
const sliderInit = document.getElementById('slider-initialization');
sliderInit.min = 0;
sliderInit.max = 65535;
sliderInit.step = 1;
sliderInit.value = initVal;
sliderInit.addEventListener('input', onInitChange);

const sliderMax = document.getElementById('slider-max-steps');
sliderMax.min = 1;
sliderMax.max = 50;
sliderMax.step = 1;
sliderMax.value = maxSteps;
sliderMax.addEventListener('input', onMaxStepsChange);

// Checkbox listeners
document.getElementById('checkbox-all-points').addEventListener('change', onCheckboxChange);
document.getElementById('checkbox-cycle4').addEventListener('change', onCheckboxChange);
document.getElementById('checkbox-cycle2').addEventListener('change', onCheckboxChange);
document.getElementById('checkbox-cycle1').addEventListener('change', onCheckboxChange);
document.getElementById('checkbox-modify').addEventListener('change', onCheckboxChange);

// Compute fixed points once
computeFixedPoints();

// Initial simulation and draw
updateValueDisplays();
updateSimulation();
</script>

</body>
</html>
