      <label><input type="radio" name="step-type" id="step-reboil" value="reboil_ratio"> reboil ratio</label>
    </div>

    <div id="step-percent-selection">
      <label for="step-percent">% step</label>
      <input type="number" id="step-percent" value="-0.3" min="-1" max="1" step="0.1">
    </div>

    <div id="stage-selection">
      <label>stage</label>
      <label><input type="radio" name="stage" id="stage-cond" value="Cond"> Global &#39;Cond</label>
      <label><input type="radio" name="stage" id="stage-1" value="1"> 1</label>
      <label><input type="radio" name="stage" id="stage-2" value="2"> 2</label>
      <label><input type="radio" name="stage" id="stage-3" value="3"> 3</label>
      <label><input type="radio" name="stage" id="stage-4" value="4"> 4</label>
      <label><input type="radio" name="stage" id="stage-5" value="5" checked> 5</label>
      <label><input type="radio" name="stage" id="stage-6" value="6"> 6</label>
      <label><input type="radio" name="stage" id="stage-7" value="7"> 7</label>
      <label><input type="radio" name="stage" id="stage-8" value="8"> 8</label>
      <label><input type="radio" name="stage" id="stage-9" value="9"> 9</label>
      <label><input type="radio" name="stage" id="stage-10" value="10"> 10</label>
      <label><input type="radio" name="stage" id="stage-reb" value="Reb"> Reb</label>
    </div>

