<script src="https://cdn.jsdelivr.net/npm/three@0.150.0/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.150.0/examples/js/controls/OrbitControls.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.150.0/examples/js/renderers/CSS2DRenderer.js"></script>

<script>
document.addEventListener('DOMContentLoaded', () => {
  /* ---------- Data Definitions ---------- */
  const families = ['all','Zodiac','Ursa Major Family','Perseus Family','Hercules Family','Orion Family','Bayer Group','La Caille Family'];

  const starList = [
      {id:'S1',ra:14.25,dec:-62.79},
      {id:'S2',ra:13.25,dec:40.71},
      {id:'S3',ra:7.2,dec:32.5},
      {id:'S4',ra:20.0,dec:-10.0},
      {id:'S5',ra:7.0,dec:-16.0},
      {id:'S6',ra:18.0,dec:5.0},
      {id:'S7',ra:5.0,dec:-30.0},
      {id:'S8',ra:14.5,dec:-12.0}
  ];

  const constellations = [
      {name:'Ursa Major',family:'Ursa Major Family',stars:['S1','S2','S4','S6'],lines:[['S1','S2'],['S2','S4'],['S4','S6']]},
      {name:'Cygnus',family:'Zodiac',stars:['S3','S5'],lines:[['S3','S5']]},
      {name:'Orion',family:'Orion Family',stars:['S7','S8'],lines:[['S7','S8']]}
  ];

  /* ---------- Utility Functions ---------- */
  const R = 5; // Sphere radius
  const starMap = {}; // id -> vector3 (on surface)
  starList.forEach(s=>{
      const raDeg = s.ra * 15; // hours to degrees
      const decDeg = s.dec;
      const raRad = THREE.MathUtils.degToRad(raDeg);
      const decRad = THREE.MathUtils.degToRad(decDeg);
      const x = Math.cos(decRad) * Math.cos(raRad);
      const y = Math.sin(decRad);
      const z = Math.cos(decRad) * Math.sin(raRad);
      starMap[s.id] = new THREE.Vector3(x,y,z).multiplyScalar(R);
  });

  /* ---------- DOM Elements ---------- */
  const selectFamily = document.getElementById('select-family');
  const selectConstellation = document.getElementById('select-constellation');
  families.forEach(f => selectFamily.options.add(new Option(f, f)));
  selectFamily.selectedIndex = families.indexOf('Zodiac');

  const sliderRA = document.getElementById('slider-ra');
  const sliderDec = document.getElementById('slider-dec');
  const checkSphere = document.getElementById('check-sphere');
  const checkStars  = document.getElementById('check-stars');
  const checkEquator = document.getElementById('check-equator');
  const checkEcliptic = document.getElementById('check-ecliptic');
  const checkZenith = document.getElementById('check-zenith');
  const checkAries = document.getElementById('check-aries');

  /* ---------- Populate Constellations ---------- */
  function populateConstellations(){
      const family = selectFamily.value;
      selectConstellation.innerHTML = '';
      if (family === 'all') {
          const names = constellations.map(c => c.name).sort();
          names.forEach(n => selectConstellation.options.add(new Option(n, n)));
          selectConstellation.options.add(new Option('None', 'None'));
          selectConstellation.value = 'None';
      } else {
          const names = constellations.filter(c => c.family === family).map(c => c.name);
          if (names.length) {
              names.forEach(n => selectConstellation.options.add(new Option(n, n)));
              selectConstellation.value = names[0];
          } else {
              selectConstellation.options.add(new Option('None', 'None'));
              selectConstellation.value = 'None';
          }
      }
  }
  populateConstellations();

  /* ---------- Three.js Setup ---------- */
  const container = document.getElementById('visualization-container');
  const scene = new THREE.Scene();

  const camera = new THREE.PerspectiveCamera(60, container.clientWidth / container.clientHeight, 0.1, 1000);
  camera.position.set(0, 0, 10);

  const renderer = new THREE.WebGLRenderer({ antialias: true });
  renderer.setSize(container.clientWidth, container.clientHeight);
  container.appendChild(renderer.domElement);

  const controls = new THREE.OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;

  /* ---------- Celestial Sphere ---------- */
  const sphereGeom = new THREE.SphereGeometry(R, 64, 64);
  const sphereMat = new THREE.MeshBasicMaterial({
      color: 0xFFA500,
      transparent: true,
      opacity: 0.4,
      side: THREE.DoubleSide
  });
  const skySphere = new THREE.Mesh(sphereGeom, sphereMat);
  scene.add(skySphere);

  /* ---------- Equator Line ---------- */
  const equatorPts = [];
  for (let i = 0; i <= 360; i += 5) {
      const raRad = THREE.MathUtils.degToRad(i);
      equatorPts.push(new THREE.Vector3(R * Math.cos(raRad), 0, R * Math.sin(raRad)));
  }
  const equatorGeom = new THREE.BufferGeometry().setFromPoints(equatorPts);
  const equatorMat = new THREE.LineBasicMaterial({ color: 0x0000ff });
  const equatorLine = new THREE.LineLoop(equatorGeom, equatorMat);
  scene.add(equatorLine);

  /* ---------- Ecliptic Line ---------- */
  const tiltRad = THREE.MathUtils.degToRad(23.5);
  const eclipticPts = equatorPts.map(p => {
      const x = p.x;
      const y = p.y * Math.cos(tiltRad) - p.z * Math.sin(tiltRad);
      const z = p.y * Math.sin(tiltRad) + p.z * Math.cos(tiltRad);
      return new THREE.Vector3(x, y, z);
  });
  const eclipticGeom = new THREE.BufferGeometry().setFromPoints(eclipticPts);
  const eclipticMat = new THREE.LineBasicMaterial({ color: 0x8B008B });
  const eclipticLine = new THREE.LineLoop(eclipticGeom, eclipticMat);
  scene.add(eclipticLine);

  /* ---------- Brightest Stars ---------- */
  const starPosArr = [];
  starList.forEach(s => {
      const p = starMap[s.id];
      starPosArr.push(p.x, p.y, p.z);
  });
  const starGeom = new THREE.BufferGeometry();
  starGeom.setAttribute('position', new THREE.Float32BufferAttribute(starPosArr, 3));
  const starMat = new THREE.PointsMaterial({ color: 0xffffff, size: 2 });
  const starsPoints = new THREE.Points(starGeom, starMat);
  scene.add(starsPoints);

  /* ---------- First Point of Aries ---------- */
  const ariesGeom = new THREE.SphereGeometry(0.1, 12, 12);
  const ariesMat = new THREE.MeshBasicMaterial({ color: 0xffff00 });
  const ariesSphere = new THREE.Mesh(ariesGeom, ariesMat);
  ariesSphere.position.set(R, 0, 0);
  scene.add(ariesSphere);

  /* ---------- Zenith Group ---------- */
  const zenithSphereGeom = new THREE.SphereGeometry(0.05, 12, 12);
  const zenithSphereMat = new THREE.MeshBasicMaterial({ color: 0x000000 });
  const zenithSphere = new THREE.Mesh(zenithSphereGeom, zenithSphereMat);
  const zenithLineGeom = new THREE.BufferGeometry()
      .setFromPoints([new THREE.Vector3(0, 0, 0), new THREE.Vector3(0, 0, 0)]);
  const zenithLineMat = new THREE.LineBasicMaterial({ color: 0x000000 });
  const zenithLine = new THREE.Line(zenithLineGeom, zenithLineMat);
  const zenithGroup = new THREE.Group();
  zenithGroup.add(zenithSphere);
  zenithGroup.add(zenithLine);
  scene.add(zenithGroup);

  function updateZenith() {
      const raDeg = parseFloat(sliderRA.value);
      const decDeg = parseFloat(sliderDec.value);
      const raRad = THREE.MathUtils.degToRad(raDeg);
      const decRad = THREE.MathUtils.degToRad(decDeg);
      const x = Math.cos(decRad) * Math.cos(raRad);
      const y = Math.sin(decRad);
      const z = Math.cos(decRad) * Math.sin(raRad);
      const pos = new THREE.Vector3(x, y, z).multiplyScalar(R);
      zenithSphere.position.copy(pos);
      zenithLine.geometry.setFromPoints([new THREE.Vector3(0, 0, 0), pos]);
  }
  sliderRA.addEventListener('input', updateZenith);
  sliderDec.addEventListener('input', updateZenith);
  updateZenith();

  /* ---------- Polar Axis ---------- */
  const polarGeom = new THREE.BufferGeometry()
      .setFromPoints([new THREE.Vector3(0, R, 0), new THREE.Vector3(0, -R, 0)]);
  const polarMat = new THREE.LineBasicMaterial({ color: 0x000000 });
  const polarAxis = new THREE.LineSegments(polarGeom, polarMat);
  scene.add(polarAxis);

  /* ---------- Labels ---------- */
  const labelRenderer = new THREE.CSS2DRenderer();
  labelRenderer.setSize(container.clientWidth, container.clientHeight);
  labelRenderer.domElement.style.position = 'absolute';
  labelRenderer.domElement.style.top = '0px';
  container.appendChild(labelRenderer.domElement);

  const northDiv = document.createElement('div');
  northDiv.className = 'label';
  northDiv.textContent = 'N';
  const northLabel = new THREE.CSS2DObject(northDiv);
  northLabel.position.set(0, R, 0);
  scene.add(northLabel);

  const southDiv = document.createElement('div');
  southDiv.className = 'label';
  southDiv.textContent = 'S';
  const southLabel = new THREE.CSS2DObject(southDiv);
  southLabel.position.set(0, -R, 0);
  scene.add(southLabel);

  /* ---------- Checkbox Handlers ---------- */
  checkSphere.addEventListener('change', () => skySphere.visible = checkSphere.checked);
  checkStars.addEventListener('change', () => starsPoints.visible = checkStars.checked);
  checkEquator.addEventListener('change', () => equatorLine.visible = checkEquator.checked);
  checkEcliptic.addEventListener('change', () => eclipticLine.visible = checkEcliptic.checked);
  checkZenith.addEventListener('change', () => zenithGroup.visible = checkZenith.checked);
  checkAries.addEventListener('change', () => ariesSphere.visible = checkAries.checked);

  /* ---------- Constellation Drawing ---------- */
  let constellationSegments = null;
  let constellationStars = null;

  function updateConstellation() {
      if (constellationSegments) {
          scene.remove(constellationSegments);
          constellationSegments.geometry.dispose();
          constellationSegments.material.dispose();
          constellationSegments = null;
      }
      if (constellationStars) {
          scene.remove(constellationStars);
          constellationStars.geometry.dispose();
          constellationStars.material.dispose();
          constellationStars = null;
      }
      const selected = selectConstellation.value;
      if (!selected || selected === 'None') return;
      const data = constellations.find(c => c.name === selected);
      if (!data) return;

      // Lines
      const verts = [];
      data.lines.forEach(pair => {
          const a = starMap[pair[0]];
          const b = starMap[pair[1]];
          verts.push(a.x, a.y, a.z, b.x, b.y, b.z);
      });
      const lineGeom = new THREE.BufferGeometry();
      lineGeom.setAttribute('position', new THREE.Float32BufferAttribute(verts, 3));
      const lineMat = new THREE.LineBasicMaterial({ color: 0xff0000 });
      constellationSegments = new THREE.LineSegments(lineGeom, lineMat);
      scene.add(constellationSegments);

      // Highlighted stars
      const starPos = [];
      data.stars.forEach(id => {
          const p = starMap[id];
          starPos.push(p.x, p.y, p.z);
      });
      const starGeom2 = new THREE.BufferGeometry();
      starGeom2.setAttribute('position', new THREE.Float32BufferAttribute(starPos, 3));
      const starMat2 = new THREE.PointsMaterial({ color: 0xffff00, size: 4 });
      constellationStars = new THREE.Points(starGeom2, starMat2);
      scene.add(constellationStars);
  }

  selectConstellation.addEventListener('change', updateConstellation);
  updateConstellation(); // initial

  /* ---------- Resize Handling ---------- */
  window.addEventListener('resize', () => {
      const width = container.clientWidth;
      const height = container.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
      labelRenderer.setSize(width, height);
  });

  /* ---------- Animation Loop ---------- */
  function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
      labelRenderer.render(scene, camera);
  }

  animate();
});
</script>
</body>
</html>
