<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="utf-8">
<title>Nonlinear Signal Simulation</title>
<style>
  html, body { margin:0; height:100%; font-family:sans-serif; }
  #app-container {
    display:flex; height:100%; width:100%;
  }
  #control-panel {
    flex:0 0 30%; padding:10px; box-sizing:border-box; border-right:1px solid #ccc;
    overflow-y:auto;
  }
  #visualization-area { flex:1; padding:10px; box-sizing:border-box; overflow-y:auto; }
  h3 { margin-top:20px; margin-bottom:5px; }
  .control-group { display:flex; align-items:center; margin-bottom:10px; }
  .control-group label { width:80px; }
  .control-group input[type=range] { flex:1; }
  .control-group span { width:35px; text-align:right; margin-left:5px; }
  button { margin-left:5px; cursor:pointer; }
  button.reset-btn {
    width:24px; height:24px; border-radius:12px; border:1px solid #888;
    background:#fff; font-weight:bold; line-height:22px; text-align:center; padding:0;
  }
  button.btn-scale { padding:5px 10px; border-radius:4px; background:#f0f0f0; border:1px solid #aaa; }
  button.btn-scale.active { background:#4c9ef9; color:#fff; font-weight:bold; }
  #level-control-group { margin-left:85px; }
  input[type=checkbox] { transform:scale(1.5); margin-right:5px; }
</style>
</head>
<body>

<div id="app-container">

  <!-- Left side: Control Panel -->
  <div id="control-panel">
    <!-- Coefficients Section -->
    <h3>coefficients</h3>
    <div class="control-group">
      <label for="slider-c1">c<sub>1</sub></label>
      <input type="range" id="slider-c1">
      <span id="c1-value-display">0</span>
      <button id="reset-c1" class="reset-btn">+</button>
    </div>
    <div class="control-group">
      <label for="slider-c2">c<sub>2</sub></label>
      <input type="range" id="slider-c2">
      <span id="c2-value-display">0</span>
      <button id="reset-c2" class="reset-btn">+</button>
    </div>

    <!-- Clipping Section -->
    <h3>clipping</h3>
    <div class="control-group">
      <input type="checkbox" id="checkbox-clipping">
      <label for="checkbox-clipping"></label>
    </div>
    <div id="level-control-group" class="control-group" style="display:none;">
      <label for="slider-level">level</label>
      <input type="range" id="slider-level">
      <span id="level-value-display">0.7</span>
    </div>

    <!-- DFT Section -->
    <h3>DFT</h3>
    <div class="control-group">
      <label>scale</label>
      <button id="btn-linear" class="btn-scale active">linear</button>
      <button id="btn-decibel" class="btn-scale">decibel</button>
    </div>
  </div>

  <!-- Right side: Visualization Area -->
  <div id="visualization-area">
    <div id="plot-signal" style="height:45%;"></div>
    <div id="plot-spectrum" style="height:45%; margin-top:20px;"></div>
  </div>

</div>

<!-- Plotly.js CDN -->
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<!-- FFT JS CDN -->
<script src="https://cdn.jsdelivr.net/npm/fft-js@0.3.0/dist/fft.min.js"></script>

<script>
(() => {
  // References to DOM elements
  const sliderC1 = document.getElementById('slider-c1');
  const sliderC2 = document.getElementById('slider-c2');
  const sliderLevel = document.getElementById('slider-level');
  const checkboxClipping = document.getElementById('checkbox-clipping');
  const levelControlGroup = document.getElementById('level-control-group');
  const c1ValDisp = document.getElementById('c1-value-display');
  const c2ValDisp = document.getElementById('c2-value-display');
  const levelValDisp = document.getElementById('level-value-display');
  const resetC1Btn = document.getElementById('reset-c1');
  const resetC2Btn = document.getElementById('reset-c2');
  const btnLinear = document.getElementById('btn-linear');
  const btnDecibel = document.getElementById('btn-decibel');

  // Global storage for spectrum magnitude
  let normalizedMag = [];
  let currentScale = 'linear';

  // Initialize slider attributes
  function initSliders() {
    sliderC1.min = -2; sliderC1.max = 2; sliderC1.step = 0.1; sliderC1.value = 0;
    sliderC2.min = -2; sliderC2.max = 2; sliderC2.step = 0.1; sliderC2.value = 0;
    sliderLevel.min = 0; sliderLevel.max = 1.5; sliderLevel.step = 0.05; sliderLevel.value = 0.7;
  }

  // Update value displays
  function updateDisplays() {
    c1ValDisp.textContent = parseFloat(sliderC1.value).toFixed(1);
    c2ValDisp.textContent = parseFloat(sliderC2.value).toFixed(1);
    levelValDisp.textContent = parseFloat(sliderLevel.value).toFixed(2);
  }

  // Signal and spectrum plotting
  function updateSimulation() {
    const c1 = parseFloat(sliderC1.value);
    const c2 = parseFloat(sliderC2.value);
    const clippingEnabled = checkboxClipping.checked;
    const level = parseFloat(sliderLevel.value);

    const N = 2048;
    const fs = 32000;
    const f = 1000;
    const A = 1;

    const dt = 1 / fs;
    const t = Array.from({length: N}, (_, i) => i * dt);

    const x = t.map(ti => A * Math.sin(2 * Math.PI * f * ti));
    const y = x.map(xi => xi + c1 * xi * xi + c2 * xi * xi * xi);

    if (clippingEnabled) {
      for (let i = 0; i < y.length; i++) {
        if (y[i] > level) y[i] = level;
        else if (y[i] < -level) y[i] = -level;
      }
    }

    // Plot signal (first 0.005s)
    const maxIndex = Math.floor(0.005 * fs) + 1;
    const tPlot = t.slice(0, maxIndex);
    const yPlot = y.slice(0, maxIndex);

    const signalData = [{x: tPlot, y: yPlot, type: 'scatter', mode: 'lines'}];
    const signalLayout = {
      title: 'signal',
      xaxis: {title: 'time[s]', range: [0, 0.005], autorange: false},
      yaxis: {title: '', range: [-2, 2], autorange: false},
      margin: {l:30, r:20, b:40, t:40}
    };
    Plotly.react('plot-signal', signalData, signalLayout);

    // FFT
    const phasors = fft(y); // returns array of [re, im] pairs
    const magnitudes = phasors.map(p => Math.sqrt(p[0]**2 + p[1]**2));
    const half = N / 2;
    normalizedMag = new Array(half);
    for (let k = 0; k < half; k++) {
      if (k === 0)
        normalizedMag[k] = magnitudes[k] / N;
      else
        normalizedMag[k] = 2 * magnitudes[k] / N;
    }

    updateSpectrumScale(); // draw spectrum with current scale
    updateDisplays();
  }

  function updateSpectrumScale() {
    const N = 2048, fs = 32000;
    const half = N / 2;
    const freq = Array.from({length: half}, (_, k) => k * fs / N);

    let yData, yTitle, yRange;
    if (currentScale === 'linear') {
      yData = normalizedMag;
      yTitle = '';
      const maxMag = Math.max(...yData) * 1.1;
      yRange = [0, Math.max(0.5, maxMag)];
    } else { // decibel
      yData = normalizedMag.map(mag => {
        if (mag <= 0) return -100; // floor to -100 dB
        return 20 * Math.log10(mag);
      });
      yTitle = '';
      yRange = [-100, 0];
    }

    const spectrumData = [{x: freq, y: yData, type: 'bar', marker: {color:'#4c9ef9'}, width: 5}];
    const spectrumLayout = {
      title: 'magnitude spectrum (DFT)',
      xaxis: {title: 'frequency[Hz]', range: [0, 8000], autorange: false},
      yaxis: {title: yTitle, range: yRange, autorange: false},
      margin: {l:30, r:20, b:40, t:40}
    };
    Plotly.react('plot-spectrum', spectrumData, spectrumLayout);
  }

  // Event listeners
  sliderC1.addEventListener('input', updateSimulation);
  sliderC2.addEventListener('input', updateSimulation);
  resetC1Btn.addEventListener('click', () => { sliderC1.value = 0; updateSimulation(); });
  resetC2Btn.addEventListener('click', () => { sliderC2.value = 0; updateSimulation(); });
  checkboxClipping.addEventListener('change', () => {
    levelControlGroup.style.display = checkboxClipping.checked ? 'flex' : 'none';
    updateSimulation();
  });
  sliderLevel.addEventListener('input', updateSimulation);
  btnLinear.addEventListener('click', () => {
    if (currentScale !== 'linear') {
      currentScale = 'linear';
      btnLinear.classList.add('active');
      btnDecibel.classList.remove('active');
      updateSpectrumScale();
    }
  });
  btnDecibel.addEventListener('click', () => {
    if (currentScale !== 'decibel') {
      currentScale = 'decibel';
      btnDecibel.classList.add('active');
      btnLinear.classList.remove('active');
      updateSpectrumScale();
    }
  });

  // Initialize app
  initSliders();
  updateSimulation(); // initial rendering
})();
</script>
</body>
</html>
