    <div class="group">
      <label>Show elements</label>
      <div>
        <input type="checkbox" id="checkbox-injected" checked>
        <label for="checkbox-injected">injected</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-refracted" checked>
        <label for="checkbox-refracted">refracted</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-reflected" checked>
        <label for="checkbox-reflected">reflected</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-ejected" checked>
        <label for="checkbox-ejected">ejected</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-normals">
        <label for="checkbox-normals">normals</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-intersections">
