<!-- p5.js library -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.9.4/lib/p5.min.js" integrity="sha256-9M/0rGcP/VUPuhYiji+H6uX8d+eaXrcHqMb+ZA/L+/8=" crossorigin="anonymous"></script>
<script>
  // Make sure DOM content is loaded before initializing
  document.addEventListener('DOMContentLoaded', function () {
    // Attach listeners to inputs to update display values and trigger redraw
    const sliders = document.querySelectorAll('#control-panel input[type="range"]');
    sliders.forEach(slider => {
      const valueSpan = document.getElementById('value-' + slider.id.split('-')[1]);
      slider.addEventListener('input', () => {
        valueSpan.textContent = parseFloat(slider.value).toFixed(2);
        redraw();
      });
    });
    const checkboxes = document.querySelectorAll('#control-panel input[type="checkbox"]');
    checkboxes.forEach(cb => cb.addEventListener('input', redraw));

    // p5.js sketch
    new p5(p => {
      const canvas = p.createCanvas(window.innerWidth - 300, window.innerHeight);
      canvas.parent('canvas-container');
      p.noLoop();
      p.textFont('Arial');

      // Helper functions
      function circleRayIntersection(origin, dir, radius) {
        const o = origin.copy();
        const d = dir.copy();
        const a = d.dot(d);
        const b = 2 * o.dot(d);
        const c = o.dot(o) - radius * radius;
        const disc = b * b - 4 * a * c;
        if (disc < 0) return null;
        const sqrtDisc = Math.sqrt(disc);
        const t1 = (-b - sqrtDisc) / (2 * a);
        const t2 = (-b + sqrtDisc) / (2 * a);
        if (t1 > 1e-9) return t1;
        if (t2 > 1e-9) return t2;
        return null;
      }

      function refract(I, N, n1, n2) {
        const cosi = -N.dot(I);
        const eta = n1 / n2;
        const k = 1 - eta * eta * (1 - cosi * cosi);
        if (k < 0) return null; // total internal reflection
        return I.copy().mult(eta).add(N.copy().mult(eta * cosi - Math.sqrt(k))).normalize();
      }

      function reflect(I, N) {
        return I.copy().sub(N.copy().mult(2 * I.dot(N)));
      }

      // Draw function
      p.draw = function () {
        p.clear();
        p.translate(p.width / 2, p.height / 2);
        const scale = p.width / 5;
        p.scale(scale, -scale); // flip Y and map world units

        // ---- UI values ----
        const rayCount = parseInt(document.getElementById('slider-ray-count').value, 10);
        const beamWidth = parseFloat(document.getElementById('slider-beam-width').value);
        const incidentAngleDeg = parseInt(document.getElementById('slider-incident-angle').value, 10);
        const refractionRatio = parseFloat(document.getElementById('slider-refraction-ratio').value);

        const showInjected = document.getElementById('checkbox-injected').checked;
        const showRefracted = document.getElementById('checkbox-refracted').checked;
        const showReflected = document.getElementById('checkbox-reflected').checked;
        const showEjected = document.getElementById('checkbox-ejected').checked;
        const showNormals = document.getElementById('checkbox-normals').checked;
        const showIntersections = document.getElementById('checkbox-intersections').checked;
        const beamTop = document.getElementById('checkbox-beam-top').checked;
        const beamBottom = document.getElementById('checkbox-beam-bottom').checked;

        // ---- Draw axes ----
        p.stroke(220); p.strokeWeight(1);
        p.line(-2.5, 0, 2.5, 0);
        p.line(0, -2.5, 0, 2.5);
        [ -2, -1, 1, 2 ].forEach(v => {
          p.line(v, -0.05, v, 0.05);
          p.line(-0.05, v, 0.05, v);
        });
        p.fill(0); p.noStroke(); p.textSize(0.15); p.textAlign(p.CENTER, p.CENTER);
        [ -2, -1, 1, 2 ].forEach(v => {
          p.text(v, v, -0.2);
          p.text(v, 0.2, v);
        });

        // ---- Draw sphere ----
        p.noFill(); p.stroke(0); p.strokeWeight(0.02);
        p.ellipse(0, 0, 2, 2);

        // ---- Prepare ray parameters ----
        const theta = p.radians(incidentAngleDeg);
        const dir = p.createVector(p.cos(theta), p.sin(theta)); // normalized
        const perp = p.createVector(-p.sin(theta), p.cos(theta));

        const spacing = beamWidth / (rayCount - 1);
        const startX = -3; // start point left of sphere
        const epsilon = 1e-6;

        // ---- Draw each ray ----
        for (let i = 0; i < rayCount; i++) {
          const offset = (i - (rayCount - 1) / 2) * spacing;
          if ((offset > 0 && !beamTop) || (offset < 0 && !beamBottom) || (offset === 0 && !(beamTop && beamBottom))) {
            continue; // skip if not within selected beam halves
          }
          const startPoint = p.createVector(startX, 0).add(perp.copy().mult(offset));

          // first intersection
          const t1 = circleRayIntersection(startPoint, dir, 1);
          if (t1 === null) {
            // no intersection: draw a straight line segment for injected rays
            if (showInjected) {
              p.stroke('#FF0000'); p.strokeWeight(0.02);
              p.line(startPoint.x, startPoint.y, startPoint.x + dir.x * 10, startPoint.y + dir.y * 10);
            }
            continue;
          }
          const p1 = startPoint.copy().add(dir.copy().mult(t1));

          // Mid segment: injected ray
          if (showInjected) {
            p.stroke('#FF0000'); p.strokeWeight(0.02);
            p.line(startPoint.x, startPoint.y, p1.x, p1.y);
          }

          // Normals
          const normal1 = p1.copy().sub(p.createVector(0, 0)).normalize();
          if (showNormals) {
            p.stroke('#777'); p.strokeWeight(0.01);
            p.line(p1.x, p1.y, p1.x + normal1.x * 0.2, p1.y + normal1.y * 0.2);
          }
          if (showIntersections) {
            p.fill('#FF0000');
            p.noStroke();
            p.ellipse(p1.x, p1.y, 0.1, 0.1);
          }

          // First refraction
          const dirRefracted = refract(dir, normal1, 1, refractionRatio);
          if (!dirRefracted) continue; // TIR, skip
          const startInside1 = p1.copy().add(dirRefracted.copy().mult(epsilon));
          const t2 = circleRayIntersection(startInside1, dirRefracted, 1);
          if (t2 === null) continue;
          const p2 = startInside1.copy().add(dirRefracted.copy().mult(t2));

          // Refracted ray
          if (showRefracted) {
            p.stroke('#00FF00'); p.strokeWeight(0.02);
            p.line(p1.x, p1.y, p2.x, p2.y);
          }
          const normal2 = p2.copy().sub(p.createVector(0, 0)).normalize();
          if (showNormals) {
            p.stroke('#777'); p.strokeWeight(0.01);
            p.line(p2.x, p2.y, p2.x + normal2.x * 0.2, p2.y + normal2.y * 0.2);
          }
          if (showIntersections) {
            p.fill('#00FF00');
            p.noStroke();
            p.ellipse(p2.x, p2.y, 0.1, 0.1);
          }

          // Reflection at second intersection
          const dirReflected = reflect(dirRefracted, normal2);
          const startInside2 = p2.copy().add(dirReflected.copy().mult(epsilon));
          const t3 = circleRayIntersection(startInside2, dirReflected, 1);
          if (t3 === null) continue;
          const p3 = startInside2.copy().add(dirReflected.copy().mult(t3));

          // Reflected ray
          if (showReflected) {
            p.stroke('#0000FF'); p.strokeWeight(0.02);
            p.line(p2.x, p2.y, p3.x, p3.y);
          }
          const normal3 = p3.copy().sub(p.createVector(0, 0)).normalize();
          if (showNormals) {
            p.stroke('#777'); p.strokeWeight(0.01);
            p.line(p3.x, p3.y, p3.x + normal3.x * 0.2, p3.y + normal3.y * 0.2);
          }
          if (showIntersections) {
            p.fill('#0000FF');
            p.noStroke();
            p.ellipse(p3.x, p3.y, 0.1, 0.1);
          }

          // Second refraction to exit
          const dirEjected = refract(dirReflected, normal3, refractionRatio, 1);
          if (!dirEjected) continue;
          const endPoint = p3.copy().add(dirEjected.copy().mult(10));

          // Ejected ray
          if (showEjected) {
            p.stroke('#000000'); p.strokeWeight(0.02);
            p.line(p3.x, p3.y, endPoint.x, endPoint.y);
          }
        }
      };

      // Ensure correct canvas on window resize
      window.addEventListener('resize', () => {
        p.resizeCanvas(window.innerWidth - 300, window.innerHeight);
        p.redraw();
      });
    });

    // Initial redraw
    redraw();
  });
</script>
</body>
</html>
