    <canvas id="main-canvas"></canvas>
  </div>
</div>

<!-- Load p5.js -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.8.0/lib/p5.min.js"></script>
<script>
  /* ------------ GLOBAL VARIABLES ------------ */
  const DEFAULT_SIZE = 18;      // default total vertices
  const DEFAULT_DEGREE = 3;     // default max degree of lower part
  let graphSize = DEFAULT_SIZE;
  let maxDeg = DEFAULT_DEGREE;
  let history = [];          // array of state objects
  let currentStep = 0;
  let numVertices;           // total vertices
  let canvasWidth, canvasHeight;
  let bfsScale = 0.3;        // fraction of canvas width for tree
  const colors = {
    cover: '#00C000',
    root: '#FF0000',
    defaultVertex: '#333333',
    defaultEdge: '#808080',
    matchingEdge: '#A0A0FF',
    highlightEdge: '#FF0000',
    background: '#FFFFFF',
    treeNode: '#FFA500',
    treeBorder: '#000000',
    treeText: '#008080'
  };

  /* ------------ DOM SETUP & EVENT LISTENERS ------------ */
  // activate default controls
  document.getElementById(`size-${DEFAULT_SIZE}`).classList.add('active');
  document.getElementById(`degree-${DEFAULT_DEGREE}`).classList.add('active');

  // size buttons
  document.querySelectorAll('#size-controls button').forEach(btn => {
    btn.addEventListener('click', e => {
      document.querySelectorAll('#size-controls button').forEach(b=>b.classList.remove('active'));
      btn.classList.add('active');
      graphSize = parseInt(btn.id.split('-')[1],10);
      generateAndRender();
    });
  });

  // degree buttons
  document.querySelectorAll('#degree-controls button').forEach(btn => {
    btn.addEventListener('click', e => {
      document.querySelectorAll('#degree-controls button').forEach(b=>b.classList.remove('active'));
      btn.classList.add('active');
      maxDeg = parseInt(btn.id.split('-')[1],10);
      generateAndRender();
    });
  });

  // reset button
  document.getElementById('reset-button').addEventListener('click', () => generateAndRender());

  // step buttons will be created dynamically

  /* ------------ GRAPH GENERATION & ALGORITHM ------------ */
  function generateRandomGraph(size, maxDeg) {
    const n = size / 2;  // each partition
    const adj = Array.from({length: n}, () => new Set()); // adjacency lists from left to right
    const verts = Array.from({length: n}, (_,i)=>({id:`U${i+1}`}));
    const lowers = Array.from({length: n}, (_,i)=>({id:`V${i+1}`}));
    // Randomly add edges ensuring degree constraints for V vertices
    lowers.forEach((v,i) => {
      const degree = Math.floor(Math.random() * (maxDeg-1)) + 1; // between 1 and maxDeg
      const used = new Set();
      while (used.size < degree) {
        const uIdx = Math.floor(Math.random() * n);
        if (!used.has(uIdx)) {
          used.add(uIdx);
          adj[uIdx].add(i);
        }
      }
    });
    return {adj, nLeft: n, nRight: n, lowers, verts};
  }

  // Hungarian algorithm simplified for demonstration
  function runHungarian(graph) {
    const {adj, nLeft, nRight} = graph;
    const matchU = Array(nLeft).fill(null); // matched right vertex index or null
    const matchV = Array(nRight).fill(null); // matched left vertex index or null
    const states = []; // history of states

    // initial state (no matching)
    states.push(createState(matchU, matchV, null, [], `Step 1: Matched edges = 0 / ${nLeft}`));

    // iterate for each left vertex
    for (let u0 = 0; u0 < nLeft; u0++) {
      if (matchU[u0] !== null) continue; // already matched
      const result = bfs(u0, adj, matchU, matchV);
      if (!result.found) break; // no augmenting path
      // augment along path
      augmentPath(result.path, matchU, matchV);
      const bfsTree = buildBfsTree(result.parents, result.treeRoots);
      const pathEdges = result.path.map(pair => ({u:pair[0], v:pair[1]}));
      const status = `Step ${states.length+1}: Matched edges = ${states.length}`;
      states.push(createState(matchU, matchV, bfsTree, pathEdges, status));
    }
    return states;

    function bfs(startU, adj, matchU, matchV) {
      const queue = [];
      const parents = {}; // key: 'Ux' or 'Vy', value: parent key
      const visited = new Set();
      const treeRoots = {}; // root nodes mapping for tree structure
      queue.push(`U${startU}`);
      visited.add(`U${startU}`);
      parents[`U${startU}`] = null;
      treeRoots[`U${startU}`] = {id:`U${startU}`, children:[]};

      while (queue.length) {
        const cur = queue.shift();
        if (cur[0] === 'U') { // left part
          const uIdx = parseInt(cur.slice(1),10);
          adj[uIdx].forEach(vIdx => {
            if (visited.has(`V${vIdx}`)) return;
            visited.add(`V${vIdx}`);
            parents[`V${vIdx}`] = cur;
            // add to tree
            if (!treeRoots[`V${vIdx}`]) treeRoots[`V${vIdx}`] = {id:`V${vIdx}`, children:[]};
            if (!parents[cur]) parents[cur] = null;
            queue.push(`V${vIdx}`);
            // if right vertex is free, construct path
            if (matchV[vIdx] === null) {
              // reconstruct path
              const path = [];
              let node = `V${vIdx}`;
              while (node) {
                const parent = parents[node];
                if (node[0] === 'V') {
                  const uIdx = parseInt(parent.slice(1),10);
                  const vIdx = parseInt(node.slice(1),10);
                  path.unshift([uIdx, vIdx]); // push at front
                }
                node = parent;
              }
              return {found:true, path, parents, treeRoots};
            } else {
              const nextU = matchV[vIdx];
              if (!visited.has(`U${nextU}`)) {
                visited.add(`U${nextU}`);
                parents[`U${nextU}`] = `V${vIdx}`;
                if (!treeRoots[`U${nextU}`]) treeRoots[`U${nextU}`] = {id:`U${nextU}`, children:[]};
                queue.push(`U${nextU}`);
              }
            }
          });
        }
      }
      return {found:false};
    }

    function augmentPath(path, matchU, matchV) {
      // path is array of [uIdx, vIdx] pairs
      for (let i=0; i<path.length; i++) {
        const [u,v] = path[i];
        const matched = matchV[v];
        matchU[u] = v;
        matchV[v] = u;
      }
    }

    function buildBfsTree(parents, roots) {
      // Build tree structure from parents and roots mapping
      // Return object mapping each node id to node object {id, children}
      // We'll create a root object containing all roots
      const tree = {id:'Tree', children:[]};
      // first pass: assign children
      Object.keys(parents).forEach(key => {
        const parent = parents[key];
        if (parent && roots[parent]) {
          roots[parent].children.push(roots[key]);
        } else if (!parent) { // root
          if (roots[key]) tree.children.push(roots[key]);
        }
      });
      return tree;
    }

    function createState(matchU, matchV, bfsTree, pathEdges, statusText) {
      return {
        matching: matchU.reduce((acc,uIdx)=>{ if(uIdx!==null) acc.push({u:uIdx, v:matchU[uIdx]}); return acc; }, []),
        cover: [], // not used
        bfsTree: bfsTree,
        highlightedPath: pathEdges,
        statusText
      };
    }
  }

  /* ------------ P5 DRAW INTERFACE ------------ */
  function setup() {
    const cnv = createCanvas(800, 600);
    cnv.parent('canvas-wrapper');
    canvasWidth = width;
    canvasHeight = height;
    generateAndRender(); // initial generation
  }

  function draw() {
    background(colors.background);
    if (!history.length) return;

    const state = history[currentStep];
    // draw graph on right side
    drawGraph(state);
    // draw BFS tree on left side
    if (state.bfsTree) drawTree(state.bfsTree);
    // status text
    document.getElementById('status-text').textContent = state.statusText;
  }

  /* ------------ DRAW HELPERS ------------ */
  function drawGraph(state) {
    const {adj, nLeft, nRight, verts, lowers} = currentGraph;
    const leftX0 = canvasWidth * bfsScale + 30;
    const rightX0 = leftX0 + 30;
    const treeSpace = canvasWidth * bfsScale;

    const leftYSpacing = (canvasHeight-200)/nLeft;
    const rightYSpacing = (canvasHeight-200)/nRight;
    const vertexRadius = 15;

    // Edges
    stroke(colors.defaultEdge);
    strokeWeight(1);
    adj.forEach((neighbors, uIdx) => {
      neighbors.forEach(vIdx => {
        const x1 = leftX0 + (vertexRadius*2);
        const y1 = 100 + uIdx * leftYSpacing;
        const x2 = rightX0 + (vertexRadius*2);
        const y2 = 100 + vIdx * rightYSpacing;
        line(x1,y1,x2,y2);
      });
    });

    // Matching edges
    stroke(colors.matchingEdge);
    strokeWeight(4);
    state.matching.forEach(pair => {
      const uIdx = pair.u;
      const vIdx = pair.v;
      const x1 = leftX0 + (vertexRadius*2);
      const y1 = 100 + uIdx * leftYSpacing;
      const x2 = rightX0 + (vertexRadius*2);
      const y2 = 100 + vIdx * rightYSpacing;
      line(x1,y1,x2,y2);
    });

    // Highlight path edges
    if (state.highlightedPath.length) {
      stroke(colors.highlightEdge);
      strokeWeight(4);
      state.highlightedPath.forEach(edge => {
        const uIdx = edge.u;
        const vIdx = edge.v;
        const x1 = leftX0 + (vertexRadius*2);
        const y1 = 100 + uIdx * leftYSpacing;
        const x2 = rightX0 + (vertexRadius*2);
        const y2 = 100 + vIdx * rightYSpacing;
        line(x1,y1,x2,y2);
      });
    }

    // Draw vertices
    fill(colors.defaultVertex);
    noStroke();
    // left vertices
    verts.forEach((v, i) => {
      const x = leftX0;
      const y = 100 + i * leftYSpacing;
      ellipse(x,y,vertexRadius*2);
      fill('white');
      textAlign(CENTER, CENTER);
      textSize(12);
      text(i+1, x, y);
      fill(colors.defaultVertex);
    });
    // right vertices
    lowers.forEach((v, i) => {
      const x = rightX0;
      const y = 100 + i * rightYSpacing;
      ellipse(x,y,vertexRadius*2);
      fill('white');
      textAlign(CENTER, CENTER);
      textSize(12);
      text(i+1, x, y);
      fill(colors.defaultVertex);
    });
  }

  function drawTree(treeObj) {
    const treeStartX = 20;
    const treeStartY = 50;
    const nodeSize = 30;
    const verticalGap = 80;
    const horizontalGap = 80;

    function drawNode(node, x, y, depth) {
      // draw node
      fill(colors.treeNode);
      stroke(colors.treeBorder);
      rect(x, y, nodeSize, nodeSize, 5);
      fill('white');
      textAlign(CENTER, CENTER);
      textSize(10);
      text(node.id, x + nodeSize/2, y + nodeSize/2);
      // draw children
      node.children.forEach((child, idx) => {
        const childX = x + (idx - (node.children.length-1)/2) * horizontalGap;
        const childY = y + verticalGap;
        stroke(colors.treeBorder);
        line(x + nodeSize/2, y + nodeSize, childX + nodeSize/2, childY);
        drawNode(child, childX, childY, depth+1);
      });
    }

    // treeObj may have multiple roots (suits)
    treeObj.children.forEach((root, idx) => {
      const x = treeStartX + (idx - (treeObj.children.length-1)/2)*horizontalGap;
      drawNode(root, x, treeStartY, 0);
    });
  }

  /* ------------ STATE & GENERATION ------------ */
  let currentGraph = {};

  function generateAndRender() {
    currentGraph = generateRandomGraph(graphSize, maxDeg);
    history = runHungarian(currentGraph);
    currentStep = 0;
    populateStepButtons();
    // trigger p5 to redraw
    redraw();
  }

  function populateStepButtons() {
    const container = document.getElementById('step-controls');
    container.innerHTML = ''; // clear
    history.forEach((state, idx) => {
      const btn = document.createElement('button');
      btn.id = `step-${idx+1}`;
      btn.textContent = idx+1;
      if (idx === 0) btn.classList.add('active');
      btn.addEventListener('click', () => {
        currentStep = idx;
        document.querySelectorAll('#step-controls button').forEach(b=>b.classList.remove('active'));
        btn.classList.add('active');
        redraw();
      });
      container.appendChild(btn);
    });
  }
</script>
</body>
