  body, html {margin:0;padding:0;font-family:Arial,Helvetica,sans-serif;background:#f9f9f9;}
  #main-container {max-width:1200px;margin:auto;padding:20px;}
  #control-panel {display:flex;flex-wrap:wrap;gap:20px;margin-bottom:20px;}
  .control-group {display:flex;flex-direction:column;padding:10px;border:1px solid #ddd;background:#fff;border-radius:4px;}
  .control-group label{font-weight:bold;margin-bottom:5px;}
  .scale-group, .range-group, .elements-group {display:flex;align-items:center;gap:10px;}
  .elements-group span{margin-left:5px;font-size:1.1em;color:#333;}
  .alg-group {display:flex;flex-wrap:wrap;gap:10px;}
  .alg-group span{font-weight:bold;margin-right:10px;}
  .alg-group label{margin-right:15px;}
  #plot-container{width:100%;height:500px;}
  @media(max-width:600px){
    .control-group{width:100%;}
  }
</style>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <!-- Plot Settings -->
    <div class="control-group scale-group" id="plot-settings">
      <label>Scale:</label>
      <label><input type="radio" name="scale-type" id="radio-scale-linear" checked> linear</label>
      <label><input type="radio" name="scale-type" id="radio-scale-logarithmic"> logarithmic</label>
    </div>
    <div class="control-group range-group" id="plot-range">
      <label>Range:</label>
      <label><input type="radio" name="range-type" id="radio-range-fixed" checked> fixed</label>
      <label><input type="radio" name="range-type" id="radio-range-automatic"> automatic</label>
    </div>
    <div class="control-group elements-group" id="elements-control">
      <label for="slider-elements">Number of elements:</label>
      <input type="range" id="slider-elements" min="1" max="20" step="1" value="10">
      <span id="display-elements">+ 10</span>
    </div>

    <!-- Algorithm Selection -->
    <div class="control-group alg-group" id="algorithms-o-n2">
      <span>\(O(n^2)\)</span>
      <label><input type="checkbox" id="check-bubble" checked> bubble</label>
      <label><input type="checkbox" id="check-selection" checked> selection</label>
      <label><input type="checkbox" id="check-insertion" checked> insertion</label>
    </div>
    <div class="control-group alg-group" id="algorithms-o-nlogn">
      <span>\(O(n \log n)\)</span>
      <label><input type="checkbox" id="check-merge"> merge</label>
      <label><input type="checkbox" id="check-quick"> quick</label>
      <label><input type="checkbox" id="check-heap"> heap</label>
    </div>
    <div class="control-group alg-group" id="algorithms-o-nn-factorial">
      <span>\(O(n \cdot n!)\)</span>
      <label><input type="checkbox" id="check-bogo"> bogo</label>
    </div>
  </div>

  <div id="plot-container"></div>
</div>

<script>
  // Utility functions
  function factorial(n){
    if(n<=1) return 1;
    let res=1;
    for(let i=2;i<=n;i++) res*=i;
    return res;
  }
  function computeData(n){
    const x = [];
    const y = {
      bubble: [], selection: [], insertion: [], merge: [], quick: [], heap: []
    };
    for(let i=1;i<=n;i++){
      x.push(i);
      y.bubble.push(i*i);
      y.selection.push(0.25*i*i);
      y.insertion.push(i*factorial(i));
      y.merge.push(i*Math.log2(i));
      y.quick.push(1.2*i*Math.log2(i));
      y.heap.push(1.5*i*Math.log2(i));
    }
    return {x, y};
  }

  // Get elements
  const slider = document.getElementById('slider-elements');
  const display = document.getElementById('display-elements');

  const scaleLinear = document.getElementById('radio-scale-linear');
  const scaleLogarithmic = document.getElementById('radio-scale-logarithmic');
  const rangeFixed = document.getElementById('radio-range-fixed');
  const rangeAutomatic = document.getElementById('radio-range-automatic');

  const checkBubble = document.getElementById('check-bubble');
  const checkSelection = document.getElementById('check-selection');
  const checkInsertion = document.getElementById('check-insertion');
  const checkMerge = document.getElementById('check-merge');
  const checkQuick = document.getElementById('check-quick');
  const checkHeap = document.getElementById('check-heap');
  const checkBogo = document.getElementById('check-bogo');

  // Initial data
  let currentN = parseInt(slider.value);

  const dataTemplate = [
    {name:'bubble',mode:'lines',line:{color:'green',width:5},visible:true},
    {name:'selection',mode:'lines',line:{color:'orange',width:5,dash:'dot'},visible:true},
    {name:'insertion',mode:'lines',line:{color:'red',width:2},visible:true},
    {name:'merge',mode:'lines',line:{color:'blue',width:2},visible:false},
    {name:'quick',mode:'lines',line:{color:'purple',width:2},visible:false},
    {name:'heap',mode:'lines',line:{color:'cyan',width:2},visible:false}
  ];

  // Layout
  const layout = {
    title:'time complexity',
    xaxis:{title:'number of elements',range:[0,20],autorange:false},
    yaxis:{title:'operations',range:[0,500],autorange:false,type:'linear'},
    legend:{orientation:'h',y:0}
  };

  // Render initial plot
  function renderPlot(){
    const {x, y} = computeData(currentN);
    const data = dataTemplate.map(trace=>{
      const visible = trace.name === 'insertion'
        ? (checkInsertion.checked || checkBogo.checked)
        : trace.visible;
      return {
        x: x,
        y: y[trace.name],
        name: trace.name,
        type: 'scatter',
        mode:'lines',
        line: trace.line,
        visible: visible
      };
    });
    Plotly.react('plot-container', data, layout, {responsive:true});
  }

  // Event listeners
  slider.addEventListener('input',e=>{
    currentN = parseInt(e.target.value);
    display.textContent = `+ ${currentN}`;
    renderPlot();
  });

  function updateYScale(){
    layout.yaxis.type = scaleLinear.checked ? 'linear' : 'log';
    Plotly.relayout('plot-container', layout);
  }
  scaleLinear.addEventListener('change',updateYScale);
  scaleLogarithmic.addEventListener('change',updateYScale);

  function updateRange(){
    if(rangeFixed.checked){
      layout.xaxis.autoRange = false;
      layout.yaxis.autoRange = false;
      layout.xaxis.range = [0,20];
      layout.yaxis.range = [0,500];
    } else {
      layout.xaxis.autoRange = true;
      layout.yaxis.autoRange = true;
    }
    Plotly.relayout('plot-container', layout);
  }
  rangeFixed.addEventListener('change',updateRange);
  rangeAutomatic.addEventListener('change',updateRange);

  function updateVisibility(){
    renderPlot();
  }
  [checkBubble,checkSelection,checkInsertion,checkMerge,checkQuick,checkHeap,checkBogo].forEach(cb=>cb.addEventListener('change',updateVisibility));

  // Initial render
  renderPlot();
  display.textContent = `+ ${currentN}`;
</script>
</body>
</html>
