<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="utf-8">
<title>Chemical Structure Editor</title>
<link rel="preconnect" href="https://cdn.jsdelivr.net">
<script src="https://cdn.jsdelivr.net/npm/p5@1.6.0/lib/p5.min.js"></script>
<style>
  *, *::before, *::after{box-sizing:border-box;}
  body{margin:0;font-family:sans-serif;display:flex;flex-direction:column;height:100vh;background:#fff;}
  #toolbar{display:flex;flex-wrap:wrap;padding:5px;background:#f8f8f8;border-bottom:1px solid #ccc;}
  #toolbar button{background:#e0e0e0;border:1px solid #999;border-radius:3px;padding:5px 8px;margin:2px;cursor:pointer;}
  #toolbar button.selected{background:#a0c0ff;}
  #canvas-container{flex:1;display:flex;justify-content:center;align-items:center;background:#fff;}
  #footer-controls{display:flex;align-items:center;justify-content:space-between;padding:5px;background:#f8f8f8;border-top:1px solid #ccc;}
  #select-structure{font-size:16px;width:200px;height:90px;font-family:sans-serif;}
  #select-structure option{font-size:16px;}
</style>
</head>
<body>
<div id="toolbar">
  <button id="btn-charge-minus">-</button>
  <button id="btn-charge-plus">+</button>
  <button id="btn-charge-2">2</button>
  <button id="btn-element-H">H</button>
  <button id="btn-element-B">B</button>
  <button id="btn-element-C">C</button>
  <button id="btn-element-N">N</button>
  <button id="btn-element-O">O</button>
  <button id="btn-element-F">F</button>
  <button id="btn-element-P">P</button>
  <button id="btn-element-S">S</button>
  <button id="btn-element-Cl">Cl</button>
  <button id="btn-element-Se">Se</button>
  <button id="btn-element-Br">Br</button>
  <button id="btn-element-I">I</button>
  <button id="btn-element-Li">Li</button>
  <button id="btn-element-Na">Na</button>
  <button id="btn-element-K">K</button>
  <button id="btn-element-Be">Be</button>
  <button id="btn-element-Mg">Mg</button>
  <button id="btn-element-Ca">Ca</button>
  <button id="btn-element-Cr">Cr</button>
  <button id="btn-element-Mn">Mn</button>
  <button id="btn-element-Fe">Fe</button>
  <button id="btn-element-Co">Co</button>
  <button id="btn-element-Cu">Cu</button>
  <button id="btn-element-Zn">Zn</button>
  <button id="btn-element-Al">Al</button>
  <button id="btn-element-Si">Si</button>
</div>

<div id="canvas-container"></div>

<div id="footer-controls">
  <button id="btn-undo">undo</button>
  <select id="select-structure" size="4"></select>
  <button id="btn-add">add</button>
</div>

<script>
/*** GLOBAL STATE ***/
let sceneObjects = [];
let selectedTool = null;
let activeObject = null;
let interactionMode = null; // 'translate' or 'rotate'
let mouseOffsetX = 0, mouseOffsetY = 0;
let startAngle = 0, startRotation = 0;
let stageWidth = 800, stageHeight = 600;

/*** INITIALIZE ***/
function setup() {
  let c = createCanvas(stageWidth, stageHeight);
  c.parent('canvas-container');
  c.id('drawing-canvas');
  c.style('border','1px solid #ccc');
  textFont('Arial');
}

/*** DRAW LOOP ***/
function draw() {
  background(255);
  // draw all objects
  sceneObjects.forEach(obj => {
    push();
    translate(obj.x, obj.y);
    rotate(obj.rotation);
    if(obj.type==='atom') drawAtom(obj);
    else if(obj.type==='structure') drawStructure(obj);
    pop();
  });

  // preview when a tool is selected
  if(selectedTool) {
    push();
    translate(mouseX, mouseY);
    if(selectedTool.type==='atom') drawAtom(selectedTool);
    else if(selectedTool.type==='structure') drawStructure(selectedTool);
    pop();
  }
}

/*** TOOL HELPERS ***/
function drawAtom(obj, preview = false) {
  push();
  fill(0);
  noStroke();
  textAlign(CENTER, CENTER);
  textSize(preview ? 14 : 16);
  text(obj.value, 0, 0);
  pop();
}

function drawBond() {
  stroke(0); strokeWeight(2);
  line(-25,0,25,0);
}
function drawWater() {
  textSize(18); fill(0); textAlign(CENTER, CENTER);
  text('O',0,0);
  text('H',-20,-30);
  text('H',20,-30);
  stroke(0); line(0,0,-20,-30); line(0,0,20,-30);
}
function drawBorazine() {
  let r=40;
  stroke(0); noFill();
  for (let i=0;i<6;i++){
    let ang = PI/3 * i;
    let x=r*cos(ang), y=r*sin(ang);
    if(i>0){
      let prevX=r*cos(PI/3*(i-1)), prevY=r*sin(PI/3*(i-1));
      line(prevX,prevY,x,y);
    }
    textSize(16);
    text(i%2===0?'B':'N',x,y);
  }
  line(r*cos(5*PI/3), r*sin(5*PI/3), r*cos(0), r*sin(0));
}
function drawCyclopropylEthane() {
  let r=30;
  textSize(16);
  // ring
  for(let i=0;i<3;i++){
    let ang = TWO_PI/3 * i - PI/3;
    let x=r*cos(ang), y=r*sin(ang);
    if(i>0){
      let prevAng = TWO_PI/3*(i-1)-PI/3;
      let px=r*cos(prevAng), py=r*sin(prevAng);
      line(px,py,x,y);
    }
    text('C',x,y);
  }
  line(r*cos(-PI/3), r*sin(-PI/3), r*cos(TWO_PI/3*2-PI/3), r.sin(TWO_PI/3*2-PI/3));
  // ethane group
  let leftX=-50, leftY=0;
  text('C',leftX,leftY);
  line(0,0,leftX,leftY);
  text('C',leftX-30,leftY);
  line(leftX,leftY,leftX-30,leftY);
}
function drawIsopropylamine() {
  let r=30;
  textSize(16);
  // chain of 3 C
  for(let i=-1;i<=1;i++){
    let x=i*r, y=0;
    text('C',x,y);
    if(i!==0){
      let hY=i<0? -20:20;
      text('C',x,hY);
      line(x,0,x,hY);
    }
  }
  // NH2
  text('N',0,-30);
  line(0,0,0,-30);
  text('H', -10,-35); text('H',10,-35);
  line(0,-30,-10,-35); line(0,-30,10,-35);
}
function drawStructure(obj) {
  switch(obj.value){
    case 'bond': drawBond(); break;
    case 'water': drawWater(); break;
    case 'borazine': drawBorazine(); break;
    case 'cyclopropyl_ethane': drawCyclopropylEthane(); break;
    case 'isopropylamine': drawIsopropylamine(); break;
    default: break;
  }
}

/*** MOUSE EVENTS ***/
function mousePressed() {
  if(selectedTool) {
    // add new item
    let newObj = {...selectedTool, x:mouseX, y:mouseY, rotation:0, bboxRadius: selectedTool.type==='atom'?20:40};
    sceneObjects.push(newObj);
    selectedTool = null;
    deselectAllButtons();
    return;
  }
  // check for object interaction
  for(let i=sceneObjects.length-1;i>=0;i--){
    let obj = sceneObjects[i];
    let d = dist(mouseX, mouseY, obj.x, obj.y);
    if(d <= obj.bboxRadius){
      activeObject = obj;
      if(d <= 20){
        interactionMode='rotate';
        startAngle = atan2(mouseY-obj.y, mouseX-obj.x);
        startRotation = obj.rotation;
      }else{
        interactionMode='translate';
        mouseOffsetX = mouseX-obj.x;
        mouseOffsetY = mouseY-obj.y;
      }
      return;
    }
  }
}
function mouseDragged(){
  if(activeObject){
    if(interactionMode==='translate'){
      activeObject.x = mouseX-mouseOffsetX;
      activeObject.y = mouseY-mouseOffsetY;
    }else if(interactionMode==='rotate'){
      let currentAngle = atan2(mouseY-activeObject.y, mouseX-activeObject.x);
      activeObject.rotation = startRotation + (currentAngle - startAngle);
    }
  }
}
function mouseReleased(){
  activeObject=null;
  interactionMode=null;
}

/*** UTILITY ***/
function deselectAllButtons(){
  document.querySelectorAll('#toolbar button.selected').forEach(b=>b.classList.remove('selected'));
}

/*** UI EVENT BINDINGS ***/
window.addEventListener('DOMContentLoaded', ()=>{
  // populate structure dropdown
  const sel = document.getElementById('select-structure');
  sel.innerHTML = `
    <option value="bond" selected>—</option>
    <option value="water">Water</option>
    <option value="cyclopropyl_ethane">Cyclopropyl-ethane</option>
    <option value="borazine">Borazine</option>
    <option value="isopropylamine">Isopropylamine</option>
  `;

  // toolbar button listeners
  document.querySelectorAll('#toolbar button').forEach(btn=>{
    btn.addEventListener('click', ()=> {
      const id = btn.id;
      let tool;
      if(id.startsWith('btn-element-')){
        const sym = id.replace('btn-element-','');
        tool = {type:'atom', value:sym, bboxRadius:20};
      }else if(id.startsWith('btn-charge-')){
        const ch = id.replace('btn-charge-','');
        tool = {type:'atom', value:ch, bboxRadius:20};
      }
      if(tool){
        selectedTool = tool;
        deselectAllButtons();
        btn.classList.add('selected');
      }
    });
  });

  // undo
  document.getElementById('btn-undo').addEventListener('click', ()=>{
    sceneObjects.pop();
  });

  // add structure
  document.getElementById('btn-add').addEventListener('click', ()=>{
    const val = sel.value;
    const obj = {
      type:'structure',
      value:val,
      x:stageWidth/2,
      y:stageHeight/2,
      rotation:0,
      bboxRadius:40
    };
    sceneObjects.push(obj);
  });
});
</script>
</body>
</html>
