      <option value="0">0</option>
      <option value="1.0471975511965976">π/3</option>
      <option value="1.5707963267948966">π/2</option>
      <option value="2.0943951023931953">2π/3</option>
      <option value="3.141592653589793">π</option>
    </select>
    <span id="value-phase">-1.571</span>
  </div>
</div>

<div id="formula-display"></div>

<div id="canvas-container"></div>

<script>
  // Canvas & rendering constants
  const canvasWidth = 800;
  const canvasHeight = 400;
  const margin = 50; // left and top margins
  const scale = 30;   // pixels per unit

  // Wave parameters (initialized from UI defaults)
  let amplitude = 1.5;
  let frequency = 0.3;
  let wavelength = 4.5;
  let time = 2.5;
  let phase = -Math.PI / 2;

  let k = 2 * Math.PI / wavelength;
  let omega = 2 * Math.PI * frequency;

  // Update UI and global state
  function updateUI() {
    const amp = parseFloat(document.getElementById('slider-amplitude').value);
    const freq = parseFloat(document.getElementById('slider-frequency').value);
    const wl = parseFloat(document.getElementById('slider-wavelength').value);
    const t = parseFloat(document.getElementById('slider-time').value);
    const ph = parseFloat(document.getElementById('select-phase').value);

    amplitude = amp; frequency = freq; wavelength = wl; time = t; phase = ph;
    k = 2 * Math.PI / wavelength;
    omega = 2 * Math.PI * frequency;

    document.getElementById('value-amplitude').textContent = amp.toFixed(3);
    document.getElementById('value-frequency').textContent = freq.toFixed(3);
    document.getElementById('value-wavelength').textContent = wl.toFixed(3);
    document.getElementById('value-time').textContent = t.toFixed(3);
    document.getElementById('value-phase').textContent = ph.toFixed(3);

    // Build formula string
    let formula;
    if (Math.abs(phase - Math.PI / 2) < 1e-6) {
      formula = `${amplitude.toFixed(2)} \\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
    } else if (Math.abs(phase + Math.PI / 2) < 1e-6) {
      formula = `-${amplitude.toFixed(2)} \\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
    } else if (Math.abs(phase - Math.PI) < 1e-6) {
      formula = `-${amplitude.toFixed(2)} \\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
    } else if (Math.abs(phase) < 1e-6) {
      formula = `${amplitude.toFixed(2)} \\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
    } else {
      formula = `${amplitude.toFixed(2)} \\cos(${omega.toFixed(2)}t - ${k.toFixed(2)}x + ${phase.toFixed(2)})`;
    }

    document.getElementById('formula-display').innerHTML = `\\(y(x,t) = ${formula}\\)`;

    if (typeof MathJax !== 'undefined')
      MathJax.typesetPromise([document.getElementById('formula-display')]).catch(console.error);

    if (sketchInstance) sketchInstance.redraw();
  }

  // Attach events after DOM load
  document.addEventListener('DOMContentLoaded', () => {
    ['slider-amplitude', 'slider-frequency', 'slider-wavelength', 'slider-time'].forEach(id => {
      document.getElementById(id).addEventListener('input', updateUI);
    });
    document.getElementById('select-phase').addEventListener('change', updateUI);
    updateUI();
  });

  let sketchInstance;

  // p5.js sketch
  const sketch = (p) => {
    p.setup = () => {
      sketchInstance = p;
      const cnv = p.createCanvas(canvasWidth, canvasHeight);
      cnv.parent('canvas-container');
      p.noLoop();
      p.textSize(12);
      p.strokeWeight(1);
    };

    p.draw = () => {
      p.background(255);

      const originX = margin;
      const originY = canvasHeight / 2;

      // Axes
      p.stroke(0);
      p.line(originX, originY, canvasWidth - margin, originY); // x-axis
      p.line(canvasWidth - margin, originY, canvasWidth - margin - 10, originY - 10);
      p.line(canvasWidth - margin, originY, canvasWidth - margin - 10, originY + 10);

      p.line(originX, originY, originX, margin); // y-axis
      p.line(originX, margin, originX - 10, margin + 10);
      p.line(originX, margin, originX + 10, margin + 10);

      p.noStroke();
      p.fill(0);
      p.text('x', canvasWidth - margin, originY - 15);
      p.text('y', originX + 15, margin - 5);

      // Grid lines (light green, dashed)
      p.drawingContext.setLineDash([5, 5]);
      p.stroke(173, 255, 47);
      for (let i = 1; i <= 16; i++) {
        const px = originX + i * scale;
        p.line(px, originY, px, margin);
        p.line(px, originY, px, originY + 6);
        p.fill(0);
        p.noStroke();
        p.text(i.toString(), px, originY + 20);
        p.stroke(173, 255, 47);
      }
      p.drawingContext.setLineDash([]);

      // Wave curve
      p.noFill();
      p.stroke(0, 0, 255);
      p.strokeWeight(2);
      p.beginShape();
      for (let px = originX; px <= canvasWidth - margin; px++) {
        const wx = (px - originX) / scale;
        const wy = amplitude * Math.cos(omega * time - k * wx + phase);
        const py = originY - wy * scale;
        p.vertex(px, py);
      }
      p.endShape();

      // Points and vectors
      const velScale = 15; // pixels per unit of velocity
      const accScale = 5;  // pixels per unit of acceleration

      for (let x = 1; x <= 16; x++) {
        const wx = x;
        const wy = amplitude * Math.cos(omega * time - k * wx + phase);
        const px = originX + wx * scale;
        const py = originY - wy * scale;

        // Draw point
        p.fill(0);
        p.noStroke();
        p.ellipse(px, py, 6, 6);

        // Velocity
        const v = -amplitude * omega * Math.sin(omega * time - k * wx + phase);
        // Acceleration
        const a = -amplitude * omega * omega * Math.cos(omega * time - k * wx + phase);

        drawArrow(p, px, py, v * velScale, 'red', '𝑣');
        drawArrow(p, px, py, a * accScale, 'black', '𝑎');
      }
    };

    function drawArrow(p, baseX, baseY, vectorY, color, label) {
      const headSize = 6;
      const sign = vectorY >= 0 ? 1 : -1;
      const tipY = baseY + vectorY;
      p.stroke(color);
      p.fill(color);
      p.line(baseX, baseY, baseX, tipY);
      p.beginShape();
      p.vertex(baseX - headSize, tipY);
      p.vertex(baseX + headSize, tipY);
      p.vertex(baseX, tipY + headSize * sign);
      p.endShape(p.CLOSE);
      p.noStroke();
      p.fill(color);
      const labelX = baseX + headSize + 3;
      const labelY = tipY + 3 * sign;
      p.text(label, labelX, labelY);
    }
  };

  new p5(sketch);
</script>

</body>
</html>
