<script>
let epsilon = 0.0;
const slider = document.getElementById('slider-eccentricity');
const valDisplay = document.getElementById('value-eccentricity');
const energyVal = document.getElementById('energy-value');

// Update UI and computations
function updateEpsilon(evt){
  epsilon = parseFloat(evt.target.value);
  valDisplay.textContent = epsilon.toFixed(2);
  updateEnergy();
  redraw(); // trigger p5 redraw
}
slider.addEventListener('input', updateEpsilon);

// Compute and display energy
function updateEnergy(){
  const E = -0.25 * (1 - epsilon * epsilon);
  if (Math.abs(epsilon) < 1e-12) {
    energyVal.innerHTML = '\\(-\\frac{1}{4}\\)';
    MathJax.typesetPromise([energyVal]).catch(console.error);
  } else {
    energyVal.innerHTML = E.toFixed(2);
  }
}

const p5Sketch = function(p){
  const canvasWidth = 700;
  const canvasHeight = 500;
  const scale = 30; // 30 px per unit
  let originX, originY;

  p.setup = function(){
    const canvas = p.createCanvas(canvasWidth, canvasHeight);
    canvas.parent('canvas-container');
    p.noLoop();
    originX = canvasWidth * 0.7;
    originY = canvasHeight / 2;
  };

  p.draw = function(){
    p.background('#f0f0f0');
    p.push();
    // Move origin and flip y-axis
    p.translate(originX, originY);
    p.scale(1, -1);

    // Draw axes
    p.stroke(0);
    p.strokeWeight(1);
    p.line(-originX, 0, 320, 0); // X axis
    p.line(0, -originY, 0, 230); // Y axis

    // Tick marks
    p.textAlign(p.CENTER, p.TOP);
    p.textSize(12);
    for (let x = -15; x <= 5; x++){
      if (x === 0) continue;
      let px = x * scale;
      p.line(px, -5, px, 5);
      p.text(x, px, -10);
    }
    for (let y = -7; y <= 7; y++){
      if (y === 0) continue;
      let py = y * scale;
      p.line(-5, py, 5, py);
      p.text(y, -10, py);
    }
    p.text('Global\'x', 300, 0);
    p.text('Global\'y', 0, 240);

    // Central body
    p.noStroke();
    p.fill(0);
    p.ellipse(0, 0, 6, 6);

    // Orbit
    p.stroke('#4a80b5');
    p.strokeWeight(2);
    p.noFill();

    const p_latus = 2;
    // Determine angle sweep
    let thetaStart = 0, thetaEnd = Math.PI * 2;
    if (epsilon == 0){
      // Circle offset
      p.beginShape();
      for (let theta = 0; theta <= Math.PI*2; theta += 0.01){
        const x = p_latus * Math.cos(theta) - p_latus;
        const y = p_latus * Math.sin(theta);
        p.vertex(x, y);
      }
      p.endShape(p.Closed);
    } else if (epsilon > 0 && epsilon < 1){
      // Ellipse
      p.beginShape();
      for (let theta = 0; theta <= Math.PI*2; theta += 0.01){
        const r = p_latus / (1 + epsilon * Math.cos(theta));
        const x = r * Math.cos(theta);
        const y = r * Math.sin(theta);
        p.vertex(x, y);
      }
      p.endShape(p.Closed);
    } else if (Math.abs(epsilon - 1) < 1e-12){
      // Parabola
      p.beginShape();
      for (let theta = -2.2; theta <= 2.2; theta += 0.005){
        const denom = (1 + epsilon * Math.cos(theta));
        if (denom === 0) continue;
        const r = p_latus / denom;
        const x = r * Math.cos(theta);
        const y = r * Math.sin(theta);
        p.vertex(x, y);
      }
      p.endShape();
    } else if (epsilon > 1){
      // Hyperbola
      const thetaMax = Math.acos(-1/epsilon);
      p.beginShape();
      for (let theta = -thetaMax; theta <= thetaMax; theta += 0.01){
        const denom = (1 + epsilon * Math.cos(theta));
        if (denom < 1e-6) continue;
        const r = p_latus / denom;
        const x = r * Math.cos(theta);
        const y = r * Math.sin(theta);
        p.vertex(x, y);
      }
      p.endShape();
    }
    p.pop();
  };
};

new p5(p5Sketch);
</script>
</body>
</html>
