let totalSteps = 10;
let currentStep = 0;
let plotMode = 'conflicts'; // or 'eigenvalues'

/* ---------- Utility ---------- */
function randInt(max) { return Math.floor(Math.random() * max); }

// Hamming distance between two indices represented as binary strings
function hamming(a, b) {
  let diff = a ^ b;
  let dist = 0;
  while (diff) { dist += diff & 1; diff >>= 1; }
  return dist;
}

// Initialize problem instance
function initProblem() {
  // choose 2 random solutions
  solutions = [];
  while (solutions.length < 2) {
    let idx = randInt(N);
    if (!solutions.includes(idx)) solutions.push(idx);
  }
  // compute conflict numbers
  for (let i = 0; i < N; i++) {
    if (solutions.includes(i)) conflict[i] = 0;
    else {
      let d1 = hamming(i, solutions[0]);
      let d2 = hamming(i, solutions[1]);
      conflict[i] = Math.min(d1, d2);
    }
  }
}

// --------- Algorithm implementation (simplified) ---------
function computeStateVector() {
  // start with uniform superposition
  const norm = 1 / Math.sqrt(N);
  let vec = Array(N).fill(math.complex(norm, 0));

  // modify based on algorithm and step
  const s = currentStep / totalSteps;
  switch (algorithm) {
    case 'unstructured':
      // simple amplitude amplification: increase amplitude of solutions
      solutions.forEach(idx => {
        vec[idx] = math.add(vec[idx], math.complex(0.5 * s, 0));
      });
      break;
    case 'adiabatic':
    case 'discrete-adiabatic':
    case 'heuristic':
      // heuristic: amplify solutions proportionally to s, damp others
      for (let i = 0; i < N; i++) {
        if (solutions.includes(i)) {
          vec[i] = math.add(vec[i], math.complex(0.5 * s, 0));
        } else {
          vec[i] = math.multiply(vec[i], 1 - 0.1 * s);
        }
      }
      break;
  }

  // normalize
  let sum = 0;
  for (let i = 0; i < N; i++) {
    sum += Math.pow(math.abs(vec[i]), 2);
  }
  const scale = 1 / Math.sqrt(sum);
  vec = vec.map(c => math.multiply(c, scale));

  return vec;
}

// --------- Plotting ---------- */
function plotAmplitudes(vec) {
  const x = [];
  const y = [];
  const colors = [];
  for (let i = 0; i < N; i++) {
    x.push(math.re(vec[i]));
    y.push(math.im(vec[i]));
    if (solutions.includes(i)) {
      colors.push('black');
    } else {
      // color by conflict number (simple mapping)
      const c = conflict[i];
      const hue = Math.floor(240 - c * 20); // 240=blue, 0=red
      colors.push(`hsl(${hue}, 80%, 50%)`);
    }
  }
  const trace = {
    x, y,
    mode: 'markers',
    type: 'scattergl',
    marker: { color: colors, size: 8 }
  };
  const layout = {
    xaxis: { title: 'Re', range: [-1, 1] },
    yaxis: { title: 'Im', range: [-1, 1] },
    margin: { t: 30 }
  };
  Plotly.newPlot('plot-amplitudes', [trace], layout, {displayModeBar: false});
}

function plotProbability(vec) {
  // group probabilities by conflict number
  const probs = {};
  for (let i = 0; i < N; i++) {
    const c = conflict[i];
    const p = Math.pow(math.abs(vec[i]), 2);
    probs[c] = (probs[c] || 0) + p;
  }
  const xs = Object.keys(probs).map(Number).sort((a,b)=>a-b);
  const ys = xs.map(x => probs[x]);

  const trace = {
    x: xs,
    y: ys,
    type: 'bar',
    marker: { color: 'steelblue' }
  };
  const layout = {
    yaxis: { title: 'probability', range: [0, 1] },
    margin: { t: 30 },
    title: plotMode === 'conflicts' ? 'conflicts' : 'eigenvalues'
  };
  if (plotMode === 'conflicts') layout.xaxis = { title: 'number of conflicts' };
  else layout.xaxis = { title: 'eigenvalue' }; // simplified

  Plotly.newPlot('plot-probability', [trace], layout, {displayModeBar: false});
}

// --------- Update function ----------
function updatePlots() {
  const vec = computeStateVector();
  plotAmplitudes(vec);
  plotProbability(vec);
}

// --------- Event Handlers ----------
function setActiveButton(container, btnId) {
  const btns = container.querySelectorAll('button');
  btns.forEach(b => b.classList.remove('active'));
  document.getElementById(btnId).classList.add('active');
}

document.getElementById('btn-unstructured').addEventListener('click', () => {
  algorithm = 'unstructured';
  setActiveButton(document.getElementById('control-panel'), 'btn-unstructured');
  totalSteps = 5;
  document.getElementById('slider-total-steps').value = totalSteps;
  document.getElementById('display-total-steps').textContent = totalSteps;
  document.getElementById('slider-current-step').max = totalSteps;
  currentStep = 0;
  document.getElementById('slider-current-step').value = currentStep;
  document.getElementById('display-current-step').textContent = currentStep;
  document.getElementById('label-current-step').textContent = `Current step (0 to ${totalSteps}):`;
  updatePlots();
});

document.getElementById('btn-adiabatic').addEventListener('click', () => {
  algorithm = 'adiabatic';
  setActiveButton(document.getElementById('control-panel'), 'btn-adiabatic');
  totalSteps = 200;
  document.getElementById('slider-total-steps').value = totalSteps;
  document.getElementById('display-total-steps').textContent = totalSteps;
  document.getElementById('slider-current-step').max = totalSteps;
  currentStep = 0;
  document.getElementById('slider-current-step').value = currentStep;
  document.getElementById('display-current-step').textContent = currentStep;
  document.getElementById('label-current-step').textContent = `Current step (0 to ${totalSteps}):`;
  updatePlots();
});

document.getElementById('btn-discrete-adiabatic').addEventListener('click', () => {
  algorithm = 'discrete-adiabatic';
  setActiveButton(document.getElementById('control-panel'), 'btn-discrete-adiabatic');
  totalSteps = 200;
  document.getElementById('slider-total-steps').value = totalSteps;
  document.getElementById('display-total-steps').textContent = totalSteps;
  document.getElementById('slider-current-step').max = totalSteps;
  currentStep = 0;
  document.getElementById('slider-current-step').value = currentStep;
  document.getElementById('display-current-step').textContent = currentStep;
  document.getElementById('label-current-step').textContent = `Current step (0 to ${totalSteps}):`;
  updatePlots();
});

document.getElementById('btn-heuristic').addEventListener('click', () => {
  algorithm = 'heuristic';
  setActiveButton(document.getElementById('control-panel'), 'btn-heuristic');
  totalSteps = 10;
  document.getElementById('slider-total-steps').value = totalSteps;
  document.getElementById('display-total-steps').textContent = totalSteps;
  document.getElementById('slider-current-step').max = totalSteps;
  currentStep = 0;
  document.getElementById('slider-current-step').value = currentStep;
  document.getElementById('display-current-step').textContent = currentStep;
  document.getElementById('label-current-step').textContent = `Current step (0 to ${totalSteps}):`;
  updatePlots();
});

document.getElementById('slider-total-steps').addEventListener('input', (e) => {
  totalSteps = parseInt(e.target.value);
  document.getElementById('display-total-steps').textContent = totalSteps;
  const currentSlider = document.getElementById('slider-current-step');
  currentSlider.max = totalSteps;
  if (currentStep > totalSteps) {
    currentStep = totalSteps;
    currentSlider.value = currentStep;
    document.getElementById('display-current-step').textContent = currentStep;
  }
  document.getElementById('label-current-step').textContent = `Current step (0 to ${totalSteps}):`;
  updatePlots();
});

document.getElementById('slider-current-step').addEventListener('input', (e) => {
  currentStep = parseInt(e.target.value);
  document.getElementById('display-current-step').textContent = currentStep;
  updatePlots();
});

document.getElementById('btn-conflicts').addEventListener('click', () => {
  plotMode = 'conflicts';
