function vecSub(a,b){ return [a[0]-b[0], a[1]-b[1]]; }
function vecAdd(a,b){ return [a[0]+b[0], a[1]+b[1]]; }
function vecScale(v,s){ return [v[0]*s, v[1]*s]; }
