    }else{
        res = vecAdd(
            vecScale(bPar, rPar/vecLen(bPar)),
            vecScale(bPerp, rPerp/vecLen(bPerp))
        );
    }
    return res;
}

// Update error values
function updateErrors(){
    dopt = computeDopt(cVec, bVec);
    // Errors
    let errCA = 0.5 * vecLen(vecSub(cVec, aVec))**2;  // D(C, A)
    let errDB = 0.5 * vecLen(vecSub(dopt, bVec))**2;  // D(D, B)
    return [errCA, errDB];
}

// Boundary curve data for given theta
function computeCurve(){
    let sin2t = Math.sin(2*theta);
    let maxX = sin2t*sin2t;
    let xs = [], ys = [];
    let step = maxX / resamplePts;
    for(let i=0;i<=resamplePts;i++){
        let x = i*step;
        let y = Math.pow(sin2t - Math.sqrt(x),2);
        xs.push(x);
        ys.push(y);
    }
    return {xs, ys};
}

// Optional optimal pair vectors for plotting
function computeOptimalPair(){
    let sin2t = Math.sin(2*theta);
    // magnitude of optimal c
    let mOpt = radius * sin2t/2;
    // choose 45 degrees orientation
    let angleOpt = Math.PI/4;
    let cOpt = [mOpt * Math.cos(angleOpt), mOpt * Math.sin(angleOpt)];
    let dOptOpt = computeDopt(cOpt,bVec);
    // errors
    let err = (sin2t/2)**2;
    return {cOpt,dOptOpt,err};
}

// Format for display
function formatNum(x){ return x.toFixed(5); }

// ---- Update Display Elements ----
function updateSpans(){
    document.getElementById('span-theta-value').textContent = formatNum(theta).replace('.',',');
    document.getElementById('span-sinsq-theta-value').textContent = formatNum(Math.sin(theta)**2);
}

// ---- Plotly Setup ----
let plotObj = null;
function initPlot(){
    const curve = computeCurve();
    const [errCA, errDB] = updateErrors();
    const optimal = computeOptimalPair();
    const traceCurve = {
        x: curve.xs,
        y: curve.ys,
        mode: 'lines',
        name: 'Trade‑off curve',
        line:{color:'#1f77b4'}
    };
    const traceCur = {
        x: [errCA],
        y: [errDB],
        mode: 'markers',
        name: 'Current Error',
        marker:{color:'#d62728',size:10}
    };
    const traceOpt = {
        x: [optimal.err],
        y: [optimal.err],
        mode: 'markers',
        name: 'Optimal Error',
        marker:{color:'#2ca02c',size:10},
        visible: document.getElementById('checkbox-optimal-pair').checked ? true : false
    };
    const layout = {
        title: 'Error Trade‑off',
        xaxis:{title:'D(C, A)',range:[0,1.5]},
        yaxis:{title:'D(C, B)',range:[0,1.5]},
        showlegend:true,
        margin:{l:50,r:20,t:40,b:50}
    };
    plotObj = Plotly.newPlot('plot-error',[traceCurve,traceCur,traceOpt],layout,{displayModeBar:false});
}

function updatePlot(){
    const curve = computeCurve();
    const [errCA, errDB] = updateErrors();
    const optimal = computeOptimalPair();
    const newTraces = [
        {x: curve.xs, y: curve.ys}, // boundary
        {x: [errCA], y: [errDB]},   // current
        {x: [optimal.err], y: [optimal.err]} // optimal
    ];
    const options = {};
    const vizOpt = {
        curve:'recreate',
        current:'recreate',
        optimal: document.getElementById('checkbox-optimal-pair').checked? 'recreate' : 'hidden'
    };
    Plotly.react('plot-error',[{
        x: newTraces[0].x, y:newTraces[0].y, mode:'lines',name:'Trade‑off curve',line:{color:'#1f77b4'}
    },{
        x:newTraces[1].x, y:newTraces[1].y, mode:'markers',name:'Current Error',
        marker:{color:'#d62728',size:10}
    },{
        x:newTraces[2].x, y:newTraces[2].y, mode:'markers',name:'Optimal Error',
        marker:{color:'#2ca02c',size:10},
        visible: vizOpt.optimal==='recreate' ? true : false
    }],null,{displayModeBar:false});
}

// ---- p5.js Sketch ----
function setup(){
    let cnv = createCanvas(400,400);
    cnv.parent('canvas-container');
    noLoop();
    // Initial computations
    recomputeAB();
    updateSpans();
    initPlot();
}

function draw(){
    background(255);
    // Transform origin to bottom centre, positive y up
    push();
    translate(width/2,height);
    scale(1,-1);
    // Semi‑circle
    stroke(0);strokeWeight(2);
    noFill();
    arc(0,0,radius,radius,0,PI);
    // Axial line (baseline)
    line(-radius,0,radius,0);
    // Vectors
    drawVector(aVec,'blue','a');
    drawVector(bVec,'red','b');
    drawVector(cVec,'green','c');
    // Optional drawing
    if(document.getElementById('checkbox-metric-circle').checked){
        let rMetric = vecLen(vecSub(cVec,aVec));
        drawingContext.setLineDash([10,10]);stroke('rgba(0,0,255,0.5)'); strokeWeight(1);
        ellipse(aVec[0],aVec[1],2*rMetric,2*rMetric);
        drawingContext.setLineDash([]);
    }
    if(document.getElementById('checkbox-metric-region').checked){
        drawingContext.setLineDash([5,5]);stroke('rgba(0,128,0,0.5)');strokeWeight(1);
        ellipse(0,0,2*vecLen(cVec),2*vecLen(cVec));
        drawingContext.setLineDash([]);
    }
    if(document.getElementById('checkbox-first-order-region').checked){
        drawingContext.setLineDash([5,5]);stroke('rgba(255,165,0,0.5)');strokeWeight(1);
        ellipse(0,0,2*vecLen(dopt),2*vecLen(dopt));
        drawingContext.setLineDash([]);
    }
    if(document.getElementById('checkbox-first-order-vec').checked){
        // Dashed orange vector from b to dopt
        drawingContext.setLineDash([5,5]); stroke('orange'); strokeWeight(2);
        line(bVec[0],bVec[1],dopt[0],dopt[1]); drawingContext.setLineDash([]);
        // Arrowhead
        let dir = vecSub(dopt,bVec);
        let len = vecLen(dir);
        if(len>0){
            let ang = Math.atan2(dir[1],dir[0]);
            push(); translate(dopt[0],dopt[1]); rotate(ang);
            triangle(-10,5,-10,-5,0,0);
            pop();
        }
    }
    // Optimal pair if enabled
    if(document.getElementById('checkbox-optimal-pair').checked){
        let {cOpt,dOptOpt} = computeOptimalPair();
        drawVector(cOpt,'purple','c_opt');
        drawVector(dOptOpt,'magenta','d_opt');
    }
    // End optional drawing
    pop();
}

function drawVector(vec,color,label) {
    push();
    stroke(color); strokeWeight(2);
    line(0,0,vec[0],vec[1]);
    // Arrowhead
    let len = vecLen(vec);
    if(len>0){
        let ang = Math.atan2(vec[1],vec[0]);
        push(); translate(vec[0],vec[1]); rotate(ang);
        fill(color); noStroke();
        triangle(-10,5,-10,-5,0,0);
        pop();
    }
    // Label
    noStroke(); fill(color); textSize(14); text(label,vec[0]*1.1,vec[1]*1.1);
    pop();
}

// ---- Event Listeners ----
document.getElementById('slider-theta').addEventListener('input',(e)=>{
    theta = parseFloat(e.target.value);
    recomputeAB();
    updateSpans();
    // Redraw
    redraw(); // p5
    updatePlot();
});

['checkbox-metric-circle','checkbox-metric-region','checkbox-first-order-vec',
 'checkbox-first-order-region','checkbox-optimal-pair','checkbox-optimal-region'].forEach(id=>{
    document.getElementById(id).addEventListener('change',()=>{
        redraw();
        updatePlot();
    });
});

// Dragging of vector c
function mousePressed(){
    let mx = mouseX - width/2;
    let my = height - mouseY;
    let distToC = Math.hypot(mx - cVec[0], my - cVec[1]);
    if(distToC<=10){
        draggingC = true;
        dragOffset = [cVec[0]-mx, cVec[1]-my];
    }
}
function mouseDragged(){
    if(draggingC){
        let mx = mouseX - width/2;
        let my = height - mouseY;
        let newC = [mx+dragOffset[0], my+dragOffset[1]];
        // Clamp to radius
        let len = vecLen(newC);
        if(len>radius){ newC = vecScale(newC, radius/len); }
        cVec = newC;
        redraw();
        updatePlot();
    }
}
function mouseReleased(){
    draggingC = false;
}
</script>
</body>
