<script>
  // Utility function to compute the analytical temperature using Fourier series
  function computeT(x, t, terms = 100) {
    let sum = 0;
    const pi = Math.PI;
    for (let n = 1; n <= terms; n++) {
      const coeff = (8 / (pi * pi)) * (1 / (n * n));
      const sin_npi_half = Math.sin(n * pi / 2);
      sum += coeff * sin_npi_half * Math.sin(n * pi * x) * Math.exp(-2 * n * n * pi * pi * t);
    }
    return sum;
  }

  // Generates an array of x-values (smooth curve)
  function generateCurveX(num = 200) {
    const xArr = [];
    for (let i = 0; i <= num; i++) {
      xArr.push(i / num);
    }
    return xArr;
  }

  // Computes Chebyshev-Gauss-Lobatto collocation points in [0,1]
  function computeChebyshevPoints(numPoints) {
    const N = numPoints - 1;
    const points = [];
    for (let j = 0; j <= N; j++) {
      const x = (Math.cos(Math.PI * j / N) + 1) / 2;
      points.push(x);
    }
    return points;
  }

  // Update the plot based on current slider values
  function updatePlot() {
    const numPoints = parseInt(document.getElementById('slider-points').value, 10);
    const t = parseFloat(document.getElementById('slider-time').value);

    // Prepare analytical curve
    const curveX = generateCurveX(200);
    const curveY = curveX.map(x => computeT(x, t));

    // Prepare numerical points
    const chebX = computeChebyshevPoints(numPoints);
    const chebY = chebX.map(x => computeT(x, t));

    const data = [
      {
        x: curveX,
        y: curveY,
        type: 'scatter',
        mode: 'lines',
        line: { color: 'blue', width: 3 }
      },
      {
        x: chebX,
        y: chebY,
        type: 'scatter',
        mode: 'markers',
        marker: { color: 'red', size: 8 }
      }
    ];

    const layout = {
      xaxis: { title: 'x', range: [0,1], tickmode: 'linear', dtick: 0.2, gridcolor: '#e0e0e0' },
      yaxis: { title: 'T', range: [-0.1,1.1], tickmode: 'linear', dtick: 0.2, gridcolor: '#e0e0e0' },
      showlegend: false,
      margin: { t:20, r:20, b:40, l:40 },
      hovermode: false
    };

    Plotly.react('plot-container', data, layout);
  }

  // Initialize labels and plot on page load
  document.addEventListener('DOMContentLoaded', () => {
    const pointsSlider = document.getElementById('slider-points');
    const timeSlider = document.getElementById('slider-time');

    document.getElementById('label-points').textContent = pointsSlider.value;
    document.getElementById('label-time').textContent = parseFloat(timeSlider.value).toFixed(3);

    updatePlot();

    // Event listeners for real-time updates
