<script src="https://cdn.jsdelivr.net/npm/p5@1.7.0/lib/p5.min.js"></script>
</head>
<body>
<div id="main">
  <div id="control-panel">
    <h4>Choose 'regular points' below, then the number of regular points,</h4>
    <div id="regular-points-selector">
      <button id="btn-regular-3">3</button>
      <button id="btn-regular-4" class="active">4</button>
      <button id="btn-regular-5">5</button>
    </div>
    <p>and drag them anywhere.</p>
    <p>Once these points are chosen, you can drag the points to see how the network changes in real time.</p>
    <h4>Now choose 'Steiner points' and how many you want to use.</h4>
    <div>
      <input type="radio" name="point-mode" id="radio-regular">
      <label for="radio-regular">regular points</label>
      <input type="radio" name="point-mode" id="radio-steiner" checked>
      <label for="radio-steiner">Steiner points</label>
    </div>
    <div id="steiner-points-selector">
      <button id="btn-steiner-0">0</button>
      <button id="btn-steiner-1">1</button>
      <button id="btn-steiner-2" class="active">2</button>
      <button id="btn-steiner-3">3</button>
    </div>
    <p>You can drag them anywhere.</p>
    <p>Can you find an optimal position for the Steiner points such that the total network length is minimized?</p>
    <hr>
    <div>
      <label for="checkbox-show-angles"><input type="checkbox" id="checkbox-show-angles" checked> show the angles</label>
    </div>
    <hr>
    <div>
      <p>The total length of the network is:</p>
      <h3 id="text-total-length"></h3>
    </div>
  </div>
  <div id="canvas-container">
    <div id="canvas-wrapper"></div>
  </div>
</div>

<script>
/* ------------------ Global State ------------------ */
let numRegularPoints = 4;
let numSteinerPoints = 2;
let pointMode = 'steiner';  // 'regular' or 'steiner'
let showAngles = true;
let regularPoints = [];   // each {x, y}
let steinerPoints = [];   // each {x, y}
let mstEdges = [];        // each {i, j, w}
let totalLength = 0;
let dragging = false;
let dragIndex = null;   // index in current array
let dragType = '';      // 'regular' or 'steiner'

/* ------------------ Utility Functions ------------------ */
function dist(a, b) {
  return Math.sqrt((a.x-b.x)**2 + (a.y-b.y)**2);
}

function computeMST() {
  const pts = [...regularPoints, ...steinerPoints];
  const n = pts.length;
  // build all edges
  const edges = [];
  for (let i = 0; i < n; i++) {
    for (let j = i+1; j < n; j++) {
      edges.push({i, j, w: dist(pts[i], pts[j])});
    }
  }
  // Kruskal
  edges.sort((a,b)=>a.w-b.w);
  const parent = Array(n).fill(0).map((_,i)=>i);
  const find = i=>parent[i]===i? i : parent[i]=find(parent[i]);
  const union = (a,b)=>{ parent[find(a)] = find(b); };
  mstEdges = [];
  totalLength = 0;
  for (const e of edges) {
    if (find(e.i)!==find(e.j)) {
      union(e.i, e.j);
      mstEdges.push(e);
      totalLength += e.w;
      if (mstEdges.length===n-1) break;
    }
  }
  updateLengthText();
}

/* ------------------ Point Initialization ------------------ */
function initRegularPoints() {
  const centerX = 300; const centerY = 300;  // will be updated by p5
  const radius = 240;  // 40% of canvas width 600
  regularPoints = [];
  for (let i = 0; i < numRegularPoints; i++) {
    const theta = (2*Math.PI*i/numRegularPoints) - Math.PI/2;
    regularPoints.push({x: centerX + radius*Math.cos(theta), y: centerY + radius*Math.sin(theta)});
  }
}

function initSteinerPoints() {
  const centerX = 300; const centerY = 300; // will be updated by p5
  steinerPoints = [];
  for (let i = 0; i < numSteinerPoints; i++) {
    steinerPoints.push({x: centerX, y: center});
  }
}

function repositionCentralPoints() {
  // Called when regular points change; center points reset
  const centerX = 300; const centerY = 300;
  steinerPoints = steinerPoints.map(()=>({x:centerX, y:centerY}));
}

/* ------------------ Update Text ------------------ */
function updateLengthText() {
  document.getElementById('text-total-length').textContent = totalLength.toFixed(5);
}

/* ------------------ Button and Control Handlers ------------------ */
document.querySelectorAll('#regular-points-selector button').forEach(b=>{
  b.addEventListener('click', ()=>{
    numRegularPoints = parseInt(b.textContent);
    toggleActiveButton('#regular-points-selector button', b);
    initRegularPoints();
    repositionCentralPoints();
    computeMST();
  });
});
document.querySelectorAll('#steiner-points-selector button').forEach(b=>{
  b.addEventListener('click', ()=>{
    numSteinerPoints = parseInt(b.textContent);
    toggleActiveButton('#steiner-points-selector button', b);
    if (numSteinerPoints > steinerPoints.length) {
      const centerX = 300, centerY = 300;
      for (let i = steinerPoints.length; i < numSteinerPoints; i++) {
        steinerPoints.push({x:centerX, y:centerY});
      }
    } else if (numSteinerPoints < steinerPoints.length) {
      steinerPoints.splice(numSteinerPoints);
    }
    computeMST();
  });
});
function toggleActiveButton(selector, activeBtn){
  document.querySelectorAll(selector).forEach(b=>b.classList.remove('active'));
  activeBtn.classList.add('active');
}
document.getById('radio-regular').addEventListener('change', ()=>{
  if (this.checked) pointMode='regular';
});
document.getElementById('radio-steiner').addEventListener('change', ()=>{
  if (this.checked) pointMode='steiner';
});
document.getElementById('checkbox-show-angles').addEventListener('change', e=>{
  showAngles = e.target.checked;
});

/* ------------------ p5.js Sketch ------------------ */
const sketch = (p)=>{
  let canvas;
  p.setup = ()=>{
    canvas = p.createCanvas(600,600);
    canvas.parent('canvas-wrapper');
    p.pixelDensity(1);
    initRegularPoints();
    initSteinerPoints();
    computeMST();
  };
  p.draw = ()=>{
    p.background('#EEEEEE');
    // draw complete graph
    const allPts = [...regularPoints, ...steinerPoints];
    p.stroke('rgba(190,160,130,0.5)');
    p.strokeWeight(1);
    for (let i=0;i<allPts.length;i++){
      for (let j=i+1;j<allPts.length;j++){
        const a=allPts[i], b=allPts[j];
        p.line(a.x,a.y,b.x,b.y);
      }
    }
    // draw MST
    p.stroke('#333333'); p.strokeWeight(3);
    for (const e of mstEdges){
      const a=allPts[e.i], b=allPts[e.j];
      p.line(a.x,a.y,b.x,b.y);
    }
    // draw regular points
    p.stroke('#0000FF'); p.strokeWeight(2); p.fill('#FFFFFF');
    regularPoints.forEach(pnt=>{p.ellipse(pnt.x,pnt.y,16,16);});
    // draw steiner points
    p.stroke('#FF0000'); p.strokeWeight(2); p.fill('#FFFFFF');
    steinerPoints.forEach(pnt=>{
      p.ellipse(pnt.x,pnt.y,16,16);
      p.ellipse(pnt.x,pnt.y,8,8);
    });
    // draw angles if requested
    if (showAngles){
      const angleInfo = computeAngles(allPts, mstEdges);
      angleInfo.forEach(info=>{
        const targetPt = allPts[info.idx];
        const angDeg = Math.round(info.angleDeg);
        const x = targetPt.x + info.bisectRad*Math.cos(info.bisectRadAng);
        const y = targetPt.y + info.bisectRad*Math.sin(info.bisectRadAng);
        p.fill('#000000'); p.textSize(14); p.noStroke(); p.text(`${angDeg}°`, x, y);
      });
    }
  };
  // Dragging
  p.mousePressed = ()=>{
    const mx = p.mouseX, my = p.mouseY;
    // check regular points if mode allows
    if (pointMode==='regular'){
      for (let i=0;i<regularPoints.length;i++){
        if (dist({x:mx,y:my}, regularPoints[i])<10){
          dragging=true; dragIndex=i; dragType='regular'; return;
        }
      }
    } else if (pointMode==='steiner'){
      for (let i=0;i<steinerPoints.length;i++){
        if (dist({x:mx,y:my}, steinerPoints[i])<10){
          dragging=true; dragIndex=i; dragType='steiner'; return;
        }
      }
    }
  };
  p.mouseDragged = ()=>{
    if (!dragging) return;
    const mdx = p.mouseX, mdy = p.mouseY;
    if (dragType==='regular'){
      regularPoints[dragIndex]={x:mdx,y:mdy};
    } else {
      steinerPoints[dragIndex]={x:mdx,y:mdy};
    }
    computeMST();
  };
  p.mouseReleased = ()=>{
    dragging=false; dragIndex=null; dragType='';
  };
};

const myp5 = new p5(sketch);

/* ------------------ Angle Computation ------------------ */
function computeAngles(allPts, edges){
  const res=[];
  const basedOnSteiner = [];
  Build neighbor list for each point in MST
  const neighbors = Array(allPts.length).fill(null).map(()=>[]);
  edges.forEach(e=>{
    neighbors[e.i].push(e.j);
    neighbors[e.j].push(e.i);
  });
  // Process only Steiner points indices
  for (let si=0; si<steinerPoints.length; si++){
    const idx = numRegularPoints + si;
    const neigh = neighbors[idx];
    if (neigh.length<2) continue;
    // compute angles
    const vecs=[];
    neigh.forEach(j=>{
      const p=allPts[j], q=allPts[idx];
      vecs.push({ang: Math.atan2(p.y-q.y, p.x-q.x), idx:j});
    });
    vecs.sort((a,b)=>a.ang-b.ang);
    // compute angle differences
    for (let k=0;k<vecs.length;k++){
      const a1=vecs[k].ang; const a2=vecs[(k+1)%vecs.length].ang;
      let diff = a2-a1;
      if (diff<0) diff+=2*Math.PI;
      const angleDeg = diff*180/Math.PI;
      // bisector angle
      const bisect = a1 + diff/2;
      // offset for display
      const dispRad=15;
      res.push({idx:idx, angleDeg:angleDeg, bisectRad:dispRad, bisectRadAng:bisect});
    }
  }
  return res;
}
</script>
</body>
</html>
