<script>
const eps = 1e-12;

// Helper: format numbers to 3 decimal places
function fmt(v){ return v.toFixed(3); }

// Generate evenly spaced array
function linspace(a,b,n){const arr=[];const step=(b-a)/(n-1);for(let i=0;i<n;i++)arr.push(a+step*i);return arr;}

function gevPdf(x, mu, sigma, xi){
  const out=[];
  for(let i=0;i<x.length;i++){
    const xi0=Math.abs(xi)<eps?0:xi;
    const xm=x[i]-mu;
    if(Math.abs(xi)<eps){
      // Gumbel
      const z=Math.exp(-(xm/sigma));
      out.push((1/sigma)*Math.exp(-xm/sigma)*z);
    }else{
      const t = 1 + xi0*(xm/sigma);
      if(t<=0){out.push(0);}else{
        const tt = Math.pow(t, -1/xi0);
        out.push((1/sigma)*Math.pow(tt, xi0+1)*Math.exp(-tt));
      }
    }
  }
  return out;
}

function gevReturnLevel(p, mu, sigma, xi){
  // p: array of return periods (>=2)
  const out=[];
  for(let i=0;i<p.length;i++){
    const pi=p[i];
    if(Math.abs(xi)<eps){
      out.push(mu - sigma*Math.log(-Math.log(1-1/pi)));
    }else{
      out.push(mu - (sigma/xi)*(1 - Math.pow(-Math.log(1-1/pi), -xi)));
    }
  }
  return out;
}

// Update readouts
function updateReadouts(){
  const sliders = [
    {id:'slider-mu1', disp:'display-mu1'},
    {id:'slider-sigma1', disp:'display-sigma1'},
    {id:'slider-xi1', disp:'display-xi1'},
    {id:'slider-mu2', disp:'display-mu2'},
    {id:'slider-sigma2', disp:'display-sigma2'},
    {id:'slider-xi2', disp:'display-xi2'},
    {id:'slider-pmax', disp:'display-pmax'}
  ];
  sliders.forEach(el=>{
    const val = parseFloat(document.getElementById(el.id).value);
    document.getElementById(el.disp).value = fmt(val);
  });
}

// Get current parameter values
function getParams(){
  return {
    mu1:parseFloat(document.getElementById('slider-mu1').value),
    sigma1:parseFloat(document.getElementById('slider-sigma1').value),
    xi1:parseFloat(document.getElementById('slider-xi1').value),
    mu2:parseFloat(document.getElementById('slider-mu2').value),
    sigma2:parseFloat(document.getElementById('slider-sigma2').value),
    xi2:parseFloat(document.getElementById('slider-xi2').value),
    pmax:parseInt(document.getElementById('slider-pmax').value)
  };
}

// Update PDF plot
function updatePdfPlot(){
  const {mu1,sigma1,xi1,mu2,sigma2,xi2}=getParams();
  const x=linspace(2.5,7,300);
  const y1=gevPdf(x,mu1,sigma1,xi1);
  const y2=gevPdf(x,mu2,sigma2,xi2);
  const data=[
    {x:x, y:y1, mode:'lines', name:'GEV 1', line:{color:'blue'},
     fill:'tozeroy', fillcolor:'rgba(0,0,255,0.2)'},
    {x:x, y:y2, mode:'lines', name:'GEV 2', line:{color:'red'},
     fill:'tozeroy', fillcolor:'rgba(255,0,0,0.2)'}
  ];
  const layout={
    title:'PDFs of GEV Distributions 1 and 2',
    xaxis:{range:[2.5,7], showticklabels:false},
    yaxis:{showticklabels:false},
    margin:{l:40,r:40,t:40,b:40}
  };
  Plotly.react('plot-pdf',data,layout,{responsive:true});
}

// Update Return Level plot
function updateReturnPlot(){
  const {mu1,sigma1,xi1,mu2,sigma2,xi2,pmax}=getParams();
  const p=[];
  for(let i=2;i<=pmax;i++)p.push(i);
  const y1=gevReturnLevel(p,mu1,sigma1,xi1);
  const y2=gevReturnLevel(p,mu2,sigma2,xi2);
  const data=[
    {x:p, y:y1, mode:'lines', name:'GEV 1', line:{color:'blue'}},
    {x:p, y:y2, mode:'lines', name:'GEV 2', line:{color:'red'}}
  ];
  const layout={
    xaxis:{title:'Return Period (years)', range:[0,pmax]},
    yaxis:{title:'Maximum Sea Level (meters)'},
    margin:{l:50,r:40,t:30,b:50},
    legend:{x:0.05, y:0.95}
  };
  Plotly.react('plot-return-level',data,layout,{responsive:true});
}

// Master update function
function update(){  
  updateReadouts();
  updatePdfPlot();
  updateReturnPlot();
}

// Attach event listeners
document.querySelectorAll('#control-panel input[type=range]').forEach(inp=>{
  inp.addEventListener('input',update);
});

// Initial rendering
update();
</script>
</body>
</html>
