    [[1,0],[1,4],[1.5,4],[1.5,1],[2.5,1],[2.5,4],[3,4],[3,0]],
    // Part 2
    [[4,0],[4,4],[4.5,4],[4.5,2],[5.5,2],[5.5,4],[6,4],[6,0]]
  ]
};

// Interpolate linearly between points
function evalPiecewise(t, pts) {
  // For UH, handle as dual-trace
  if (Array.isArray(pts[0])) {
    // first part
    const val1 = evalPiecewise(t, pts[0]) ?? 0;
    const val2 = evalPiecewise(t, pts[1]) ?? 0;
    return Math.max(val1, val2);
  }

  // Find segment
  for (let i=0; i<pts.length-1; i++) {
    const x1=pts[i][0], y1=pts[i][1];
    const x2=pts[i+1][0], y2=pts[i+1][1];
    if (t>=x1 && t<=x2) {
      const ratio = (t - x1)/(x2 - x1);
      return y1 + ratio*(y2 - y1);
    }
  }
  return 0; // outside support
}

// Generate sample arrays
function generateSamples(xFunc) {
  const tArr = [];
  const yArr = [];
  const step = (state.tRange.max - state.tRange.min)/(state.tRange.points-1);
  for (let i=0;i<state.tRange.points;i++){
    const t = state.tRange.min + i*step;
    tArr.push(t);
    yArr.push( xFunc(t) );
  }
  return {t:tArr, y:yArr};
}

// Functions for transformed signals
function getSignals() {
  const basePts = SIGNALS[state.signal];
  const baseFun = (t)=>evalPiecewise(t, basePts);
  const tArr = [], xArr = [], yArr = [], zArr = [], wArr = [];
  const step = (state.tRange.max - state.tRange.min)/(state.tRange.points-1);
  for (let i=0;i<state.tRange.points;i++){
    const t = state.tRange.min + i*step;
    tArr.push(t);
    xArr.push( baseFun(t) );
    yArr.push( baseFun(state.a*t - state.b) );
    zArr.push( baseFun(t - state.b) );
    wArr.push( baseFun(state.a*t) );
  }
  return {t:tArr, x:xArr, y:yArr, z:zArr, w:wArr};
}

// ======== PLOT UPDATE ========
function createPlotLayout(title) {
  const layout = {
    title: title,
    xaxis: {title: 'time (s)', range: [-10,20], zeroline:true},
    yaxis: {range: [-0.5,4.5], zeroline:true},
    showlegend:true,
    legend: {x:0.01, y:0.75},
    shapes: [
      {
        type: 'circle',
        xref: 'x',
        yref: 'y',
        x0: state.T,
        y0: 0,
        x1: state.T,
        y1: 0,
        xanchor: 'center',
        yanchor: 'middle',
        width: 0,
        height: 0,
        marker:{color:'red', size:12}
      },
      {
        type: 'circle',
        xref: 'x',
        yref: 'y',
        x0: state.b,
        y0: 0,
        x1: state.b,
        y1: 0,
        xanchor: 'center',
        yanchor: 'middle',
        width: 0,
        height: 0,
        marker:{color:'green', size:12}
      }
    ],
    annotations: [
      {x: state.T, y:0, text:'T', showarrow:false, yshift:15, font:{color:'red'}},
      {x: state.b, y:0, text:'b', showarrow:false, yshift:15, font:{color:'green'}}
    ]
  };
  layout.legend.y = title ? 0.75 : 0.98;
  return layout;
}

function updatePlots() {
  const sigs = getSignals();

  // Top plot
  const topTrace = {
    x: sigs.t,
    y: sigs.x,
    mode: 'lines',
    line: {color:'rgb(70,130,180)', width:2.5},
    name: '\\(x(t)\\)'
  };
  Plotly.react('plot-top', [topTrace], createPlotLayout('signal'), {displaylogo:false, responsive:true});

  // Bottom plot
  const bottomTraces = [
    {
      x:sigs.t, y:sigs.x, mode:'lines',
      line:{color:'rgb(70,130,180)', width:2.5},
      name:'\\(x(t)\\)'
    },
    {
      x:sigs.t, y:sigs.y, mode:'lines',
      line:{color:'red', width:2.5},
      name:'\\(y(t) = x(at - b) = x(a(t-T))\\)'
    },
    {
      x:sigs.t, y:sigs.z, mode:'lines',
      line:{color:'olivedrab', width:2.5},
      name:'\\(z(t) = x(t - b)\\)'
    },
    {
      x:sigs.t, y:sigs.w, mode:'lines',
      line:{color:'saddlebrown', width:2.5},
      name:'\\(w(t) = x(at)\\)'
    }
  ];

  // Respect checkbox visibility
  ['check-x','check-y','check-z','check-w'].forEach((chkId, idx)=>{
    const chk = document.getElementById(chkId).checked;
    bottomTraces[idx].visible = chk;
  });

  Plotly.react('plot-bottom', bottomTraces, createPlotLayout(''), {displaylogo:false, responsive:true});
}

// ======== EVENT LISTENERS ========

// Signal buttons
['btn-rampdown','btn-tophat','btn-stairstep','btn-triangle','btn-uh'].forEach(id=>{
  const btn=document.getElementById(id);
  btn.addEventListener('click',()=>{
    state.signal = id.split('-')[1];
    // Remove active
    document.querySelectorAll('#control-panel button').forEach(b=>b.classList.remove('active'));
    btn.classList.add('active');
    updatePlots();
  });
});

// Sliders
function setSlider(id, value){
  document.getElementById(id).value = value;
  document.getElementById(id+"-value").textContent = value.toFixed(1);
}
function updateTfromAB(){
  const a = parseFloat(document.getElementById('slider-a').value);
  const b = parseFloat(document.getElementById('slider-b').value);
  const T = b/a;
  state.a=a; state.b=b; state.T=T;
  setSlider('slider-a', a);
  setSlider('slider-b', b);
  setSlider('slider-t', T);
}
function updateBFromAT(){
  const a = parseFloat(document.getElementById('slider-a').value);
  const T = parseFloat(document.getElementById('slider-t').value);
  const b = a*T;
  state.a=a; state.T=T; state.b=b;
  setSlider('slider-a', a);
  setSlider('slider-b', b);
  setSlider('slider-t', T);
}

['slider-a','slider-b','slider-t'].forEach(id=>{
  const el=document.getElementById(id);
  el.addEventListener('input',()=>{
    if(id==='slider-a' || id==='slider-b'){
      updateTfromAB();
    }else if(id==='slider-t'){
      updateBFromAT();
    }
    updatePlots();
  });
});

// Checkboxes
['check-x','check-y','check-z','check-w'].forEach(id=>{
  document.getElementById(id).addEventListener('change',updatePlots);
});

// ======== INITIAL RENDER ========
window.addEventListener('load',()=>{
  // Set initial slider values
  setSlider('slider-a',2.0);
  setSlider('slider-b',4.0);
  setSlider('slider-t',2.0);
  // Checkboxes
  document.getElementById('check-x').checked=true;
  document.getElementById('check-y').checked=true;
  document.getElementById('check-z').checked=true;
  document.getElementById('check-w').checked=false;
  // Default active button
  document.getElementById('btn-triangle').classList.add('active');
  // Initial plots
  updatePlots();
  // Render MathJax
  MathJax.typesetPromise();
});
</script>
</body>
</html>
