<body>
<div id="main-container">
    <div id="control-panel">
        <div>
            <label for="radio-x-0">units in x direction</label>
            <input type="radio" name="unitsX" id="radio-x-0" value="0"> 0
            <input type="radio" name="unitsX" id="radio-x-1" value="1"> 1
            <input type="radio" name="unitsX" id="radio-x-2" value="2" checked> 2
            <input type="radio" name="unitsX" id="radio-x-3" value="3"> 3
            <input type="radio" name="unitsX" id="radio-x-4" value="4"> 4
        </div>
        <div>
            <label for="radio-y-0">units in y direction</label>
            <input type="radio" name="unitsY" id="radio-y-0" value="0"> 0
            <input type="radio" name="unitsY" id="radio-y-1" value="1"> 1
            <input type="radio" name="unitsY" id="radio-y-2" value="2" checked> 2
            <input type="radio" name="unitsY" id="radio-y-3" value="3"> 3
            <input type="radio" name="unitsY" id="radio-y-4" value="4"> 4
        </div>
        <div>
            <label for="slider-trim">trim</label>
            <input type="range" id="slider-trim" min="0.65" max="0.9" step="0.001" value="0.8">
            <span id="label-trim-value">0.800</span>
        </div>
        <div>
            <input type="checkbox" id="checkbox-double"> <label for="checkbox-double">double</label>
            <input type="checkbox" id="checkbox-sphere"> <label for="checkbox-sphere">sphere</label>
            <input type="checkbox" id="checkbox-triangles"> <label for="checkbox-triangles">triangles</label>
        </div>
    </div>
    <div id="canvas-container">
        <canvas id="main-canvas"></canvas>
        <button id="btn-reset-view">+</button>
    </div>
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
<script>
    const canvas = document.getElementById('main-canvas');
    const renderer = new THREE.WebGLRenderer({canvas, antialias:true});
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    const camera = new THREE.PerspectiveCamera(45, canvas.clientWidth/canvas.clientHeight, 0.1, 1000);
    camera.position.set(0, 0, 15);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    const hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444, 1.5);
    scene.add(hemiLight);
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight.position.set(5, 10, 7.5);
    scene.add(dirLight);

    let groupMain = null;
    let sphereMesh = null;
    let trianglesGroup = null;

    const sliderTrim = document.getElementById('slider-trim');
    const labelTrim = document.getElementById('label-trim-value');
    const checkboxDouble = document.getElementById('checkbox-double');
    const checkboxSphere = document.getElementById('checkbox-sphere');
    const checkboxTriangles = document.getElementById('checkbox-triangles');
    const resetBtn = document.getElementById('btn-reset-view');

    // Helper functions
    function hexToRGB(hex){
        const r = ((hex >> 16) & 255) / 255;
        const g = ((hex >> 8) & 255) / 255;
        const b = (hex & 255) / 255;
        return [r,g,b];
    }

    function slerpVectors(v1, v2, t){
        let dot = v1.dot(v2);
        dot = Math.max(-1, Math.min(1, dot));
        if (dot > 0.9995){
            return new THREE.Vector3().copy(v1).lerp(v2, t);
        }
        const theta = Math.acos(dot);
        const sinTheta = Math.sin(theta);
        const a = Math.sin((1 - t) * theta) / sinTheta;
        const b = Math.sin(t * theta) / sinTheta;
        return new THREE.Vector3(
            a * v1.x + b * v2.x,
            a * v1.y + b * v2.y,
            a * v1.z + b * v2.z
        );
    }

    function generateArc(vStart, vEnd, segments){
        const arc = [];
        for(let i=0;i<=segments;i++){
            const t = i/segments;
            arc.push(slerpVectors(vStart, vEnd, t));
        }
        return arc;
    }

    function createCurvedTriangle(pos, rotation, double, trim){
        const radius = 1;
        const centerColor = double ? 0x80ffff : 0xffc0cb;
        const edgeColor = double ? 0x008080 : 0x904090;
        const segRange = Math.round(4 + ((0.9 - trim)/0.25)*8);
        const segments = Math.max(4, segRange);
        // define vertices on unit circle
        const v0 = new THREE.Vector3(Math.cos(0)*radius, Math.sin(0)*radius, 0);
        const v1 = new THREE.Vector3(Math.cos(2*Math.PI/3)*radius, Math.sin(2*Math.PI/3)*radius, 0);
        const v2 = new THREE.Vector3(Math.cos(4*Math.PI/3)*radius, Math.sin(4*Math.PI/3)*radius, 0);
        // perimeter arcs
        const side0 = generateArc(v0, v1, segments);
        const side1 = generateArc(v1, v2, segments);
        const side2 = generateArc(v2, v0, segments);
        const perimeter = side0.concat(side1.slice(1), side2.slice(1));
        const centerPoint = new THREE.Vector3().add(v0).add(v1).add(v2).divideScalar(3);
        // local vertices relative to center
        const localVerts = perimeter.map(v => new THREE.Vector3().subVectors(v, centerPoint));

        const positions = [];
        const colors = [];
        // center vertex
        positions.push(centerPoint.x, centerPoint.y, centerPoint.z);
        colors.push(...hexToRGB(centerColor));
        // perimeter vertices
        localVerts.forEach(v => {
            positions.push(v.x, v.y, v.z);
            colors.push(...hexToRGB(edgeColor));
        });
        // indices
        const n = localVerts.length;
        const indices = [];
        for(let i=0;i<n;i++){
            const a = 0;
            const b = i+1;
            const c = ((i+1)%n)+1;
            indices.push(a,b,c);
        }
        // geometry
        const geom = new THREE.BufferGeometry();
        geom.setAttribute('position', new THREE.Float32BufferAttribute(positions,3));
        geom.setAttribute('color', new THREE.Float32BufferAttribute(colors,3));
        geom.setIndex(indices);
        geom.computeVertexNormals();
        const material = new THREE.MeshStandardMaterial({vertexColors:true, side:THREE.DoubleSide});
        const mesh = new THREE.Mesh(geom, material);
        // outline
        const lineGeom = new THREE.BufferGeometry();
        lineGeom.setAttribute('position', new THREE.Float32BufferAttribute(positions.slice(3),3));
        const lineIndices = [];
        for(let i=0;i<n;i++){
            lineIndices.push(i, (i+1)%n);
        }
        lineGeom.setIndex(lineIndices);
        const lineMaterial = new THREE.LineBasicMaterial({color:0x000000, linewidth:2});
        const line = new THREE.LineSegments(lineGeom, lineMaterial);
        const group = new THREE.Group();
        group.add(mesh);
        group.add(line);
        group.position.copy(pos);
        if(rotation) group.rotation.z = rotation;
        return group;
    }

    function createFlatTriangle(pos, rotation){
        const radius = 1;
        const v0 = new THREE.Vector3(Math.cos(0)*radius, Math.sin(0)*radius, 0);
        const v1 = new THREE.Vector3(Math.cos(2*Math.PI/3)*radius, Math.sin(2*Math.PI/3)*radius, 0);
        const v2 = new THREE.Vector3(Math.cos(4*Math.PI/3)*radius, Math.sin(4*Math.PI/3)*radius, 0);
        const geom = new THREE.BufferGeometry().setFromPoints([v0,v1,v2]);
        const mesh = new THREE.Mesh(geom, new THREE.MeshBasicMaterial({color:0x800080, transparent:true, opacity:0.4, side:THREE.DoubleSide}));
        const pointsGeom = new THREE.BufferGeometry().setFromPoints([v0,v1,v2]);
        const points = new THREE.Points(pointsGeom, new THREE.PointsMaterial({color:0x000000, size:3}));
        const group = new THREE.Group();
        group.add(mesh);
        group.add(points);
        group.position.copy(pos);
        if(rotation) group.rotation.z = rotation;
        return group;
    }

    function updateScene(){
        // Remove existing groupMain
        if(groupMain){
            scene.remove(groupMain);
        }
        groupMain = new THREE.Group();

        const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
        const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
        const trim = parseFloat(sliderTrim.value);
        const doubleFlag = checkboxDouble.checked;
        const sphereFlag = checkboxSphere.checked;
        const trianglesFlag = checkboxTriangles.checked;

        // Lattice
        const spacing = 3; // spacing between centers
        for(let i=0;i<unitsX;i++){
            for(let j=0;j<unitsY;j++){
                const posX = (i - unitsX/2 + 0.5)*spacing;
                const posY = (j - unitsY/2 + 0.5)*spacing;
                const pos = new THREE.Vector3(posX, posY, 0);
                const rot = ((i + j) % 2 === 1) ? Math.PI : 0;
                const primary = createCurvedTriangle(pos, rot, false, trim);
                groupMain.add(primary);
                if(doubleFlag){
                    const offPos = new THREE.Vector3(posX + spacing/2, posY + spacing/2, 0);
                    const double = createCurvedTriangle(offPos, rot, true, trim);
                    groupMain.add(double);
                }
                if(trianglesFlag){
                    const flatGrp = createFlatTriangle(pos, rot);
                    if(trianglesGroup) trianglesGroup.add(flatGrp);
                }
            }
        }

        scene.add(groupMain);

        // Sphere
        if(sphereFlag){
            if(!sphereMesh){
                const geom = new THREE.SphereGeometry(0.5,32,32);
                const mat = new THREE.MeshPhongMaterial({color:0xdddddd, shininess:80});
                sphereMesh = new THREE.Mesh(geom, mat);
                sphereMesh.position.set(0,0,0);
                scene.add(sphereMesh);
            }
        } else {
            if(sphereMesh){ scene.remove(sphereMesh); sphereMesh = null;}
        }

        // Triangles
        if(trianglesFlag){
            if(!trianglesGroup){
                trianglesGroup = new THREE.Group();
            }
            scene.add(trianglesGroup);
        } else {
            if(trianglesGroup){ scene.remove(trianglesGroup); trianglesGroup = null;}
        }
    }

    // Event listeners
    document.querySelectorAll('input[name="unitsX"]').forEach(r=>r.addEventListener('change', updateScene));
    document.querySelectorAll('input[name="unitsY"]').forEach(r=>r.addEventListener('change', updateScene));
    sliderTrim.addEventListener('input', ()=>{
        labelTrim.textContent = parseFloat(sliderTrim.value).toFixed(3);
        updateScene();
    });
    checkboxDouble.addEventListener('change', updateScene);
    checkboxSphere.addEventListener('change', updateScene);
    checkboxTriangles.addEventListener('change', updateScene);
    resetBtn.addEventListener('click', ()=>{
        controls.reset();
    });

    // Initialize
    updateScene();

    function onResize(){
        const width = window.innerWidth;
        const height = window.innerHeight - document.getElementById('control-panel').offsetHeight;
        camera.aspect = width / height;
        camera.updateProjectionMatrix();
        renderer.setSize(width, height);
    }

    window.addEventListener('resize', onResize);
    onResize();

    function animate(){
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
    }
    animate();
</script>
</body>
</html>
