const functions = {
  f: (t)=>0.5*t,
  g: (t)=>Math.sin(2*Math.PI*t)+0.3*Math.cos(3*2*Math.PI*t),
  sin: (t)=>Math.sin(2*Math.PI*t),
  cos: (t)=>Math.cos(2*Math.PI*t),
  tan: (t)=>Math.tan(Math.PI*t)
};

document.getElementById('info-button').onclick = () => {
