const sketch = s => {
  let cnv;
  s.setup = () => {
    cnv = s.createCanvas(950, 500);
    cnv.parent('canvas-container');
    cnv.classes('p5Canvas');
    s.noLoop();
  };
  s.redrawCB = () => { s.redraw(); };

  const margin = 60;
  const scaleX = (t)=> s.map(t, startTime, startTime+totalTime, margin, s.width-margin);
  const scaleY = (y)=> s.map(y, -1.2, 1.2, s.height-margin, margin);

  const drawAxes = () => {
    s.stroke(0);
    // x-axis
    s.line(scaleX(startTime), scaleY(0), scaleX(startTime+totalTime), scaleY(0));
    // y-axis
    s.line(scaleX(0), scaleY(-1.2), scaleX(0), scaleY(1.2));
    // ticks
    s.textSize(12);
    for (let t=startTime; t<=startTime+totalTime; t+=0.5) {
      const x=scaleX(t);
      s.line(x, scaleY(0)-5, x, scaleY(0)+5);
      s.text(t.toFixed(1), x-10, scaleY(0)+20);
    }
    for (let y=-1.2; y<=1.2; y+=0.5) {
      const yPix = scaleY(y);
      s.line(scaleX(0)-5, yPix, scaleX(0)+5, yPix);
      if (y!==0) s.text(y.toFixed(1), scaleX(0)+10, yPix+5);
    }
  };

  const getAnalog = (t)=> functions[funcName](t);

  const simulateASDM = () => {
    const B = 0.25;
    const delta = 0.05;
    let t = startTime;
    const dt = 0.001;
    let output = B;
    let integral = 0;
    const flips=[];
    while (t <= startTime+totalTime) {
      const f = getAnalog(t);
      integral += (f - output)*dt;
      if ( (output===B && integral < -delta) || (output===-B && integral > delta) ) {
        flips.push(t);
        output = -output;
        integral = 0;
      }
      t += dt;
    }
    return flips;
  };

  const simulateIAF = () => {
    const theta = 0.05;
    const y_spike = 0.3;
    let t = startTime;
    const dt = 0.001;
    let integral = 0;
    const spikes=[];
    while (t <= startTime+totalTime) {
      integral += getAnalog(t)*dt;
      if (integral >= theta) {
        spikes.push(t);
        integral = 0;
      }
      t += dt;
    }
    return spikes;
  };

  s.draw = () => {
    s.clear();
    s.background(255);
    drawAxes();
    // Analog signal
    s.stroke(0,0,255);
    s.strokeWeight(2);
    s.noFill();
    s.beginShape();
    for(let t=startTime; t<=startTime+totalTime; t+=0.005){
      const y=getAnalog(t);
      const x=scaleX(t);
      const yPix=scaleY(y);
      s.vertex(x,yPix);
    }
    s.endShape();

    // Encoded signal
    s.stroke(255,0,0);
    s.strokeWeight(2);
    s.noFill();
    if (encMethod==='ASDM') {
      const flips = simulateASDM();
      if(flips.length>0){
        let prevT=startTime;
        let prevY=0;
        for(let i=0;i<flips.length;i++){
          const curT=flips[i];
          // horizontal segment to flip
          s.line(scaleX(prevT), scaleY(prevY), scaleX(curT), scaleY(prevY));
          // vertical flip
          const newY = prevY===0? B : -B;
          s.line(scaleX(curT), scaleY(prevY), scaleX(curT), scaleY(newY));
          prevT=curT;
          prevY=newY;
        }
        // draw final horizontal segment to end
        const lastT=startTime+totalTime;
        s.line(scaleX(prevT), scaleY(prevY), scaleX(lastT), scaleY(prevY));
      }
    } else if (encMethod==='IAF') {
      const spikes = simulateIAF();
      const y_spike=0.3;
      spikes.forEach(t=>{
        const x=scaleX(t);
        // vertical line
        s.line(x, scaleY(0), x, scaleY(y_spike));
        // dot
        s.fill(255,0,0); s.noStroke(); s.ellipse(x, scaleY(y_spike), 6, 6);
      });
    }
  };
};
