  // Two cameras
  const orthoSize = 10;
  const orthoCam = new THREE.OrthographicCamera(
    orthoSize, -orthoSize,
    orthoSize, -orthoSize,
    0.1, 1000
  );
  orthoCam.position.set(0, 0, 1);
  orthoCam.lookAt(0, 0, 0);

  const perspCam = new THREE.PerspectiveCamera(
    45, 1, 0.1, 1000
  );
  perspCam.position.set(10, 10, 10);
  perspCam.lookAt(0, 0, 0);

  let camera = perspCam; // Default active camera

  // ---------- Geometry Creation ----------
  let plotPoints = 40;
  const size = 10; // 10x10 world units
  let planeGeo, mesh, wireframe;
  function createGrid() {
    if (mesh) { scene.remove(mesh); }
    if (wireframe) { scene.remove(wireframe); }
    planeGeo = new THREE.PlaneBufferGeometry(
      size, size, plotPoints, plotPoints
    );
    planeGeo.rotateX(-Math.PI / 2); // make plane XY

    mesh = new THREE.Mesh(
      planeGeo,
      new THREE.MeshLambertMaterial({ vertexColors: true, side: THREE.DoubleSide })
    );
    scene.add(mesh);

    const wfGeo = new THREE.WireframeGeometry(planeGeo);
    wireframe = new THREE.LineSegments(
      wfGeo,
      new THREE.LineBasicMaterial({ color: 0x556b2f })
    );
    scene.add(wireframe);
  }

  createGrid();

  // ---------- State Variables ----------
  let time = 0;
  let isPlaying = false;
  const timeStep = 0.05;

  // Wave parameters
  let pWaveType = 'sine'; // 'sine' or 'pulse'
  let sWaveType = 'sine';
  let sWaveDir = 'xy'; // 'xy' or 'z'

  let pAmp = 0.1, pWl = 2, sAmp = 0.1, sWl = 2.5;

  // ---------- Utility Functions ----------
  function degToRad(deg) { return deg * Math.PI / 180; }

  function waveFunc(type, u) {
    if (type === 'sine') return Math.sin(2 * Math.PI * u);
    else return Math.exp(-Math.pow(u, 2));
  }

  function getColor(z, maxAbsZ) {
    const ratio = Math.abs(z) / maxAbsZ;
    if (z >= 0) { // red gradient
      return new THREE.Color(1, 1 - ratio, 1 - ratio);
    } else { // blue gradient
      return new THREE.Color(1 - ratio, 1 - ratio, 1);
    }
  }

  // ---------- Animation Loop ----------
  function animate() {
    requestAnimationFrame(animate);
    if (isPlaying) {
      time += timeStep;
      if (time > 20) time = 0;
      document.getElementById('time-slider').value = time.toFixed(2);
    }
    updateGeometry();
    renderer.render(scene, camera);
  }

  function updateGeometry() {
    const posAttr = planeGeo.attributes.position;
    const colorAttr = planeGeo.attributes.color;
    const nVertices = posAttr.count;
    if (!colorAttr || colorAttr.count !== nVertices) {
      // Add color attribute if not present
      const colors = new Float32Array(nVertices * 3);
      planeGeo.setAttribute('color', new THREE.BufferAttribute(colors, 3));
    }

    const colors = planeGeo.attributes.color.array;
    let maxZ = -Infinity, minZ = Infinity;
    for (let i = 0; i < nVertices; i++) {
      const x = posAttr.getX(i);
      const y = posAttr.getY(i);
      const r = Math.sqrt(x * x + y * y) + 1e-6; // Avoid division by zero

      // P-wave
      const pMag = pAmp * waveFunc(pWaveType, (r / pWl) - time);
      const pVec = { x: pMag * (x / r), y: pMag * (y / r), z: 0 };

      // S-wave
      const sMag = sAmp * waveFunc(sWaveType, (r / sWl) - time);
      let sVec;
      if (sWaveDir === 'z') sVec = { x: 0, y: 0, z: sMag };
      else sVec = { x: -sMag * (y / r), y: sMag * (x / r), z: 0 };

      const newZ = pVec.z + sVec.z;
      posAttr.setZ(i, newZ);

      maxZ = Math.max(maxZ, newZ);
      minZ = Math.min(minZ, newZ);
    }
    posAttr.needsUpdate = true;

    const maxAbsZ = Math.max(Math.abs(maxZ), Math.abs(minZ));
    for (let i = 0; i < nVertices; i++) {
      const z = posAttr.getZ(i);
      const col = getColor(z, maxAbsZ);
      colors[3 * i] = col.r;
      colors[3 * i + 1] = col.g;
      colors[3 * i + 2] = col.b;
    }
    planeGeo.attributes.color.needsUpdate = true;
  }

