<script>
// ---- State Variables ----
let a = 1, b = -1, c = -1;          // coefficients
let z = 1.5;
let plotRange = 4;
let showLabels = false, showCircle = false, showAxes = false, showGridLines = false;

// Points
let O = {x:0, y:0};
let L1, L2, L3, L1p, L2p;
let circleCenter = {x:0, y:0}, circleRadius=0;

// P5 sketch
let sketch, myCanvas;
const createSketch = () => {
  sketch = new p5(p => {
    p.setup = function() {
      myCanvas = p.createCanvas(600, 600);
      myCanvas.parent('canvas-container');
      p.noLoop();
      p.textSize(12);
      p.textAlign(p.LEFT, p.BASELINE);
    };
    p.draw = function() {
      p.clear();
      // Apply transforms
      const scaleFactor = (Math.min(p.width, p.height)/2) / plotRange;
      p.push();
      p.translate(p.width/2, p.height/2);
      p.scale(scaleFactor);
      p.scale(1, -1);
      // Center world coordinates
      const center = computeCenter();
      p.translate(-center.x, -center.y);

      if(showGridLines) drawGrid(p, plotRange);
      if(showAxes) drawAxes(p, plotRange);
      drawCoefficientPath(p);
      drawTestPath(p);
      if(showCircle) drawCircle(p);
      if(showLabels) drawLabels(p);
      p.pop();
    };
  }, 'canvas-container');
};

const recomputePoints = () => {
  L1 = {x: a,     y: 0};
  L2 = {x: a,     y: b};
  L3 = {x: a - c, y: b};
  // Test path
  L1p = {x: a,      y: -z*a};
  L2p = {x: a + b*z + a*z*z, y: b};
  // Circle
  circleCenter = {x: (a - c)/2, y: b/2};
  circleRadius = Math.sqrt((a - c)*(a - c) + b*b)/2;
};

const computePz = (zVal) => a*zVal*zVal + b*zVal + c;
const distance = (p1, p2) => Math.hypot(p1.x - p2.x, p1.y - p2.y);
const computeError = () => distance(L3, L2p);

const computeCenter = () => {
  const points = [O, L1, L2, L3, L1p, L2p, {x: circleCenter.x + circleRadius, y: circleCenter.y}, {x: circleCenter.x - circleRadius, y: circleCenter.y}, {x: circleCenter.x, y: circleCenter.y + circleRadius}, {x: circleCenter.x, y: circleCenter.y - circleRadius}];
  const xs = points.map(p=>p.x), ys = points.map(p=>p.y);
  const minX = Math.min(...xs), maxX = Math.max(...xs);
  const minY = Math.min(...ys), maxY = Math.max(...ys);
  return {x: (minX+maxX)/2, y: (minY+maxY)/2};
};

const drawGrid = (p, range) => {
  const step = range / 5;
  p.stroke(220); p.strokeWeight(0.5);
  for(let x=-range; x<=range; x+=step) {
    p.line(x,-range,x,range);
    p.fill(0); p.noStroke();
    p.text((x).toFixed(1), x, -range-0.1);
  }
  for(let y=-range; y<=range; y+=step) {
    p.line(-range,y,range,y);
    p.fill(0); p.noStroke();
    p.text((y).toFixed(1), -range-0.2, y);
  }
};

const drawAxes = (p, range) => {
  p.stroke(0); p.strokeWeight(1.5);
  p.line(-range,0,range,0); // X
  p.line(0,-range,0,range); // Y
};

const drawCoefficientPath = p => {
  p.stroke(0); p.strokeWeight(1);
  p.line(O.x, O.y, L1.x, L1.y);
  p.line(L1.x, L1.y, L2.x, L2.y);
  p.line(L2.x, L2.y, L3.x, L3.y);
  // Points
  [O, L1, L2, L3].forEach(pt=> {p.fill(0); p.noStroke(); p.ellipse(pt.x, pt.y, 5,5);});
};

const drawTestPath = p => {
  p.stroke(0); p.strokeWeight(1);
  p.line(O.x, O.y, L1p.x, L1p.y);
  p.stroke(255,0,255); // magenta
  p.line(L1p.x, L1p.y, L2p.x, L2p.y);
  p.stroke(255,0,0); // red
  p.line(L2p.x, L2p.y, L3.x, L3.y);
  [L1p, L2p].forEach(pt=>{p.fill(0); p.noStroke(); p.ellipse(pt.x, pt.y, 4,4);});
};

const drawCircle = p => {
  p.stroke(0); p.noStroke(); p.strokeWeight(1);
  p.ellipseMode(p.RADIUS);
  p.ellipse(circleCenter.x, circleCenter.y, circleRadius, circleRadius);
};

const drawLabels = p => {
  p.fill(0); p.noStroke(); p.textSize(12); p.textStyle(p.BOLD);
  p.text('O',(O.x+0.1).toFixed(2),(O.y+0.1).toFixed(2));
  p.text('L1',(L1.x+0.1).toFixed(2),(L1.y+0.1).toFixed(2));
  p.text('L2',(L2.x+0.1).toFixed(2),(L2.y+0.1).toFixed(2));
  p.text('L3',(L3.x+0.1).toFixed(2),(L3.y+0.1).toFixed(2));
  p.text('L1\'',(L1p.x+0.1).toFixed(2),(L1p.y+0.1).toFixed(2));
  p.text('L2\'',(L2p.x+0.1).toFixed(2),(L2p.y+0.1).toFixed(2));
};

const formatPoly = (a,b,c) => {
  const coeff = val => {
    if(val===1) return '';
    if(val===-1) return '-';
    return `${val}`;
  };
  let parts = [];
  parts.push(`${coeff(a)}z^2`);
  if(b!==0){
    if(b>0) parts.push(`+ ${coeff(b)}z`);
    else parts.push(`- ${coeff(-b)}z`);
  }
  if(c!==0){
    if(c>0) parts.push(`+ ${coeff(c)}`);
    else parts.push(`- ${coeff(-c)}`);
  }
  return parts.join(' ');
};

const updateFormula = () => {
  const container = document.getElementById('formula-text');
  const polyStr = formatPoly(a,b,c);
  container.innerHTML = `\\(${polyStr}\\)`;
  if(window.MathJax && window.MathJax.typesetPromise) MathJax.typesetPromise([container]);
};

const updateTable = () => {
  document.getElementById('value-pz').textContent = computePz(z).toFixed(4);
  document.getElementById('value-l3l2').textContent = computeError().toFixed(4);
  document.getElementById('value-z').textContent = z.toFixed(4);
};

const updateLabels = () => {
  document.getElementById('label-z').textContent = z.toFixed(2);
  document.getElementById('label-plot-range').textContent = plotRange.toFixed(1);
};

const updateSketch = () => {
  if(sketch) sketch.redraw();
};

const fullUpdate = () => {
  recomputePoints();
  updateTable();
  updateFormula();
  updateSketch();
};

const randomExample = () => {
  const choices = [-3,-2,-1,1,2,3];
  a = choices[Math.floor(Math.random()*choices.length)];
  b = Math.floor(Math.random()*11)-5; // -5..5
  c = Math.floor(Math.random()*11)-5;
  fullUpdate();
};

const setupControls = () => {
  // sliders
  const sliderZ = document.getElementById('slider-z');
  sliderZ.value = z;
  sliderZ.addEventListener('input', () => { z = parseFloat(sliderZ.value); updateLabels(); fullUpdate(); });

  const sliderPlot = document.getElementById('slider-plot-range');
  sliderPlot.value = plotRange;
  sliderPlot.addEventListener('input', () => { plotRange = parseFloat(sliderPlot.value); updateLabels(); fullUpdate(); });

  // checkboxes
  document.getElementById('check-show-labels').addEventListener('change', e=>{ showLabels=e.target.checked; fullUpdate(); });
  document.getElementById('check-show-circle').addEventListener('change', e=>{ showCircle=e.target.checked; fullUpdate(); });
  document.getElementById('check-show-axes').addEventListener('change', e=>{ showAxes=e.target.checked; fullUpdate(); });
  document.getElementById('check-show-grid-lines').addEventListener('change', e=>{ showGridLines=e.target.checked; fullUpdate(); });

  // buttons
  document.getElementById('btn-new-example').addEventListener('click', () => { randomExample(); });
  document.getElementById('btn-golden-ratio').addEventListener('click', () => { a=1; b=-1; c=-1; fullUpdate(); });

  // initial labels
  updateLabels();
  updateFormula();
  updateTable();
};

window.addEventListener('load', () => {
  createSketch();
  setupControls();
});
</script>
</body>
</html>
