];
const turningPointsData = [
  {alpha:0.0105, t2:3.80},
  {alpha:0.0253, t2:5.60},
  {alpha:0.0392, t2:8.10},
  {alpha:0.0501, t2:9.60},
  {alpha:0.0605, t2:0.90},
  {alpha:0.0667, t2:1.97}
];

/* ---------- State ---------- */
let currentView = 'plot_solutions';
const sliders = document.getElementById('slider-alpha');
const alphaValueDisplay = document.getElementById('alpha-value-display');
const plots = document.getElementById('plot-view');
const tables = document.getElementById('table-view');
