/* ---------- Calculation helpers ---------- */
function interpolate(t1,t2,alpha1,alpha2){
  const t = alpha1===alpha2 ? t1 : t1 + (t2-t1)*(alpha2-alpha1)/ (alpha2-alpha1);
  return t; // just placeholder
}
function findYIntersection(alpha){
  const points = solutionCurveData;
  const intersections = [];
  for(let i=0;i<points.length-1;i++){
    const a1=points[i].alpha, a2=points[i+1].alpha;
    if((alpha>=a1 && alpha<=a2) || (alpha>=a2 && alpha<=a1)){
      const t1=points[i].t2;
      const t2=points[i+1].t2;
      const t = t1 + (t2-t1)*(alpha-a1)/(a2-a1);
      intersections.push({
        x1: points[i].x1 + (points[i+1].x1-points[i].x1)*(alpha-a1)/(a2-a1),
        x2: points[i].x2 + (points[i+1].x2-points[i].x2)*(alpha-a1)/(a2-a1),
        t1: points[i].t1 + (points[i+1].t1-points[i].t1)*(alpha-a1)/(a2-a1),
        t2: t
      });
    }
  }
  return intersections;
}
