      drawTurningPoints(p,w,h,margin);
    }
  }

  function mapX(val) {return p.map(val, 0, 0.07, margin, w-margin);}
  function mapY(val) {return p.map(val, 0, 8, h-margin, margin);} // invert y

  function drawGrid(pg,w,h,margin){
    pg.stroke(240); pg.strokeWeight(1);
    const majorTicks = 5;
    for(let i=0;i<=majorTicks;i++){
      const x = mapX(i*0.07/majorTicks);
      pg.line(x,margin,x,h-margin);
      const y = mapY(i*8/majorTicks);
      pg.line(margin,y,w-margin,y);
    }
  }
  function drawAxes(pg,w,h,margin){
    pg.stroke(0); pg.strokeWeight(2);
    pg.line(margin,margin,margin,h-margin); // y-axis
    pg.line(margin,h-margin,w-margin,h-margin); // x-axis
    // labels
    pg.fill(0); pg.noStroke();
    pg.textSize(12); pg.textAlign(p.CENTER, p.CENTER);
    pg.text('α', w/2, h-margin+20);
    pg.text('T₂(α)', margin-30, h/2);
  }
  function drawCurve(pg,w,h,margin){
    pg.stroke(255,0,0); pg.noFill(); pg.strokeWeight(2);
    pg.beginShape();
    solutionCurveData.forEach(pt=>{
      const x=mapX(pt.alpha);
      const y=mapY(pt.t2);
      pg.vertex(x,y);
