  function drawIntersectionDots(pg,w,h,margin){
    const alpha=parseFloat(sliders.value);
    const intersections=findYIntersection(alpha);
    pg.noStroke(); pg.fill(0,0,255);
    intersections.forEach(sol=>{
      const x=mapX(alpha);
      const y=mapY(sol.t2);
      pg.ellipse(x,y,10,10);
    });
  }
  function drawTurningPoints(pg,w,h,margin){
    pg.noStroke(); pg.fill(0,255,0);
    turningPointsData.forEach(tp=>{
      const x=mapX(tp.alpha);
      const y=mapY(tp.t2);
      pg.ellipse(x,y,10,10);
    });
  }
  // Expose update method
  chart.update = () => {p.loop(); p.noLoop();}
});

</script>
