<body>
<div id="main-container">
  <div id="control-panel">
    <div id="controls-row-1">
      <div id="type-controls">
        <span>type</span>
        <button id="btn-type-single">single</button>
        <button id="btn-type-double" class="active">double</button>
      </div>
      <div id="item-controls">
        <span>item</span>
        <button id="btn-item-none">none</button>
        <button id="btn-item-point" class="active">point</button>
        <button id="btn-item-fox">fox</button>
        <button id="btn-item-filled-fox">filled fox</button>
      </div>
    </div>
    <div id="controls-row-2">
      <div id="growth-slider-container">
        <label for="slider-growth">spiral growth</label>
        <input type="range" id="slider-growth" min="0.01" max="0.5" step="0.0005" value="0.15">
        <span id="display-growth">0.1500</span>
        <button id="btn-reset-growth">+</button>
      </div>
    </div>
  </div>
  <div id="canvas-container">
    <canvas id="p5-canvas"></canvas>
  </div>
</div>

<script>
/* ---------- State Variables ---------- */
let spiralType = 'double';          // 'single' | 'double'
let itemType = 'point';             // 'none' | 'point' | 'fox' | 'filled-fox'
let spiralGrowth = 0.15;            // k
let z0_loc = {re:0, im:0};          // single spiral center
let z_plus_loc = {re:-100, im:0};   // double spiral pole plus
let z_minus_loc = {re:100, im:0};   // double spiral pole minus

/* ---------- Complex Utility Functions ---------- */
const complex = {
  add: (a,b)=>({re:a.re+b.re, im:a.im+b.im}),
  sub: (a,b)=>({re:a.re-b.re, im:a.im-b.im}),
  mul: (a,b)=>({re:a.re*b.re-a.im*b.im, im:a.re*b.im+a.im*b.re}),
  div: (a,b)=>{ let d = b.re*b.re + b.im*b.im; return {re:(a.re*b.re + a.im*b.im)/d, im:(a.im*b.re - a.re*b.im)/d}; },
  exp: (a)=>{ let e = Math.exp(a.re); return {re:e*Math.cos(a.im), im:e*Math.sin(a.im)}; },
  conj: (a)=>({re:a.re, im:-a.im}),
  abs: (a)=>Math.hypot(a.re,a.im)
};

/* ---------- Fox Shape Vertices ---------- */
const foxVertices = [
  {x:-15,y:0},{x:-10,y:-5},{x:-5,y:-4},{x:0,y:-5},{x:5,y:-5},
  {x:8,y:-2},{x:10,y:-6},{x:12,y:-6},{x:15,y:0},{x:12,y:6},
  {x:10,y:6},{x:8,y:2},{x:5,y:5},{x:0,y:5},{x:-5,y:4},{x:-10,y:5}
];

/* ---------- Helper to format growth ----- */
function updateGrowthDisplay(value){ document.getElementById('display-growth').textContent = value.toFixed(4); }

/* ---------- Hook up UI Events ---------- */
document.getElementById('btn-type-single').addEventListener('click',()=>{ selectType('single'); });
document.getElementById('btn-type-double').addEventListener('click',()=>{ selectType('double'); });

document.getElementById('btn-item-none').addEventListener('click',()=>{ selectItem('none'); });
document.getElementById('btn-item-point').addEventListener('click',()=>{ selectItem('point'); });
document.getElementById('btn-item-fox').addEventListener('click',()=>{ selectItem('fox'); });
document.getElementById('btn-item-filled-fox').addEventListener('click',()=>{ selectItem('filled-fox'); });

const slider = document.getElementById('slider-growth');
slider.addEventListener('input',()=>{ spiralGrowth = parseFloat(slider.value); updateGrowthDisplay(spiralGrowth); redraw(); });
document.getElementById('btn-reset-growth').addEventListener('click',()=>{ spiralGrowth = 0.25; slider.value = spiralGrowth; updateGrowthDisplay(spiralGrowth); redraw(); });

function selectType(type){
  spiralType = type;
  document.querySelectorAll('#type-controls button').forEach(b=>b.classList.remove('active'));
  document.getElementById(`btn-type-${type}`).classList.add('active');
  redraw();
}

function selectItem(type){
  itemType = type;
  document.querySelectorAll('#item-controls button').forEach(b=>b.classList.remove('active'));
  document.getElementById(`btn-item-${type}`).classList.add('active');
  redraw();
}

/* ---------- p5 Sketch ---------- */
let sketch = (p)=>{
  let dragging = null;
  let dragOffset = {x:0, y:0};

  p.setup = ()=>{
    let cnv = p.createCanvas(800, 600);
    cnv.parent('canvas-container');
    p.noFill();
    p.textFont('Arial');
    p.textSize(12);
    p.angleMode(p.RADIANS);
    updateGrowthDisplay(spiralGrowth);
  };

  p.windowResized = ()=>{
    p.resizeCanvas(800, 600);
  };

  p.draw = ()=>{
    p.background(255);
    p.push();
    p.translate(p.width/2, p.height/2);
    drawSpiral();
    p.pop();
  };

  function drawSpiral(){
    let T = 8*Math.PI;
    let epsilon = 0.01;
    if(spiralType==='single'){
      drawSpiralSingle(T);
    } else {
      drawSpiralDouble(T, epsilon);
      if(itemType!=='none'){
        let midX = (z_plus_loc.re + z_minus_loc.re)/2;
        let midY = (z_plus_loc.im + z_minus_loc.im)/2;
        p.noStroke();
        p.fill(0);
        p.text('z0Loc', midX, midY + 15);
      }
    }
    drawLocators();
  }

  function drawSpiralSingle(T){
    p.stroke('#4A6DA8'); p.strokeWeight(2);
    p.beginShape();
    for(let t=-T; t<=T; t+=0.02){
      let r = Math.exp(spiralGrowth*t);
      let x = z0_loc.re + r*Math.cos(t);
      let y = z0_loc.im - r*Math.sin(t);
      p.vertex(x, y);
    }
    p.endShape();
    if(itemType!=='none') drawItemsSingle(T);
  }

  function drawSpiralDouble(T, epsilon){
    // base w(t)
    const k = spiralGrowth;
    const p_loc = z_plus_loc, q_loc = z_minus_loc;
    // left branch (t<-epsilon)
    p.stroke('#4A6DA8'); p.strokeWeight(2);
    p.beginShape();
    for(let t=-T; t<-epsilon; t+=0.02){
      let w = complex.exp({re:k*t, im:t});
      let numerator = complex.sub(complex.mul(p_loc, w), q_loc);
      let denominator = complex.sub(w, {re:1, im:0});
      let z = complex.div(numerator, denominator);
      p.vertex(z.re, z.im);
    }
    p.endShape();
    // right branch (t>epsilon)
    p.beginShape();
    for(let t=epsilon; t<=T; t+=0.02){
      let w = complex.exp({re:k*t, im:t});
      let numerator = complex.sub(complex.mul(p_loc, w), q_loc);
      let denominator = complex.sub(w, {re:1, im:0});
      let z = complex.div(numerator, denominator);
      p.vertex(z.re, z.im);
    }
    p.endShape();
    if(itemType!=='none') drawItemsDouble(T, epsilon);
  }

  function drawItemsSingle(T){
    switch(itemType){
      case 'point': drawPointsSingle(T); break;
      case 'fox': drawFoxesSingle(T, false); break;
      case 'filled-fox': drawFoxesSingle(T, true); break;
    }
  }

  function drawItemsDouble(T, epsilon){
    switch(itemType){
      case 'point': drawPointsDouble(T, epsilon); break;
      case 'fox': drawFoxesDouble(T, epsilon, false); break;
      case 'filled-fox': drawFoxesDouble(T, epsilon, true); break;
    }
  }

  function drawPointsSingle(T){
    p.noStroke(); p.fill('#CB3335');
    for(let t=-T; t<=T; t+=0.5){
      let r = Math.exp(spiralGrowth*t);
      let x = z0_loc.re + r*Math.cos(t);
      let y = z0_loc.im - r*Math.sin(t);
      p.circle(x, y, 5);
    }
  }

  function drawPointsDouble(T, epsilon){
    p.noStroke(); p.fill('#CB3335');
    const p_loc = z_plus_loc, q_loc = z_minus_loc;
    const k = spiralGrowth;
    // left
    for(let t=-T; t<-epsilon; t+=0.5){
      let w = complex.exp({re:k*t, im:t});
      let z = complexDiv(baseW->w, p_loc, q_loc);
      // baseW helper
      let numerator = complex.sub(complex.mul(p_loc, w), q_loc);
      let denominator = complex.sub(w, {re:1, im:0});
      let zPoint = complex.div(numerator, denominator);
      p.circle(zPoint.re, zPoint.im, 5);
    }
    // right
    for(let t=epsilon; t<=T; t+=0.5){
      let w = complex.exp({re:k*t, im:t});
      let numerator = complex.sub(complex.mul(p_loc, w), q_loc);
      let denominator = complex.sub(w, {re:1, im:0});
      let zPoint = complex.div(numerator, denominator);
      p.circle(zPoint.re, zPoint.im, 5);
    }
  }

  function drawFoxesSingle(T, filled){
    const p_loc = z0_loc;
    p.stroke(filled?'#D6292D':'#D6292D');
    p.strokeWeight(filled?0:1);
    if(filled) p.fill('#D6292D'); else p.noFill();
    for(let t=-T; t<=T; t+=1){
      let r = Math.exp(spiralGrowth*t);
      let x = p_loc.re + r*Math.cos(t);
      let y = p_loc.im - r*Math.sin(t);
      // tangent
      let dx = -r*Math.sin(t)*spiralGrowth - r*Math.cos(t);
      let dy = -r*Math.cos(t)*spiralGrowth + r*Math.sin(t);
      let angle = Math.atan2(dy, dx);
      p.push(); p.translate(x, y); p.rotate(angle); p.scale(1);
      p.beginShape();
      foxVertices.forEach(v=>p.vertex(v.x, v.y));
      p.endShape(p.CLOSE);
      p.pop();
    }
  }

  function drawFoxesDouble(T, epsilon, filled){
    const p_loc = z_plus_loc, q_loc = z_minus_loc;
    const k = spiralGrowth;
    p.stroke(filled?'#D6292D':'#D6292D');
    p.strokeWeight(filled?0:1);
    if(filled) p.fill('#D6292D'); else p.noFill();

    // left branch
    for(let t=-T; t<-epsilon; t+=1){
      let w = complex.exp({re:k*t, im:t});
      let numerator = complex.sub(complex.mul(p_loc, w), q_loc);
      let denominator = complex.sub(w, {re:1, im:0});
      let z = complex.div(numerator, denominator);
      // derivative
      let numDeriv = complex.mul(complex.add(complex.mul({re:2*k, im:2}, complex.exp({re:k*t, im:t})), {re:0,im:0}), complex.sub(p_loc, q_loc));
      let denom = complex.sub(w, {re:1,im:0});
      let denomSq = complex.mul(denom, denom);
      let zDer = complex.div(numDeriv, denomSq);
      let angle = Math.atan2(zDer.im, zDer.re);
      p.push(); p.translate(z.re, z.im); p.rotate(angle); p.scale(1);
      p.beginShape();
      foxVertices.forEach(v=>p.vertex(v.x, v.y));
      p.endShape(p.CLOSE);
      p.pop();
    }

    // right branch
    for(let t=epsilon; t<=T; t+=1){
      let w = complex.exp({re:k*t, im:t});
      let numerator = complex.sub(complex.mul(p_loc, w), q_loc);
      let denominator = complex.sub(w, {re:1, im:0});
      let z = complex.div(numerator, denominator);
      // derivative similar to above
      let numDeriv = complex.mul(complex.add(complex.mul({re:2*k, im:2}, complex.exp({re:k*t, im:t})), {re:0,im:0}), complex.sub(p_loc, q_loc));
      let denom = complex.sub(w, {re:1,im:0});
      let denomSq = complex.mul(denom, denom);
      let zDer = complex.div(numDeriv, denomSq);
      let angle = Math.atan2(zDer.im, zDer.re);
      p.push(); p.translate(z.re, z.im); p.rotate(angle); p.scale(1);
      p.beginShape();
      foxVertices.forEach(v=>p.vertex(v.x, v.y));
      p.endShape(p.CLOSE);
      p.pop();
    }
  }

  function drawLocators(){
    // Helper
    const drawLoc = (loc, fillCol, name)=>{
      const r=10;
      p.push();
      p.translate(loc.re, loc.im);
      p.stroke(0);
      p.fill(fillCol);
      p.ellipse(0,0,r*2,r*2);
      // crosshair
      p.stroke(0);
      p.line(-r,0,r,0);
      p.line(0,-r,0,r);
      if(name){
        p.noStroke();
        p.fill(0);
        p.text(name, r+3, -r-3);
      }
      p.pop();
    };
    if(spiralType==='single'){
      drawLoc(z0_loc, 'rgba(255,255,0,0.8)', 'z0Loc');
    } else {
      drawLoc(z_plus_loc, 'rgba(255,255,0,0.8)', 'z+');
      drawLoc(z_minus_loc, 'rgba(0,255,0,0.8)', 'z-');
    }
  }

  /* ---------- Mouse Interaction for Locators ---------- */
  p.mousePressed = ()=>{
    const mx = p.mouseX - p.width/2;
    const my = p.mouseY - p.height/2;
    const radius = 15; // detection radius
    if(spiralType==='single'){
      if(Math.hypot(mx - z0_loc.re, my - z0_loc.im) < radius){
        dragging = 'z0'; dragOffset = {x:mx - z0_loc.re, y:my - z0_loc.im};
      }
    } else {
      if(Math.hypot(mx - z_plus_loc.re, my - z_plus_loc.im) < radius){
        dragging = 'plus'; dragOffset = {x:mx - z_plus_loc.re, y:my - z_plus_loc.im};
      } else if(Math.hypot(mx - z_minus_loc.re, my - z_minus_loc.im) < radius){
        dragging = 'minus'; dragOffset = {x:mx - z_minus_loc.re, y:my - z_minus_loc.im};
      }
    }
  };
  p.mouseDragged = ()=>{
    if(!dragging) return;
    const mx = p.mouseX - p.width/2;
    const my = p.mouseY - p.height/2;
    if(dragging==='z0'){
      z0_loc.re = mx - dragOffset.x;
      z0_loc.im = my - dragOffset.y;
    } else if(dragging==='plus'){
      z_plus_loc.re = mx - dragOffset.x;
      z_plus_loc.im = my - dragOffset.y;
    } else if(dragging==='minus'){
      z_minus_loc.re = mx - dragOffset.x;
      z_minus_loc.im = my - dragOffset.y;
    }
  };
  p.mouseReleased = ()=>{ dragging = null; };
};

new p5(sketch);

/* ---------- Redraw Helper ---------- */
function redraw(){ /* p5 will automatically redraw on changes; no action needed unless we call redraw manually */ }
</script>
</body>
</html>
