<body>
  <div id="control-panel">
    <button id="start-over-btn">Start Over</button>
    <label for="vertex-grid">Initial Vertex</label>
    <div id="vertex-grid">
      <button id="btn-a" class="vertex-btn">a</button>
      <button id="btn-b" class="vertex-btn">b</button>
      <button id="btn-c" class="vertex-btn">c</button>
