      <button id="btn-e" class="vertex-btn">e</button>
      <button id="btn-f" class="vertex-btn">f</button>
      <button id="btn-g" class="vertex-btn">g</button>
      <button id="btn-h" class="vertex-btn">h</button>
      <button id="btn-i" class="vertex-btn">i</button>
      <button id="btn-j" class="vertex-btn">j</button>
      <button id="btn-k" class="vertex-btn">k</button>
      <button id="btn-l" class="vertex-btn">l</button>
