  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.3/lib/p5.min.js"></script>
  <script>
    // ----- Data Structures -----
    const canvasSize = 600;
    const margin = 80;
    const gridSize = 4;
    const cellGap = (canvasSize - 2 * margin) / (gridSize - 1);
    const vertices = []; // {id, x, y}
    const adjacency = {}; // id -> [{to, weight}]
    const edges = []; // {u, v, weight}
    const distances = {}; // id -> number
    const visited = {}; // id -> boolean
    const pred = {}; // id -> predecessor id
    let startVertex = 'm';
    let startOverFlag = false; // to force reset after random example
    // Predefined fixed weights
    const fixedWeights = {
      "m-n": 8, "n-o": 7, "o-p": 7,
      "i-j": 9, "j-k": 7, "k-l": 1,
      "e-f": 1, "f-g": 7, "g-h": 6,
      "a-b": 10, "b-c": 4, "c-d": 8,
      "m-i": 6, "i-e": 9, "e-a": 9,
      "n-j": 7, "j-f": 6, "f-b": 5,
      "o-k": 1, "k-g": 8, "g-c": 8,
      "p-l": 2, "l-h": 1, "h-d": 7
    };

    // ----- Initialization -----
    function setup() {
      createCanvas(canvasSize, canvasSize);
      noLoop();
      initVertices();
      setGraphWeights(fixedWeights);
      resetAlgorithmState();
      updateTitle();
      draw();
    }
    function initVertices() {
      const letters = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p'];
      let idx = 0;
      for(let row=0; row<gridSize; row++) {
        for(let col=0; col<gridSize; col++) {
          const x = margin + col * cellGap;
          const y = margin + row * cellGap;
          const id = letters[idx++];
          vertices.push({id, x, y});
        }
      }
    }
    function setGraphWeights(weightsObj) {
      adjacency = {}; edges.splice(0, edges.length); // clear
      // horizontal edges
      const horizPairs = [
        ['a','b'], ['b','c'], ['c','d'],
        ['e','f'], ['f','g'], ['g','h'],
        ['i','j'], ['j','k'], ['k','l'],
        ['m','n'], ['n','o'], ['o','p']
      ];
      const vertPairs = [
        ['a','e'], ['b','f'], ['c','g'], ['d','h'],
        ['e','i'], ['f','j'], ['g','k'], ['h','l'],
        ['i','m'], ['j','n'], ['k','o'], ['l','p']
      ];
      horizPairs.concat(vertPairs).forEach(pair => {
        const key = pair.join('-');
        const weight = weightsObj[key] || 1;
        const [u, v] = pair;
        // adjacency
        if (!adjacency[u]) adjacency[u] = [];
        if (!adjacency[v]) adjacency[v] = [];
        adjacency[u].push({to: v, weight});
        adjacency[v].push({to: u, weight});
        // edge list
        edges.push({u, v, weight});
      });
    }
    function resetAlgorithmState() {
      distances = {}; visited = {}; pred = {};
      vertices.forEach(v => {
        distances[v.id] = Infinity;
        visited[v.id] = false;
        pred[v.id] = null;
      });
      distances[startVertex] = 0;
      document.getElementById('next-step-btn').disabled = false;
    }
    function updateTitle() {
      document.getElementById('title-display').textContent = `Dijkstra's algorithm starting at vertex ${startVertex}`;
    }
    function highlightSelectedButton() {
      vertices.forEach(v => {
        const btn = document.getElementById(`btn-${v.id}`);
        if (v.id === startVertex) btn.classList.add('active');
        else btn.classList.remove('active');
      });
    }

    // ----- Animation -----
    function draw() {
      background(255);
      // Draw edges
      stroke(0);
      strokeWeight(1);
      edges.forEach(e => {
        const u = vertices.find(v=>v.id===e.u);
        const v = vertices.find(v=>v.id===e.v);
        let inTree = false;
        if (visited[e.u] && pred[e.u]===e.v) inTree = true;
        if (visited[e.v] && pred[e.v]===e.u) inTree = true;
        if (inTree) {
          stroke(0,0,255);
          strokeWeight(4);
        }
        line(u.x, u.y, v.x, v.y);
        // Edge weight
        const midX = (u.x+v.x)/2;
        const midY = (u.y+v.y)/2;
        fill(255);
        noStroke();
        rect(midX-12, midY-8, 24, 16, 3);
        fill(0);
        textSize(12);
        textAlign(CENTER, CENTER);
        text(e.weight, midX, midY);
      });
      // Draw vertices
      vertices.forEach(v => {
        const color = visited[v.id] ? color(255,0,0) : color(0);
        fill(color);
        noStroke();
        ellipse(v.x, v.y, 20, 20);
        // Vertex label
        fill(0);
        textAlign(LEFT, CENTER);
        textSize(16);
        text(v.id, v.x - 10, v.y + 10);
        // Distance label
        let distStr = distances[v.id] === Infinity ? '∞' : distances[v.id];
        const distColor = visited[v.id] ? color(255,0,0) : color(0);
        fill(distColor);
        textSize(12);
        text(`(${distStr})`, v.x - 7, v.y - 20);
      });
    }

    // ----- Algorithm Step -----
    function nextStep() {
      let minDist = Infinity;
      let u = null;
      vertices.forEach(v => {
        if (!visited[v.id] && distances[v.id] < minDist) {
          minDist = distances[v.id];
          u = v.id;
        }
      });
      if (!u) return; // no more unvisited
      visited[u] = true;
      const neighbors = adjacency[u];
      neighbors.forEach(nb => {
        if (!visited[nb.to]) {
          const newDist = distances[u] + nb.weight;
          if (newDist < distances[nb.to]) {
            distances[nb.to] = newDist;
            pred[nb.to] = u;
          }
        }
      });
      // Disable if all visited
      if (vertices.every(v => visited[v.id])) {
        document.getElementById('next-step-btn').disabled = true;
      }
      redraw();
    }

    // ----- Event Listeners -----
    document.getElementById('start-over-btn').addEventListener('click', () => {
      startVertex = 'm';
      setGraphWeights(fixedWeights);
      resetAlgorithmState();
      highlightSelectedButton();
      updateTitle();
      redraw();
    });

    vertices.forEach(v => {
      document.getElementById(`btn-${v.id}`).addEventListener('click', () => {
        startVertex = v.id;
        resetAlgorithmState();
        highlightSelectedButton();
        updateTitle();
        redraw();
      });
    });

    document.getElementById('fixed-example-btn').addEventListener('click', () => {
      setGraphWeights(fixedWeights);
      resetAlgorithmState();
      redraw();
    });

    document.getElementById('random-example-btn').addEventListener('click', () => {
      // generate random weights
      const obj = {};
      edges.forEach(e => {
        obj[e.u+'-'+e.v] = Math.floor(Math.random()*10)+1;
      });
      setGraphWeights(obj);
      resetAlgorithmState();
      redraw();
    });

    document.getElementById('next-step-btn').addEventListener('click', nextStep);
  </script>
</body>
</html>
