  // Sphere
  const sphereGeom = new THREE.SphereGeometry(1, 32, 32);
  const sphereMat = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50 });
  const sphere = new THREE.Mesh(sphereGeom, sphereMat);
  sphere.scale.set(state.sphereRadius, state.sphereRadius, state.sphereRadius);
  scene.add(sphere);

  // Helper to get unique vertices of a geometry
  function getUniqueVertices(geom) {
    const positions = geom.attributes.position.array;
    const uniq = [];
    const seen = new Set();
    for (let i = 0; i < positions.length; i += 3) {
      const v = new THREE.Vector3(positions[i], positions[i + 1], positions[i + 2]);
      const key = `${v.x.toFixed(5)}_${v.y.toFixed(5)}_${v.z.toFixed(5)}`;
      if (!seen.has(key)) {
        seen.add(key);
        uniq.push(v.clone().normalize()); // store normalized direction
      }
    }
    return uniq;
  }

  // Create Cone Mesh
  function createCone(direction) {
    const geom = new THREE.ConeGeometry(state.coneBase, state.coneHeight, 32, 1, true);
    const mat = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 });
    const mesh = new THREE.Mesh(geom, mat);

    const pos = direction.clone().multiplyScalar(state.sphereRadius + state.coneHeight / 2);
    mesh.position.copy(pos);
    mesh.quaternion.setFromUnitVectors(new THREE.Vector3(0,1,0), direction);

    return mesh;
  }

  // Build cones for current polyhedron
  function buildCones() {
    // Clear existing
    state.coneMeshes.forEach(m => scene.remove(m));
    state.coneMeshes = [];
    // Determine geometry
    let geom;
    switch(state.polygon) {
      case 'Cube': geom = new THREE.BoxGeometry(2,2,2); break;
      case 'Dodecahedron': geom = new THREE.DodecahedronGeometry(1); break;
      case 'Icosahedron': geom = new THREE.IcosahedronGeometry(1); break;
      case 'Octahedron': geom = new THREE.OctahedronGeometry(1); break;
      case 'Tetrahedron': geom = new THREE.TetrahedronGeometry(1); break;
      default: geom = new THREE.OctahedronGeometry(1);
    }
    const dirs = getUniqueVertices(geom);
    state.directions = dirs;
    dirs.forEach(dir => {
      const cone = createCone(dir);
      scene.add(cone);
      state.coneMeshes.push(cone);
    });
  }

  // Initial build
  buildCones();

  // UI Handlers
  const sliderSphere = document.getElementById('slider-sphere-diameter');
  const sliderConeH = document.getElementById('slider-cone-height');
  const sliderConeB = document.getElementById('slider-cone-base');

  sliderSphere.addEventListener('input', () => {
    const newRadius = sliderSphere.value / 2;
    const scale = newRadius / state.sphereRadius;
    sphere.scale.set(scale, scale, scale);
    state.sphereRadius = newRadius;
    // update cone positions
    state.coneMeshes.forEach((cone, idx) => {
      const dir = state.directions[idx];
      const pos = dir.clone().multiplyScalar(state.sphereRadius + state.coneHeight / 2);
      cone.position.copy(pos);
    });
  });

  sliderConeH.addEventListener('input', () => {
    state.coneHeight = sliderConeH.value;
    // update geometry and positions
    state.coneMeshes.forEach((cone, idx) => {
      const dir = state.directions[idx];
      cone.geometry.dispose();
      cone.geometry = new THREE.ConeGeometry(state.coneBase, state.coneHeight, 32, 1, true);
      const pos = dir.clone().multiplyScalar(state.sphereRadius + state.coneHeight / 2);
      cone.position.copy(pos);
    });
  });

  sliderConeB.addEventListener('input', () => {
    state.coneBase = sliderConeB.value;
    // update geometry only
    state.coneMeshes.forEach((cone) => {
      cone.geometry.dispose();
      cone.geometry = new THREE.ConeGeometry(state.coneBase, state.coneHeight, 32, 1, true);
    });
  });

  // Polyhedron buttons
  const btnCube = document.getElementById('btn-cube');
  const btnDodecahedron = document.getElementById('btn-dodecahedron');
  const btnIcosahedron = document.getElementById('btn-icosahedron');
  const btnOctahedron = document.getElementById('btn-octahedron');
  const btnTetrahedron = document.getElementById('btn-tetrahedron');
  const btns = [btnCube, btnDodecahedron, btnIcosahedron, btnOctahedron, btnTetrahedron];

  function setActive(btn, poly) {
    btns.forEach(b => b.classList.remove('active'));
    btn.classList.add('active');
    state.polygon = poly;
    buildCones();
  }

  btnCube.addEventListener('click', () => setActive(btnCube, 'Cube'));
  btnDodecahedron.addEventListener('click', () => setActive(btnDodecahedron, 'Dodecahedron'));
  btnIcosahedron.addEventListener('click', () => setActive(btnIcosahedron, 'Icosahedron'));
  btnOctahedron.addEventListener('click', () => setActive(btnOctahedron, 'Octahedron'));
  btnTetrahedron.addEventListener('click', () => setActive(btnTetrahedron, 'Tetrahedron'));

  // Resize handling
  window.addEventListener('resize', () => {
    const width = canvasContainer.clientWidth;
    const height = canvasContainer.clientHeight;
    renderer.setSize(width, height);
    camera.aspect = width / height;
    camera.updateProjectionMatrix();
  });

  // Animation loop
  function animate() {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }
  animate();
})();
</script>
</body>
</html>
