<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Analemma Demo</title>
<style>
  * { box-sizing: border-box; }
  body, html { margin:0; padding:0; height:100%; font-family:Arial, sans-serif; }
  #main-container {
    display:flex; height:100%;
  }
  #control-panel {
    width:300px; padding:20px; background:#f0f0f0; overflow:auto;
  }
  #visualization-area {
    flex-grow:1; padding:20px;
  }
  label { display:block; margin-top:10px; font-weight:bold; }
  input[type="range"] { width:100%; }
  input:disabled { opacity:0.5; }
  #manual-controls > div { margin-top:10px; }
  #display-controls { margin-top:20px; }
</style>
</head>
<body>

<div id="main-container">

  <div id="control-panel">
    <label for="select-planet">Planet</label>
    <select id="select-planet">
      <option value="manual" selected>-- choose orbit manually --</option>
      <option value="Earth">Earth</option>
      <option value="Mars">Mars</option>
    </select>

    <div id="manual-controls">
      <input type="checkbox" id="checkbox-manual" checked>
      <label for="checkbox-manual">Choose orbit manually</label>

      <div>
        <label for="slider-tilt">Axis angle relative to ecliptic</label>
        <input type="range" id="slider-tilt" min="0" max="90" step="0.1" value="23.5">
      </div>
      <div>
        <label for="slider-equinox">Spring equinox point</label>
        <input type="range" id="slider-equinox" min="-180" max="180" step="1" value="0">
      </div>
      <div>
        <label for="slider-eccentricity">Orbit eccentricity</label>
        <input type="range" id="slider-eccentricity" min="0" max="0.5" step="0.001" value="0.1">
      </div>
    </div>

    <div id="display-controls">
      <input type="checkbox" id="checkbox-snapshots" checked>
      <label for="checkbox-snapshots">Show sun snapshots</label>
      <input type="checkbox" id="checkbox-scales" checked>
      <label for="checkbox-scales">Show scales</label>
    </div>
  </div>

  <div id="visualization-area">
    <canvas id="analemma-canvas"></canvas>
  </div>

</div>

<script src="https://cdn.jsdelivr.net/npm/p5@1.4.2/lib/p5.min.js"></script>
<script>
/* Planet data */
const planetData = {
  "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
  "Mars" : { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
};

/* DOM Elements */
const selectPlanet   = document.getElementById('select-planet');
const checkboxManual = document.getElementById('checkbox-manual');
const sliderTilt     = document.getElementById('slider-tilt');
const sliderEquinox  = document.getElementById('slider-equinox');
const sliderEcc      = document.getElementById('slider-eccentricity');
const checkboxSnapshots = document.getElementById('checkbox-snapshots');
const checkboxScales     = document.getElementById('checkbox-scales');
const vizArea = document.getElementById('visualization-area');

/* Event Handlers */
function updatePlanetSelection() {
  const val = selectPlanet.value;
  if (val === 'manual') {
    checkboxManual.checked = true;
    sliderTilt.disabled = false;
    sliderEquinox.disabled = false;
    sliderEcc.disabled = false;
  } else {
    const d = planetData[val];
    sliderTilt.value = d.tilt;
    sliderEcc.value  = d.eccentricity;
    sliderEquinox.value = d.equinox;
    checkboxManual.checked = false;
    sliderTilt.disabled = true;
    sliderEquinox.disabled = true;
    sliderEcc.disabled = true;
  }
  p5Instance.redraw();
}

function updateManualCheckbox() {
  if (checkboxManual.checked) {
    selectPlanet.value = 'manual';
    sliderTilt.disabled = false;
    sliderEquinox.disabled = false;
    sliderEcc.disabled = false;
  } else {
    sliderTilt.disabled = true;
    sliderEquinox.disabled = true;
    sliderEcc.disabled = true;
  }
  p5Instance.redraw();
}

/* Attach listeners */
selectPlanet.addEventListener('change', updatePlanetSelection);
checkboxManual.addEventListener('change', updateManualCheckbox);
sliderTilt.addEventListener('input', () => p5Instance.redraw());
sliderEquinox.addEventListener('input', () => p5Instance.redraw());
sliderEcc.addEventListener('input', () => p5Instance.redraw());
checkboxSnapshots.addEventListener('change', () => p5Instance.redraw());
checkboxScales.addEventListener('change', () => p5Instance.redraw());

/* P5.js Instance */
let p5Instance = new p5(p => {
  let canvas;

  p.setup = function() {
    canvas = p.createCanvas(vizArea.clientWidth, vizArea.clientHeight);
    canvas.parent('visualization-area');
    p.noLoop(); // Draw only when redrawn
  };

  p.draw = function() {
    p.background(255);

    /* Grab current parameters */
    const tiltDeg   = parseFloat(sliderTilt.value);
    const ecc       = parseFloat(sliderEcc.value);
    const equinoxDeg= parseFloat(sliderEquinox.value);
    const showSnapshots = checkboxSnapshots.checked;
    const showScales = checkboxScales.checked;

    const tiltRad    = tiltDeg * Math.PI / 180;
    const equinoxRad = equinoxDeg * Math.PI / 180;

    /* Compute analemma points */
    const points = [];
    for (let d = 0; d < 365; d++) {
      const M = (2 * Math.PI * d) / 365.25;
      /* Newton–Raphson to solve Kepler's equation */
      let E = M;
      for (let i = 0; i < 5; i++) {
        E = E - (E - ecc * Math.sin(E) - M) / (1 - ecc * Math.cos(E));
      }
      const v = 2 * Math.atan2(Math.sqrt(1 + ecc) * Math.sin(E / 2),
                               Math.sqrt(1 - ecc) * Math.cos(E / 2));
      const lambda = v + equinoxRad;
      const y = Math.asin(Math.sin(tiltRad) * Math.sin(lambda));
      const alpha = Math.atan2(Math.cos(tiltRad) * Math.sin(lambda),
                               Math.cos(lambda));
      let EoT = M - alpha;
      EoT = ((EoT + Math.PI) % (2 * Math.PI)) - Math.PI; // wrap [-π,π]
      points.push({x: EoT, y: y});
    }

    /* Scaling and translation */
    const maxPixel = 200; // half-width and half-height of plot area in pixels
    const xScale = maxPixel / 1; // EoT amplitude ~1 rad
    const yScale = maxPixel / 1; // declination amplitude ~1
    const cx = p.width / 2;
    const cy = p.height / 2;

    /* Draw orbit ellipse at top */
    const orbitRx = 50 * (1 + ecc);
    const orbitRy = 30;
    p.stroke('brown');
    p.noFill();
    p.ellipse(cx, 60, orbitRx, orbitRy);

    /* Draw scales if requested */
    if (showScales) {
      p.stroke(0);
      p.line(cx - maxPixel, cy, cx + maxPixel, cy); // X
      p.line(cx, cy - maxPixel, cx, cy + maxPixel); // Y
      const tickStep = 20;
      for (let t = -maxPixel; t <= maxPixel; t += tickStep) {
        p.line(cx + t, cy - 5, cx + t, cy + 5);
        p.noStroke();
        p.fill(0);
        p.text(t, cx + t + 2, cy - 10);
        p.stroke(0);
      }
    }

    /* Draw analemma curve */
    p.noFill();
    p.stroke('orange');
    p.strokeWeight(2);
    p.beginShape();
    for (let i = 0; i < points.length; i++) {
      const px = cx + points[i].x * xScale;
      const py = cy + points[i].y * yScale;
      p.vertex(px, py);
    }
    p.endShape();

    /* Sun snapshots */
    if (showSnapshots) {
      p.noStroke();
      p.fill('orange');
      const step = 15; // one dot per ~15 days
      for (let i = 0; i < points.length; i += step) {
        const px = cx + points[i].x * xScale;
        const py = cy + points[i].y * yScale;
        p.circle(px, py, 8);
      }
    }
  };
});
</script>
</body>
</html>
