<script>
/* ---------- Utility functions ---------- */
function deepCopy(obj) { return JSON.parse(JSON.stringify(obj)); }

/* ---------- Graph & Algorithm State ---------- */
const state = {
  graph: null,          // {vertices: [], adjacencyMatrix: []}
  optimalPath: null,    // [{from, to, weight}, ...]
  currentAlgorithm: 'nearest-neighbor',
  algoPath: [],         // [{from, to, weight}, ...]
  visited: new Set(),
  currentVertex: null,
  nextStepEnabled: false,
  nextStepBtn: document.getElementById('btn-next-step'),
  vertexButtons: Array.from(document.querySelectorAll('.vertex-btn')),
  algoCanvasSketch: null,
  optimalCanvasSketch: null
};

/* ---------- Fixed Graph Data ---------- */
const fixedGraph = {
  vertices: [
    {label: 'A', x: 200, y: 60},
    {label: 'B', x: 80, y: 120},
    {label: 'C', x: 60, y: 240},
    {label: 'D', x: 150, y: 340},
    {label: 'E', x: 250, y: 340},
    {label: 'F', x: 340, y: 240},
    {label: 'G', x: 320, y: 120}
  ],
  adjacencyMatrix: [
    [0, 11, 16, 44, 30, 44, 23],
    [11, 0, 12, 12, 10, 30, 25],
    [16, 12, 0, 43, 32, 36, 44],
    [44, 12, 43, 0, 41, 30, 35],
    [30, 10, 32, 41, 0, 31, 20],
    [44, 30, 36, 30, 31, 0, 18],
    [23, 25, 44, 35, 20, 18, 0]
  ]
};

/* ---------- Helper: compute optimal path via brute force (N=7) ---------- */
function computeOptimalPath(vertices, adj) {
  const n = vertices.length;
  const indices = [...Array(n).keys()];
  let bestWeight = Infinity;
  let bestPath = null;
  const perm = indices.slice(1); // start from 0 fixed
  // simple DFS permutations
  function dfs(path, visited, currentWeight) {
    if (path.length === n) {
      // add return edge to start
      const last = path[path.length - 1];
      const ret = adj[last][0];
      const total = currentWeight + ret;
      if (total < bestWeight) {
        bestWeight = total;
        bestPath = [...path, 0];
      }
      return;
    }
    for (let v of perm) {
      if (!visited.has(v)) {
        const w = adj[path[path.length - 1]][v];
        visited.add(v);
        dfs(path.concat(v), visited, currentWeight + w);
        visited.delete(v);
      }
    }
  }
  dfs([0], new Set([0]), 0);
  // build edge list with weights
  const edges = [];
  for (let i = 0; i < bestPath.length - 1; i++) {
    const from = vertices[bestPath[i]].label;
    const to = vertices[bestPath[i + 1]].label;
    const weight = adj[bestPath[i]][bestPath[i + 1]];
    edges.push({from, to, weight});
  }
  return {edges, weight: bestWeight};
}

/* ---------- GUI Setup ---------- */
document.getElementById('algo-cheapest-link').addEventListener('change', changeAlgorithm);
document.getElementById('algo-nearest-neighbor').addEventListener('change', changeAlgorithm);
document.getElementById('btn-start-over').addEventListener('click', resetAlgorithm);
document.getElementById('btn-fixed-example').addEventListener('click', loadFixedGraph);
document.getElementById('btn-random-example').addEventListener('click', loadRandomGraph);
document.getElementById('btn-next-step').addEventListener('click', runNextStep);
document.getElementById('checkbox-optimal').addEventListener('change', toggleOptimal);

state.vertexButtons.forEach(btn => btn.addEventListener('click', selectStartingVertex));

/* ---------- Initial Load ---------- */
loadFixedGraph();

/* ---------- Functions ---------- */
function changeAlgorithm() {
  state.currentAlgorithm = document.querySelector('input[name="algorithm-choice"]:checked').value;
  const vertexSel = document.getElementById('starting-vertex-selector');
  if (state.currentAlgorithm === 'nearest-neighbor') {
    vertexSel.style.display = 'block';
  } else {
    vertexSel.style.display = 'none';
  }
  resetAlgorithm();
}

function resetAlgorithm() {
  state.algoPath = [];
  state.visited = new Set();
  state.currentVertex = null;
  state.nextStepEnabled = false;
  state.nextStepBtn.disabled = true;
  document.getElementById('table-edge-list').querySelector('tbody').innerHTML = '';
  document.getElementById('text-total-weight').textContent = '';
  // enable all vertex buttons if NN
  if (state.currentAlgorithm === 'nearest-neighbor') {
    state.vertexButtons.forEach(btn => btn.disabled = false);
  }
  // title
  document.getElementById('title-algo').textContent = 'Select a starting vertex';
  // redrawing canvas whole graph
  drawGraph(state.graph, state.algoCanvasSketch, [], [], '#fff', '#000', '#000');
}

function loadFixedGraph() {
  state.graph = deepCopy(fixedGraph);
  document.getElementById('text-example-type-algo').textContent = 'fixed example';
  document.getElementById('text-example-type-optimal').textContent = 'fixed example';
  // compute optimal path
  const optimal = computeOptimalPath(state.graph.vertices, state.graph.adjacencyMatrix);
  state.optimalPath = optimal;
  // create canvases
  createAlgoCanvas();
  createOptimalCanvas(false);
  resetAlgorithm();
}

function loadRandomGraph() {
  const n = 7;
  const vertices = [];
  for (let i = 0; i < n; i++) {
    vertices.push({
      label: String.fromCharCode(65 + i),
      x: 50 + Math.random() * 300,
      y: 50 + Math.random() * 300
    });
  }
  const adjacencyMatrix = Array.from({length: n}, () => Array(n).fill(0));
  for (let i = 0; i < n; i++) {
    for (let j = i + 1; j < n; j++) {
      const w = Math.floor(10 + Math.random() * 41);
      adjacencyMatrix[i][j] = adjacencyMatrix[j][i] = w;
    }
  }
  state.graph = {vertices, adjacencyMatrix};
  document.getElementById('text-example-type-algo').textContent = 'random example';
  document.getElementById('text-example-type-optimal').textContent = 'random example';
  // compute optimal path
  const optimal = computeOptimalPath(state.graph.vertices, state.graph.adjacencyMatrix);
  state.optimalPath = optimal;
  createAlgoCanvas();
  createOptimalCanvas(false);
  resetAlgorithm();
}

function createAlgoCanvas() {
  const wrapper = document.getElementById('canvas-algo-wrapper');
  wrapper.innerHTML = ''; // remove previous
  state.algoCanvasSketch = new p5(p => {
    p.setup = () => {
      p.createCanvas(450, 450);
    };
    p.draw = () => {
      p.background('#fff');
      drawGraph(state.graph, p, state.algoPath, [], '#000', '#000', '#000');
    };
  }, wrapper);
}

function createOptimalCanvas(show) {
  const wrapper = document.getElementById('canvas-optimal-wrapper');
  wrapper.innerHTML = '';
  if (!show) return;
  state.optimalCanvasSketch = new p5(p => {
    p.setup = () => {
      p.createCanvas(450, 450);
    };
    p.draw = () => {
      p.background('#fff');
      drawGraph(state.graph, p, [], state.optimalPath.edges, '#000', '#000', '#000');
    };
  }, wrapper);
}

function drawGraph(graph, p, algoPath, optPath, edgeColor, vertexColor, textColor) {
  // Draw all edges
  const n = graph.vertices.length;
  const adj = graph.adjacencyMatrix;
  for (let i = 0; i < n; i++) {
    for (let j = i + 1; j < n; j++) {
      const x1 = graph.vertices[i].x, y1 = graph.vertices[i].y;
      const x2 = graph.vertices[j].x, y2 = graph.vertices[j].y;
      p.stroke(edgeColor); p.strokeWeight(1);
      p.line(x1, y1, x2, y2);
      // weight
      const midX = (x1 + x2) / 2, midY = (y1 + y2) / 2;
      p.noStroke(); p.fill(255); p.rect(midX - 10, midY - 8, 20, 16);
      p.fill(textColor); p.textAlign(p.CENTER, p.CENTER);
      p.text(adj[i][j], midX, midY);
    }
  }
  // highlight algo path
  userPathDraw(p, algoPath, '#ff7f00', 3);
  // highlight optimal path
  userPathDraw(p, optPath, '#1f77b4', 3);
  // draw vertices
  for (const v of graph.vertices) {
    p.fill(vertexColor);
    p.stroke('#000'); p.strokeWeight(2);
    p.ellipse(v.x, v.y, 30, 30);
    p.fill('#000');
    p.noStroke(); p.textAlign(p.CENTER, p.CENTER); p.text(v.label, v.x, v.y);
  }
}

function userPathDraw(p, path, col, w) {
  for (const e of path) {
    const from = findVertexIndex(e.from);
    const to = findVertexIndex(e.to);
    const x1 = state.graph.vertices[from].x, y1 = state.graph.vertices[from].y;
    const x2 = state.graph.vertices[to].x, y2 = state.graph.vertices[to].y;
    p.stroke(col); p.strokeWeight(w);
    p.line(x1, y1, x2, y2);
  }
}

function findVertexIndex(label) {
  return state.graph.vertices.findIndex(v => v.label === label);
}

function selectStartingVertex(evt) {
  const label = evt.target.dataset.vertex;
  // lock choice
  state.vertexButtons.forEach(btn => btn.disabled = true);
  state.currentVertex = label;
  state.visited.add(label);
  document.getElementById('title-algo').textContent = `The nearest neighbor algorithm starting at vertex ${label}`;
  state.nextStepBtn.disabled = false;
}

function runNextStep() {
  if (state.currentAlgorithm === 'nearest-neighbor') {
    runNearestNeighborStep();
  } else if (state.currentAlgorithm === 'cheapest-link') {
    // placeholder
    runCheapestLinkStep();
  }
  // redraw algo canvas
  state.algoCanvasSketch.redraw();
  updateEdgeTable();
  if (isAlgorithmComplete()) {
    state.nextStepBtn.disabled = true;
    document.getElementById('text-total-weight').textContent =
      `Total weight of the tour: ${state.algoPath.reduce((a, e) => a + e.weight, 0)}`;
  }
}

function runNearestNeighborStep() {
  if (!state.currentVertex) return;
  const currIdx = findVertexIndex(state.currentVertex);
  const adj = state.graph.adjacencyMatrix;
  // find nearest unvisited neighbor
  let bestW = Infinity, bestV = null;
  for (let i = 0; i < adj.length; i++) {
    const vLabel = state.graph.vertices[i].label;
    if (!state.visited.has(vLabel) && adj[currIdx][i] < bestW) {
      bestW = adj[currIdx][i];
      bestV = vLabel;
    }
  }
  if (bestV) {
    // add edge
    state.algoPath.push({from: state.currentVertex, to: bestV, weight: bestW});
    state.visited.add(bestV);
    state.currentVertex = bestV;
  } else {
    // all visited, return to start
    const startIdx = findVertexIndex(state.algoPath[0].from);
    const currIdx = findVertexIndex(state.currentVertex);
    const returnW = adj[currIdx][startIdx];
    state.algoPath.push({from: state.currentVertex, to: state.algoPath[0].from, weight: returnW});
    state.currentVertex = null; // finished
  }
}

function runCheapestLinkStep() {
  // Not implemented – simply finish for demo
  state.nextStepBtn.disabled = true;
  document.getElementById('text-total-weight').textContent =
    `Cheapest link algorithm demo not implemented.`;
}

function isAlgorithmComplete() {
  if (state.currentAlgorithm === 'nearest-neighbor') {
    return state.currentVertex === null;
  }
  return false;
}

function updateEdgeTable() {
  const tbody = document.getElementById('table-edge-list').querySelector('tbody');
  tbody.innerHTML = '';
  state.algoPath.forEach((e, idx) => {
    const tr = document.createElement('tr');
    tr.innerHTML = `<td>${idx+1}</td><td>${e.from}–${e.to}</td><td>${e.weight}</td>`;
    tbody.appendChild(tr);
  });
}

function toggleOptimal(e) {
  const checked = e.target.checked;
  const optContainer = document.getElementById('optimal-result-container');
  if (checked) {
    optContainer.style.display = 'block';
    document.getElementById('text-optimal-weight').innerHTML = `Optimal total weight: ${state.optimalPath.weight}`;
    createOptimalCanvas(true);
  } else {
    optContainer.style.display = 'none';
    // destroy canvas to free memory
    if (state.optimalCanvasSketch) {
      state.optimalCanvasSketch.remove();
      state.optimalCanvasSketch = null;
    }
  }
}
</script>
</body>
</html>
