/* ---------- Global Variables ---------- */
let A = [1, 0];
let B = [0, 1];
let u = [1, 0];
let v = [0, 1];
let w = [0, 0];
let xScale = 80, yScale = 80;

/* ---------- Helper Functions ---------- */
function dot(a, b) { return a[0]*b[0] + a[1]*b[1]; }
function norm(a) { return Math.hypot(a[0], a[1]); }
function normalize(a) { const n = norm(a); return n===0 ? [0,0] : [a[0]/n, a[1]/n]; }
function scalarMult(a, s) { return [a[0]*s, a[1]*s]; }
function add(a, b) { return [a[0]+b[0], a[1]+b[1]]; }

/* ---------- Update Value Displays ---------- */
function updateDisplays() {
    const ids = ["au","bu","av","bv","aw","bw","x-scale","y-scale"];
    ids.forEach(id => {
        const el = document.getElementById(`slider-${id}`);
        const span = document.getElementById(`value-${id}`);
        span.textContent = Number(el.value).toFixed(2);
    });
}

/* ---------- Calculate Vectors from Slider Values ---------- */
function recalcVectors() {
    const au = +document.getElementById('slider-au').value;
    const bu = +document.getElementById('slider-bu').value;
    const av = +document.getElementById('slider-av').value;
    const bv = +document.getElementById('slider-bv').value;
    const aw = +document.getElementById('slider-aw').value;
    const bw = +document.getElementById('slider-bw').value;

    // Simplified vectors: simply use slider values as 2D components
    u = [au, bu];
    v = [av, bv];
    w = [aw, bw];

    // Points A and B are arbitrary linear combinations of the sliders
    A = [au + av + aw, bu + bv + bw];
    B = [-au + av - aw, -bu + bv - bw];

    // Update scales
    xScale = +document.getElementById('slider-x-scale').value;
    yScale = +document.getElementById('slider-y-scale').value;
}

/* ---------- Event Listeners for Sliders ---------- */
document.querySelectorAll('#control-panel input[type="range"]').forEach(slider => {
    slider.addEventListener('input', () => {
        updateDisplays();
        recalcVectors();
        redraw();
    });
});

/* ---------- p5.js Sketch ---------- */
let sketch = function(p) {
    p.setup = function() {
        const canvas = p.createCanvas(800, 600);
        canvas.parent('canvas-container');
        p.noLoop();
        recalcVectors();
        updateDisplays();
    };

    p.draw = function() {
