        p.translate(p.width/2, p.height/2);
        p.scale(xScale, -yScale); // Flip y-axis to have up positive

        // Draw axes
        const lineLength = 0.7; // in world units
        const axes = [
            {vec: u, color:'#0000CD', label:'u'},
            {vec: v, color:'#6495ED', label:'v'},
            {vec: w, color:'#0000CD', label:'w'}
        ];
        p.strokeWeight(3);
        axes.forEach(a => {
            const n = normalize(a.vec);
            const start = scalarMult(n, -lineLength);
            const end   = scalarMult(n,  lineLength);
            p.stroke(a.color);
            p.line(start[0], start[1], end[0], end[1]);
            const labelPos = scalarMult(n, lineLength + 0.05);
            p.fill(a.color);
            p.noStroke();
            p.textSize(16);
            p.text(a.label, labelPos[0], labelPos[1]);
        });

        // Draw points A, B, O
        const points = [
            {pos: A, color:'#FF0000', label:'A'},
            {pos: B, color:'#FFA500', label:'B'},
            {pos: [0,0], color:'#FF0000', label:'O'}
        ];
        points.forEach(pnt => {
            p.stroke(pnt.color);
            p.strokeWeight(2);
            p.fill(pnt.color);
            p.ellipse(pnt.pos[0], pnt.pos[1], 12, 12);
            p.fill(pnt.color);
            p.noStroke();
            p.textSize(16);
            p.text(pnt.label, pnt.pos[0] + 8, pnt.pos[1] - 8);
        });

        // Projections
        const normals = [u, v, w];
        const projPoints = [];
        [A, B].forEach((pt, idx) => {
            normals.forEach(n => {
                const nNorm = normalize(n);
                const projDist = dot(pt, nNorm);
                const proj = scalarMult(nNorm, projDist);
                projPoints.push({pt, proj, color:'#00FF00'});
            });
        });

        // Draw projection lines and points
        p.stroke('#808080');
        p.strokeWeight(1);
        projPoints.forEach(pp => {
            p.line(pp.pt[0], pp.pt[1], pp.proj[0], pp.proj[1]);
            p.stroke(pp.color);
            p.fill(pp.color);
            p.ellipse(pp.proj[0], pp.proj[1], 6, 6);
        });
    };
};

new p5(sketch);
</script>
