<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
<script>
(() => {
    // ---------- Global State ----------
    let t1 = 0, t2 = 0;
    let currentType = '2A2'; // default
    let currentDimension = '2D'; // default
    let renderer, scene, camera, controls;
    let activeMesh, wireframeMesh;
    const timePad = document.getElementById('time-pad');
    const handle = document.getElementById('time-handle');
    const vizContainer = document.getElementById('visualization-container');
    let padRect, padSize;

    // ---------- Utility ----------
    function debounce(fn, ms) {
        let timer;
        return (...args) => {
            clearTimeout(timer);
            timer = setTimeout(() => fn(...args), ms);
        };
    }

    // ---------- Three.js Setup ----------
    function initThree() {
        renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
        renderer.setSize(vizContainer.clientWidth, vizContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        vizContainer.appendChild(renderer.domElement);
        scene = new THREE.Scene();
        addLights();
        adjustCamera();
        animate();
    }

    function addLights() {
        const amb = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(amb);
        const dir = new THREE.DirectionalLight(0xffffff, 0.8);
        dir.position.set(5, 5, 10).normalize();
        scene.add(dir);
    }

    function adjustCamera() {
        const width = vizContainer.clientWidth;
        const height = vizContainer.clientHeight;
        const aspect = width / height;
        if (currentDimension === '2D') {
            const frustumSize = 5;
            camera = new THREE.OrthographicCamera(
                frustumSize * aspect / -2,
                frustumSize * aspect / 2,
                frustumSize / 2,
                frustumSize / -2,
                0.1,
                100
            );
            camera.position.set(0, 0, 5);
            camera.lookAt(0, 0, 0);
            if (controls) { controls.dispose(); controls = null; }
        } else {
            camera = new THREE.PerspectiveCamera(60, aspect, 0.1, 100);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;
        }
    }

    function animate() {
        requestAnimationFrame(animate);
        if (controls) controls.update();
        renderer.render(scene, camera);
    }

    // ---------- Parametric Functions ----------
    const Tfactor = 1.5; // scaling to compute T1/T2
    const funcs = {
        // 2D
        '2A1': (U, V) => new THREE.Vector3(U + T1, U * U + T2, 0),
        '2A2': (U, V) => new THREE.Vector3(2 * Math.pow(U,3) + T1, 3 * U * U + T2, 0),
        '2B2': (U, V) => new THREE.Vector3(U * U + T1, Math.pow(U,4) + T2, 0),
        // 3D
        '3A1p': (U,V) => {
            const x = U;
            const y = V;
            const z = 0.3*(U*U+V*V) + T1*U + T2*V;
            return new THREE.Vector3(x,y,z);
        },
        '3A1m': (U,V) => {
            const x = U;
            const y = V;
            const z = -0.3*(U*U+V*V) + T1*U + T2*V;
            return new THREE.Vector3(x,y,z);
        },
        '3A2': (U,V) => {
            const x = 2* Math.pow(U,3) + T1;
            const y = 3* U * U + T2;
            const z = V;
            return new THREE.Vector3(x,y,z);
        },
        '3A3': (U,V) => {
            const x = 3*Math.pow(U,4) + U*U*V;
            const y = -4*Math.pow(U,3) - 2*U*V + 2*T1;
            const z = V + 2*T2;
            return new THREE.Vector3(x,y,z);
        },
        '3B2p': (U,V) => new THREE.Vector3(U, V, (U*U + T1)*(U*U + T1) + V*V + T2),
        '3B2m': (U,V) => new THREE.Vector3(U, V, -(Math.pow(U,4) + 2*T1*U*U + V*V - T2)),
        '3B3': (U,V) => new THREE.Vector3(U, V, Math.pow(U,3) + T1*U + V*V + T2),
        '3C3p': (U,V) => new THREE.Vector3(U, V, Math.pow(U,3) - 3*U*V*V + 2*T1*(U*U+V*V) + 4*T2*U),
        '3C3m': (U,V) => new THREE.Vector3(U, V, Math.pow(U,3) + Math.pow(V,3) + 4*T1*U + 4*T2*V)
    };

    function mapU(v, domainMin, domainMax) {
        return domainMin + v * (domainMax - domainMin);
    }

    function generate3DGeometry(name) {
        const func = funcs[name];
        if (!func) return null;
        return new THREE.ParametricGeometry((u,v,target) => {
            const U = mapU(u, -2, 2);
            const V = mapU(v, -2, 2);
            const vec = func(U,V);
            target.set(vec.x, vec.y, vec.z);
        }, 50, 50);
    }

    function generate2DGeometry(name) {
        const func = funcs[name];
        if (!func) return null;
        const points = [];
        const steps = 200;
        const domain = name.startsWith('2A1') || name === '2B2' ? [-2,2] : [-1.5,1.5];
        for (let i=0;i<=steps;i++) {
            const t = i/steps;
            const U = mapU(t, domain[0], domain[1]);
            const vec = func(U,0);
            points.push(new THREE.Vector3(vec.x, vec.y, vec.z));
        }
        const geometry = new THREE.BufferGeometry().setFromPoints(points);
        return geometry;
    }

    function buildMesh() {
        cleanScene();
        const name = currentType;
        if (currentDimension === '2D') {
            const geom = generate2DGeometry(name);
            if (!geom) return;
            const mat = new THREE.LineBasicMaterial({ color: 0x4a69bd, linewidth: 2 });
            activeMesh = new THREE.Line(geom, mat);
            scene.add(activeMesh);
        } else {
            const geom = generate3DGeometry(name);
            if (!geom) return;
            const mat = new THREE.MeshPhongMaterial({ color: 0xffc34d, side: THREE.DoubleSide, shininess: 30 });
            activeMesh = new THREE.Mesh(geom, mat);
            scene.add(activeMesh);

            // Optional wireframe for selected types
            if (['3A3'].includes(name)) {
                const wireMat = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 1 });
                wireframeMesh = new THREE.LineSegments(new THREE.WireframeGeometry(geom), wireMat);
                scene.add(wireframeMesh);
            }
        }
    }

    function cleanScene() {
        if (activeMesh) { scene.remove(activeMesh); activeMesh.geometry.dispose(); activeMesh.material.dispose(); activeMesh = null; }
        if (wireframeMesh) { scene.remove(wireframeMesh); wireframeMesh.geometry.dispose(); wireframeMesh.material.dispose(); wireframeMesh = null; }
    }

    // ---------- Time Pad Interaction ----------
    let dragging = false;
    function updateHandlePosition(){
        const left = (t1 + 1)/2 * padSize;
        const top = (1 - t2)/2 * padSize;
        handle.style.left = left + 'px';
        handle.style.top = top + 'px';
    }

    function setTimeFromPos(x, y) {
        let l = x - padRect.left;
        let t = y - padRect.top;
        l = Math.max(0, Math.min(l, padSize));
        t = Math.max(0, Math.min(t, padSize));
        t1 = (l / padSize) * 2 - 1;
        t2 = 1 - (t / padSize) * 2;
        updateHandlePosition();
        buildMesh();
    }

    timePad.addEventListener('mousedown', e => {
        const rect = timePad.getBoundingClientRect();
        if (e.target === handle) {
            dragging = true;
        } else {
            setTimeFromPos(e.clientX, e.clientY);
        }
    });

    document.addEventListener('mousemove', e=>{
        if (!dragging) return;
        setTimeFromPos(e.clientX, e.clientY);
    });

    document.addEventListener('mouseup', () => dragging = false);

    // ---------- Button Interaction ----------
    function setActiveButton(btn) {
        document.querySelectorAll('#control-panel button').forEach(b => b.classList.remove('active'));
        btn.classList.add('active');
    }

    function handleButtonClick(e) {
        const btn = e.target;
        setActiveButton(btn);
        const id = btn.id; // e.g., btn-2A2
        const type = id.replace('btn-','');
        currentType = type;
        currentDimension = type.startsWith('2') ? '2D' : '3D';
        buildMesh();
    }

    document.querySelectorAll('#control-panel button').forEach(btn => btn.addEventListener('click', handleButtonClick));

    // ---------- Initialization ----------
    function initDefaultSelection() {
        const defaultBtn = document.getElementById('btn-2A2');
        setActiveButton(defaultBtn);
    }

    function resizeHandler(){
        const w = vizContainer.clientWidth;
        const h = vizContainer.clientHeight;
        renderer.setSize(w,h);
        adjustCamera();
    }

    window.addEventListener('resize', debounce(resizeHandler, 100));

    function init() {
        initThree();
        initDefaultSelection();
        padRect = timePad.getBoundingClientRect();
        padSize = padRect.width;
        updateHandlePosition();
        buildMesh();
    }

    init();
})();
</script>
</body>
</html>
