<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Reactor Simulation Demo</title>
<script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.3/p5.min.js"></script>
<style>
  body{font-family:Arial,Helvetica,sans-serif;margin:10px;background:#fff;}
  #control-panel{display:flex;justify-content:space-between;padding:10px;background:#fafafa;border:1px solid #ddd;}
  #left-controls,#right-controls{display:flex;flex-direction:column;}
  #right-controls > div{margin-top:8px;}
  .slider-group{display:flex;flex-direction:column;}
  .slider-container{display:flex;align-items:center;gap:8px; margin-top:4px;}
  .slider-container input[type="range"]{flex:1;}
  .slider-container input[type="number"]{width:60px;}
  #plot-area{position:relative;width:100%;height:400px;border:1px solid #ccc;background:#f0f0f0;margin-top:10px;}
  #legend{position:absolute;top:8px;left:50%;transform:translateX(-50%);display:flex;gap:20px;pointer-events:none;}
  .legend-item{display:flex;align-items:center;gap:5px;}
  .legend-line{height:2px;width:20px;}
</style>
</head>
<body>
<div id="control-panel">
  <div id="left-controls">
    <select id="select-plot">
      <option value="semibatch">semibatch</option>
      <option value="batch">batch</option>
      <option value="selectivity">selectivity</option>
    </select>
    <div id="feed-cycler" style="margin-top:8px;">
      <span id="cycler-feed-label">feed A to B</span>
      <button id="btn-feed-down">▼</button>
      <button id="btn-feed-up">▲</button>
    </div>
  </div>
  <div id="right-controls">
    <div class="slider-group">
      <label for="slider-feed-rate">feed rate</label>
      <div class="slider-container">
        <input type="range" id="slider-feed-rate" min="10" max="200" step="1" value="10">
        <input type="number" id="input-feed-rate" min="10" max="200" step="1" value="10">
      </div>
    </div>
    <div class="slider-group">
      <label for="slider-ua">UA</label>
      <div class="slider-container">
        <input type="range" id="slider-ua" min="5000" max="30000" step="100" value="15000">
        <input type="number" id="input-ua" min="5000" max="30000" step="100" value="15000">
      </div>
    </div>
    <div class="slider-group">
      <label for="slider-dh1">\(\Delta H_1\)</label>
      <div class="slider-container">
        <input type="range" id="slider-dh1" min="500" max="5000" step="50" value="1000">
        <input type="number" id="input-dh1" min="500" max="5000" step="50" value="1000">
      </div>
    </div>
    <div class="slider-group">
      <label for="slider-dh2">\(\Delta H_2\)</label>
      <div class="slider-container">
        <input type="range" id="slider-dh2" min="500" max="5000" step="50" value="3000">
        <input type="number" id="input-dh2" min="500" max="5000" step="50" value="3000">
      </div>
    </div>
  </div>
</div>
<div id="plot-area">
  <canvas id="plot-canvas"></canvas>
  <div id="legend"></div>
</div>
<script>
/* ---------- CONSTANTS & GLOBALS ---------- */
const V = 100;             // L
const T_INITIAL = 330;     // K
const R = 8.314;           // J/(mol·K)
const Cp_total = 150000;   // J/K
const k1_0 = 3e4;          // L²/(mol²·h)
const E1 = 30000;          // J/mol
const k2_0 = 1e5;          // L/(mol·h)
const E2 = 40000;          // J/mol

let simulationData = { t: [], na: [], nb: [], nd: [], nu: [], sr: [] };
let currentPlotType = 'semibatch';
let feedConfigIndex = 0;
const feedConfigs = ['feed A to B', 'feed B to A'];

/* ---------- HELPERS ---------- */
// Sync range and number inputs
function syncRangeNumber(rangeId, numberId) {
  const range = document.getElementById(rangeId);
  const number = document.getElementById(numberId);
  range.addEventListener('input', () => {
    number.value = range.value;
    computeAndDraw();
  });
  number.addEventListener('change', () => {
    let val = parseFloat(number.value);
    if (isNaN(val)) val = parseInt(range.min);
    val = Math.min(Math.max(val, parseFloat(range.min)), parseFloat(range.max));
    range.value = val;
    number.value = val;
    computeAndDraw();
  });
}

// Feed cycler
function initFeedCycler() {
  document.getElementById('btn-feed-up').addEventListener('click', () => {
    feedConfigIndex = (feedConfigIndex + 1) % feedConfigs.length;
    document.getElementById('cycler-feed-label').textContent = feedConfigs[feedConfigIndex];
    computeAndDraw();
  });
  document.getElementById('btn-feed-down').addEventListener('click', () => {
    feedConfigIndex = (feedConfigIndex - 1 + feedConfigs.length) % feedConfigs.length;
    document.getElementById('cycler-feed-label').textContent = feedConfigs[feedConfigIndex];
    computeAndDraw();
  });
}

/* ---------- ODE & SIMULATION ---------- */
function derivative(Y, t, type, params) {
  let [Na, Nb, Nd, Nu, T] = Y;
  const k1 = k1_0 * Math.exp(-E1 / (R * T));
  const k2 = k2_0 * Math.exp(-E2 / (R * T));
  const r1 = k1 * (Na / V) * Math.pow(Nb / V, 2);
  const r2 = k2 * (Na / V) * (Nb / V);
  let dNa, dNb, dNd, dNu;
  if (type === 'batch') {
    dNa = -(r1 + r2) * V;
    dNb = -(r1 + r2) * V;
    dNd = r1 * V;
    dNu = r2 * V;
  } else if (type === 'semibatchAtoB') {
    const F_A0 = (t < params.t_f) ? params.feedRate : 0;
    dNa = F_A0 - (r1 + r2) * V;
    dNb = -(r1 + r2) * V;
    dNd = r1 * V;
    dNu = r2 * V;
  } else if (type === 'semibatchBtoA') {
    const F_B0 = (t < params.t_f) ? params.feedRate : 0;
    dNa = -(r1 + r2) * V;
    dNb = F_B0 - (r1 + r2) * V;
    dNd = r1 * V;
    dNu = r2 * V;
  }
  const dT = (params.UA * (T_INITIAL - T) + (-params.DH1 * r1 - params.DH2 * r2) * V) / Cp_total;
  return [dNa, dNb, dNd, dNu, dT];
}

function rk4Step(Y, t, dt, type, params) {
  const k1 = derivative(Y, t, type, params);
  const Yk2 = Y.map((y, i) => y + k1[i] * dt / 2);
  const k2 = derivative(Yk2, t + dt / 2, type, params);
  const Yk3 = Y.map((y, i) => y + k2[i] * dt / 2);
  const k3 = derivative(Yk3, t + dt / 2, type, params);
  const Yk4 = Y.map((y, i) => y + k3[i] * dt);
  const k4 = derivative(Yk4, t + dt, type, params);
  const Ynext = Y.map((y, i) => y + dt * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]) / 6);
  return [Ynext, t + dt];
}

function simulateBatch(params) {
  const dt = 0.01;
  const steps = Math.round(2.0 / dt);
  let Y = [100, 100, 0, 0, T_INITIAL];
  let t = 0;
  const tArr = [], naArr = [], nbArr = [], ndArr = [], nuArr = [];
  for (let i = 0; i <= steps; i++) {
    tArr.push(t);
    naArr.push(Y[0]); nbArr.push(Y[1]); ndArr.push(Y[2]); nuArr.push(Y[3]);
    if (i === steps) break;
    const [Yn, tn] = rk4Step(Y, t, dt, 'batch', params);
    Y = Yn; t = tn;
  }
  return { t: tArr, na: naArr, nb: nbArr, nd: ndArr, nu: nuArr, sr: [] };
}

function simulateSemibatch(feedCfg, params) {
  const dt = 0.01;
  const steps = Math.round(2.0 / dt);
  let Y, type;
  if (feedCfg === 'feed A to B') {
    Y = [0, 100, 0, 0, T_INITIAL];
    type = 'semibatchAtoB';
  } else {
    Y = [100, 0, 0, 0, T_INITIAL];
    type = 'semibatchBtoA';
  }
  let t = 0;
  const tArr = [], naArr = [], nbArr = [], ndArr = [], nuArr = [];
  for (let i = 0; i <= steps; i++) {
    tArr.push(t);
    naArr.push(Y[0]); nbArr.push(Y[1]); ndArr.push(Y[2]); nuArr.push(Y[3]);
    if (i === steps) break;
    const [Yn, tn] = rk4Step(Y, t, dt, type, params);
    Y = Yn; t = tn;
  }
  return { t: tArr, na: naArr, nb: nbArr, nd: ndArr, nu: nuArr, sr: [] };
}

function computeSelectivityRatio(batch, semi) {
  const tArr = [], srArr = [];
  for (let i = 1; i < batch.t.length; i++) {
    const ndB = batch.nd[i], nuB = batch.nu[i];
    const ndS = semi.nd[i], nuS = semi.nu[i];
    if (nuB === 0 || nuS === 0) continue;
    const Sbatch = ndB / nuB;
    const Ssemi = ndS / nuS;
    srArr.push(Ssemi / Sbatch);
    tArr.push(batch.t[i]);
  }
  return { t: tArr, na: [], nb: [], nd: [], nu: [], sr: srArr };
}

/* ---------- LEGEND ---------- */
function updateLegend() {
  const legend = document.getElementById('legend');
  legend.innerHTML = '';
  const curves = [];
  if (currentPlotType === 'selectivity') {
    curves.push({ label: '\\(S_{d/u}\\)', color: '#800080', dashed: false });
  } else {
    curves.push({ label: '\\(N_a\\)', color: '#2ca02c', dashed: true });
    curves.push({ label: '\\(N_b\\)', color: '#000000', dashed: true });
    curves.push({ label: '\\(N_d\\)', color: '#1f77b4', dashed: false });
    curves.push({ label: '\\(N_u\\)', color: '#d62728', dashed: false });
  }
  curves.forEach(cur => {
    const item = document.createElement('div');
    item.className = 'legend-item';
    const line = document.createElement('div');
    line.className = 'legend-line';
    line.style.borderBottom = cur.dashed ? `2px dashed ${cur.color}` : `2px solid ${cur.color}`;
    const txt = document.createElement('div');
    txt.innerHTML = cur.label;
    item.appendChild(line);
    item.appendChild(txt);
    legend.appendChild(item);
  });
  MathJax.typesetPromise();
}

/* ---------- P5 DRAW ---------- */
let p5instance;
const sketch = function (s) {
  s.setup = function () {
    s.createCanvas(800, 400);
    s.noLoop();
  };
  s.draw = function () {
    drawPlot(s);
  };
};
p5instance = new p5(sketch, 'plot-canvas');

function drawPlot(s) {
  const w = s.width, h = s.height;
  s.background('#F0F0F0');
  const margin = 50;
  const xMin = 0, xMax = 2.0;
  let yMin, yMax;
  if (currentPlotType === 'selectivity') {
    const arr = simulationData.sr;
    yMin = Math.min(...arr);
    yMax = Math.max(...arr);
    const pad = 0.1 * (yMax - yMin);
    yMin -= pad;
    yMax += pad;
  } else {
    yMin = 0; yMax = 100;
  }

  /* Grid */
  s.stroke('#E0E0E0'); s.strokeWeight(1);
  for (let x = xMin; x <= xMax; x += 0.5) {
    const sx = s.map(x, xMin, xMax, margin, w - margin);
    s.line(sx, margin, sx, h - margin);
  }
  const yStep = (yMax - yMin) / 5;
  for (let y = yMin; y <= yMax; y += yStep) {
    const sy = s.map(y, yMin, yMax, h - margin, margin);
    s.line(margin, sy, w - margin, sy);
  }

  /* Axes */
  s.stroke('#000'); s.strokeWeight(2);
  s.line(margin, margin, margin, h - margin); // Y axis
  s.line(margin, h - margin, w - margin, h - margin); // X axis

  /* Labels */
  s.fill('#000'); s.noStroke(); s.textSize(14);
  s.text('time (h)', (w - margin + margin) / 2, h - margin + 25);
  const yLabel = currentPlotType === 'selectivity' ? 'selectivity ratio' : 'moles (mol)';
  s.push();
  s.translate(margin - 30, (h - margin + margin) / 2);
  s.rotate(-Math.PI / 2);
  s.text(yLabel, 0, 0);
  s.pop();

  /* Data curves */
  const drawSeries = (yVals, color, dashed) => {
    if (yVals.length === 0) return;
    s.stroke(color); s.noFill(); s.strokeWeight(2);
    s.drawingContext.setLineDash(dashed ? [5, 5] : []);
    s.beginShape();
    for (let i = 0; i < yVals.length; i++) {
      const x = simulationData.t[i];
      const y = yVals[i];
      const sx = s.map(x, xMin, xMax, margin, w - margin);
      const sy = s.map(y, yMin, yMax, h - margin, margin);
      s.vertex(sx, sy);
    }
    s.endShape();
    s.drawingContext.setLineDash([]);
  };

  if (currentPlotType === 'selectivity') {
    drawSeries(simulationData.sr, '#800080', false);
  } else {
    drawSeries(simulationData.na, '#2ca02c', true);
    drawSeries(simulationData.nb, '#000000', true);
    drawSeries(simulationData.nd, '#1f77b4', false);
    drawSeries(simulationData.nu, '#d62728', false);
  }
}

/* ---------- MAIN INITIALIZATION ---------- */
document.addEventListener('DOMContentLoaded', () => {
  syncRangeNumber('slider-feed-rate', 'input-feed-rate');
  syncRangeNumber('slider-ua', 'input-ua');
  syncRangeNumber('slider-dh1', 'input-dh1');
  syncRangeNumber('slider-dh2', 'input-dh2');
  initFeedCycler();
  document.getElementById('select-plot').addEventListener('change', computeAndDraw);
  computeAndDraw(); // Initial drawing
});

/* ---------- COMPUTE & DRAW ---------- */
function computeAndDraw() {
  currentPlotType = document.getElementById('select-plot').value;
  const feedRate = parseFloat(document.getElementById('slider-feed-rate').value);
  const UA = parseFloat(document.getElementById('slider-ua').value);
  const DH1 = parseFloat(document.getElementById('slider-dh1').value);
  const DH2 = parseFloat(document.getElementById('slider-dh2').value);
  const t_f = 100 / feedRate;
  const params = { UA, DH1, DH2, feedRate, t_f };

  if (currentPlotType === 'batch') {
    simulationData = simulateBatch(params);
  } else if (currentPlotType === 'semibatch') {
    simulationData = simulateSemibatch(feedConfigs[feedConfigIndex], params);
  } else if (currentPlotType === 'selectivity') {
    const batchData = simulateBatch(params);
    const semiData = simulateSemibatch(feedConfigs[feedConfigIndex], params);
    simulationData = computeSelectivityRatio(batchData, semiData);
  }
  updateLegend();
  p5instance.redraw();
}
</script>
</body>
</html>
