```html
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Tree Exploration Simulation</title>
<style>
  body{margin:0;font-family:sans-serif;}
  #main-container{display:flex;height:100vh;}
  #control-panel{width:260px;padding:10px;background:#f0f0f0;border-right:1px solid #ccc;}
  #control-panel h4{margin:10px 0 5px;}
  #control-panel button{margin:2px 0;width:100%;padding:5px;}
  #control-panel button.active{background:#007bff;color:#fff;}
  #control-panel button:disabled{background:#ddd;color:#999;}
  #canvas-container{flex:1;position:relative;}
  #status-text{position:absolute;top:10px;left:50%;transform:translateX(-50%);background:rgba(255,255,255,0.8);padding:5px 10px;border-radius:5px;}
  #btn-reset{position:absolute;top:10px;right:10px;background:#28a745;color:#fff;border:none;padding:10px;border-radius:50%;cursor:pointer;}
  #btn-reset:hover{background:#218838;}
</style>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <h4>Tree type</h4>
    <div id="tree-type-buttons">
      <button id="btn-rt" class="active">Random Tree</button>
      <button id="btn-rrt">RRT</button>
      <button id="btn-rrt-star">RRT*</button>
    </div>

    <h4>obstacle type</h4>
    <select id="select-obstacle">
      <option value="narrow-passage" selected>narrow passage</option>
      <option value="empty">empty</option>
      <option value="random-rects">random rects</option>
    </select>

    <h4>number of nodes to add:</h4>
    <div id="add-nodes-buttons">
      <button id="btn-add-1">1</button>
      <button id="btn-add-10">10</button>
      <button id="btn-add-100">100</button>
      <button id="btn-add-200">200</button>
      <button id="btn-add-500">500</button>
    </div>

    <h4>exploration bias</h4>
    <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
    <span id="bias-value-label">0</span>

    <h4>goal radius</h4>
    <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
    <span id="radius-value-label">1</span>
  </div>

  <div id="canvas-container">
    <p id="status-text">1 node, goal not yet reached</p>
    <button id="btn-reset">+</button>
  </div>
</div>

<script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>
<script>
/** Global simulation state */
let sim = {
  width: 500,
  height: 500,
  start: {x:50, y:250},
  goal: {x:450, y:450},
  goalRadius:1,
  bias:0,
  treeType:"RT",
  nodes: [], // each node: {x,y,parentIndex}
  goalReached:false,
  goalNodeIndex:null,
  pathIndices:[],
  obstacles:[],
  draggingGoal:false
};

const canvasContainer = document.getElementById('canvas-container');

/** Utility functions */
function distance(a,b){
  return Math.hypot(a.x-b.x,a.y-b.y);
}
function lerp(a,b,t){return a+(b-a)*t;}
function clamp(value, min, max){return Math.max(min,Math.min(max,value));}

/** Reset simulation to initial state */
function resetSimulation(){
  sim.nodes = [{x:sim.start.x, y:sim.start.y, parentIndex:null}];
  sim.goal = {x:sim.start.x+400, y:sim.start.y+200}; // reset to defaults
  sim.goalRadius = 1;
  sim.goalReached = false;
  sim.goalNodeIndex = null;
  sim.pathIndices = [];
  sim.draggingGoal = false;
  updateStatus();
  redraw();
}

/** Update status text */
function updateStatus(){
  const nodeCount = sim.nodes.length;
  const statusEl = document.getElementById('status-text');
  if(sim.goalReached){
    const pathLen = computePathLength();
    statusEl.textContent = `${nodeCount} nodes, path length ${pathLen.toFixed(2)}`;
  }else{
    statusEl.textContent = `${nodeCount} nodes, goal not yet reached`;
  }
}

/** Compute length along current path */
function computePathLength(){
  const inds = sim.pathIndices;
  let len = 0;
  for(let i=1;i<inds.length;i++){
    len += distance(sim.nodes[inds[i-1]], sim.nodes[inds[i]]);
  }
  return len;
}

/** Reconstruct path after reaching goal */
function updatePath(){
  if(!sim.goalReached) {sim.pathIndices=[];return;}
  let idx = sim.goalNodeIndex;
  const path = [];
  while(idx!=null){
    path.unshift(idx);
    idx = sim.nodes[idx].parentIndex;
  }
  sim.pathIndices = path;
}

/** Add nodes according to current tree algorithm */
function addNodes(count){
  for(let i=0; i<count; i++){
    // sample point
    let sample;
    if(sim.bias>0 && Math.random()<sim.bias){
      sample = {x:sim.goal.x, y:sim.goal.y};
    }else{
      sample = {x:Math.random()*sim.width, y:Math.random()*sim.height};
    }
    // find nearest node
    let nearestIdx=0;
    let minDist=Infinity;
    for(let j=0;j<sim.nodes.length;j++){
      const d = distance(sample, sim.nodes[j]);
      if(d<minDist){minDist=d;nearestIdx=j;}
    }
    const newNode = {x:sample.x, y:sample.y, parentIndex:nearestIdx};
    sim.nodes.push(newNode);
    // Retro-branch support for RRT*: rewire if closer
    if(sim.treeType==="RRT*" && sim.nodes.length>2){
      let bestIdx = nearestIdx;
      let bestDist = distance(sample, sim.nodes[nearestIdx]);
      // search neighbors within radius 5*goalRadius
      const radius = 5*sim.goalRadius;
      for(let k=0;k<sim.nodes.length-1;k++){
        const nd = sim.nodes[k];
        const d = distance(sample, nd);
        if(d<radius && d + distance(nd, sim.nodes[nearestIdx]) < bestDist){
          bestIdx = k;
          bestDist = d;
        }
      }
      newNode.parentIndex = bestIdx;
    }

    // check goal reached
    if(!sim.goalReached && distance(sample,sim.goal)<=sim.goalRadius){
      sim.goalReached = true;
      sim.goalNodeIndex = sim.nodes.length-1;
      updatePath();
    }
  }
  updateStatus();
  redraw();
}

/** Drawing obstacles */
function drawObstacles(){
  if(sim.obstacles.length===0) return;
  for(const r of sim.obstacles){
    s.noFill();
    s.stroke('blue');
    s.strokeWeight(8);
    s.rect(r.x, r.y, r.w, r.h);
  }
}

/** p5.js sketch */
const sketch = s => {
  s.setup = () => {
    s.createCanvas(sim.width, sim.height);
    s.noLoop();
    const selectObs = document.getElementById('select-obstacle');
    selectObs.addEventListener('change', () => {
      resetSimulation();
      setObstacles();
    });
    // Tree type buttons
    const btnRt=document.getElementById('btn-rt');
    const btnRrt=document.getElementById('btn-rrt');
    const btnRrtStar=document.getElementById('btn-rrt-star');
    [btnRt, btnRrt, btnRrtStar].forEach(btn=>btn.addEventListener('click',()=>{setTreeType(btn.id);} ));
    // Add nodes buttons
    const addBtns = document.querySelectorAll('#add-nodes-buttons button');
    addBtns.forEach(b=>b.addEventListener('click',()=>{addNodes(parseInt(b.id.split('-').pop()));}));
    // sliders
    const biasSlider=document.getElementById('slider');
    const radiusSlider=document.getElementById('slider-radius');
    const biasLabel=document.getElementById('bias-value-label');
    const radiusLabel=document.getElementById('radius-value-label');
    biasSlider.addEventListener('input',()=>{sim.bias=+biasSlider.value; biasLabel.textContent=sim.bias.toFixed(2);});
    radiusSlider.addEventListener('input',()=>{sim.goalRadius=+radiusSlider.value; radiusLabel.textContent=sim.goalRadius.toFixed(1); redraw();});
    // reset button
    document.getElementById('btn-reset').addEventListener('click',()=>{resetSimulation();});
    // initial set
    setObstacles();
    redraw();
  };
  s.draw = () => {
    s.background(255);
    // obstacles
    drawObstacles();

    // goal circle
    s.push();
    s.noFill();
    s.fill(255,255,0,100);
    s.ellipse(sim.goal.x, sim.goal.y, 2*sim.goalRadius, 2*sim.goalRadius);
    s.stroke(0);
    s.line(sim.goal.x - 10, sim.goal.y, sim.goal.x + 10, sim.goal.y);
    s.line(sim.goal.x, sim.goal.y - 10, sim.goal.x, sim.goal.y + 10);
    s.pop();

    // start node
    if(!sim.nodes[0]) return;
    s.noStroke();
    s.fill('red');
    s.circle(sim.nodes[0].x, sim.nodes[0].y, 10);
    // nodes
    s.fill('red');
    for(const n of sim.nodes){
      s.circle(n.x,n.y,5);
    }

    // edges for RRT*
    if(sim.treeType==="RRT*"){
      s.stroke(0);
      s.strokeWeight(0.5);
      for(let i=1;i<sim.nodes.length;i++){
        const p=sim.nodes[i];
        if(p.parentIndex!=null){
          const parent=sim.nodes[p.parentIndex];
          s.line(p.x,p.y,parent.x,parent.y);
        }
      }
    }

    // final path
    if(sim.goalReached){
      s.stroke('green');
      s.strokeWeight(4);
      for(let i=1;i<sim.pathIndices.length;i++){
        const a=sim.nodes[sim.pathIndices[i-1]];
        const b=sim.nodes[sim.pathIndices[i]];
        s.line(a.x,a.y,b.x,b.y);
      }
    }
  };

  // mouse interaction for goal dragging
  s.mousePressed = () => {
    const d = distance({x:mouseX, y:mouseY}, sim.goal);
    if(d <= sim.goalRadius){
      sim.draggingGoal = true;
    }
  };
  s.mouseDragged = () => {
    if(sim.draggingGoal){
      sim.goal.x = clamp(mouseX, 0, sim.width);
      sim.goal.y = clamp(mouseY, 0, sim.height);
      if(sim.goalReached){
        // check if goal still reached
        if(distance(sim.nodes[sim.goalNodeIndex],sim.goal)>sim.goalRadius){
          sim.goalReached = false;
          sim.goalNodeIndex = null;
          sim.pathIndices=[];
        }else{
          updatePath();
        }
      }
    }
  };
  s.mouseReleased = () => {
    if(sim.draggingGoal){
      sim.draggingGoal = false;
      // reset simulation after moving goal
      resetSimulation();
    }
  };
};

/** Manage obstacle sets */
function setObstacles(){
  sim.obstacles=[];
  const type=document.getElementById('select-obstacle').value;
  if(type==="empty"){return;}
  if(type==="narrow-passage"){
    // example: big rectangle border with gaps
    sim.obstacles.push({x:0,y:0,w:sim.width,h:sim.height}); // border
    // two vertical walls
    sim.obstacles.push({x:200,y:0,w:8,h:200});
    sim.obstacles.push({x:300,y:300,w:8,h:200});
  }else if(type==="random-rects"){
    for(let i=0;i<10;i++){
      const w=clamp(Math.random()*60,10,100);
      const h=clamp(Math.random()*60,10,100);
      const x=Math.random()*(sim.width-w);
      const y=Math.random()*(sim.height-h);
      sim.obstacles.push({x,y,w,h});
    }
  }
  redraw();
}

/** Manage tree type selection */
function setTreeType(id){
  const btnRt=document.getElementById('btn-rt');
  const btnRrt=document.getElementById('btn-rrt');
  const btnRrtStar=document.getElementById('btn-rrt-star');
  [btnRt,btnRrt,btnRrtStar].forEach(b=>b.classList.remove('active'));
  document.getElementById(id).classList.add('active');
  sim.treeType=id.split('-')[1]; // rt, rrt, rrt-star
  const biasSlider=document.getElementById('slider-bias');
  if(sim.treeType==="RT"){
    biasSlider.disabled=true;
  }else{
    biasSlider.disabled=false;
  }
  resetSimulation();
}

/** Initial setup */
resetSimulation();
new p5(sketch, canvasContainer);
</script>
</body>
</html>
