  <div id="visualization-container" style="flex:1; position:relative;">
    <canvas id="three-canvas"></canvas>
  </div>

  <!-- External libraries -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
  <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

  <script>
    // --- Initialization ----------------------------------------------------
    const canvas = document.getElementById('three-canvas');
    const renderer = new THREE.WebGLRenderer({canvas: canvas, antialias: true});
.setPixelRatio(window.devicePixelRatio);
    renderer.setClearColor(0xf0f0f0);

    const scene = new THREE.Scene();

    const camera = new THREE.PerspectiveCamera(60, canvas.clientWidth / canvas.clientHeight, 0.1, 100);
    camera.position.set(4, 4, 4);
    camera.lookAt(0, 1, 0);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.target.set(0, 1, 0);
    controls.update();

    // Lights
    scene.add(new THREE.AmbientLight(0x666666));
    const dirLight = new THREE.DirectionalLight(0xffffff, 1.0);
    dirLight.position.set(5,5,5);
    scene.add(dirLight);

    // Axes helper
    const axes = new THREE.AxesHelper(4);
    scene.add(axes);

    // Potential surface
    const surfGeom = new THREE.ParametricGeometry((u, v, target) => {
      const rho = 0.4 + u * 2.1;
      const theta = v * 2 * Math.PI;
      const x = rho * Math.cos(theta);
      const y = rho * rho + 1/(rho*rho);
      const z = -rho * Math.sin(theta);
      target.set(x, y, z);
    }, 80, 80);
    const surfMat = new THREE.MeshStandardMaterial({
      color: 0xE69943,
      metalness: 0.4,
      roughness: 0.5,
      side: THREE.DoubleSide
    });
    const surfMesh = new THREE.Mesh(surfGeom, surfMat);
    scene.add(surfMesh);

    // Central cylinder
    const cylGeom = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
    const cylMat = new THREE.MeshStandardMaterial({
      color: 0xcccccc,
      metalness: 0.1,
      roughness: 0.9
    });
    const cylMesh = new THREE.Mesh(cylGeom, cylMat);
    cylMesh.position.set(0, 1.25, 0);
    scene.add(cylMesh);

    // --- Resize handling ---------------------------------------------------
    function onWindowResize() {
      const width = canvas.clientWidth;
      const height = canvas.clientHeight;
      renderer.setSize(width, height);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    }
    window.addEventListener('resize', onWindowResize);
    onWindowResize();

    // --- Interaction logic -------------------------------------------------
    const sliders = {
      t: document.getElementById('slider-t'),
      rho0: document.getElementById('slider-rho0'),
      zeta0: document.getElementById('slider-zeta0'),
      e0: document.getElementById('slider-e0'),
      v: document.getElementById('slider-v'),
      lambda: document.getElementById('slider-lambda')
    };

    const numbers = {
      t: document.getElementById('input-t'),
      rho0: document.getElementById('input-rho0'),
      zeta0: document.getElementById('input-zeta0'),
      e0: document.getElementById('input-e0'),
      v: document.getElementById('input-v'),
      lambda: document.getElementById('input-lambda')
    };

    // Two-way binding
    Object.keys(sliders).forEach(key => {
      sliders[key].addEventListener('input', () => {
        numbers[key].value = sliders[key].value;
        updateVisualization();
      });
      numbers[key].addEventListener('input', () => {
        sliders[key].value = numbers[key].value;
        updateVisualization();
      });
    });

    // --- RK4 Integration & Path rendering ---------------------------------
    function updateVisualization() {
      // Read parameters
      const t = parseFloat(sliders.t.value);
      const rho0 = parseFloat(sliders.rho0.value);
      const zeta0 = parseFloat(sliders.zeta0.value);
      const e0 = parseFloat(sliders.e0.value);
      const nu = parseFloat(sliders.v.value);  // interpreted as v in the spec
      const lambda = parseFloat(sliders.lambda.value);

      const ds = 0.05;
      const steps = Math.ceil(t / ds);
      let s = 0;
      let Y = [rho0, 0, 0]; // [rho, drho/ds, phi]

      const points = [];

      function deriv(state) {
        const rho = state[0];
        const drho = state[1];
        const phi = state[2];
        const d2rho = -(2 * rho - 2 / Math.pow(rho, 3)) - 4 * Math.pow(lambda, 2) * rho + 4 * e0 / Math.pow(rho, 3);
        const dphi = 2 * (e0 / Math.pow(rho, 2) - lambda);
        return [drho, d2rho, dphi];
      }

      for (let i = 0; i <= steps; i++) {
        const rho = Y[0];
        const phi = Y[2];

        // Cartesian coordinates (mapping y->z, z->-y)
        const x = rho * Math.cos(phi);
        const y = rho * Math.sin(phi);
        const u_rho = rho * rho + 1 / (rho * rho);
        const sqrt_arg = e0 - u_rho;
        const z = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.sqrt(2) * nu * s + zeta0);
        points.push(new THREE.Vector3(x, z,y));

        // RK4
        const k1 = deriv(Y);
        const k2State = Y.map((v, idx) => v + 0.5 * ds * k1[idx]);
        const k2 = deriv(k2State);
        const k3State = Y.map((v, idx) => v + 0.5 * ds * k2[idx]);
        const k3 = deriv(k3State);
        const k4State = Y.map((v, idx) => v + ds * k3[idx]);
        const k4 = deriv(k4State);

        Y = Y.map((v, idx) => v + (ds / 6) * (k1[idx] + 2 * k2[idx] + 2 * k3[idx] + k4[idx]));
        s += ds;
      }

      // Remove old path if present
      const oldPath = scene.getObjectByName('ion-path');
      if (oldPath) {
        scene.remove(oldPath);
        if (oldPath.geometry) oldPath.geometry.dispose();
        if (oldPath.material) oldPath.material.dispose();
      }

      // Create new tube
      const curve = new THREE.CatmullRomCurve3(points, true);
      const tubeGeom = new THREE.TubeGeometry(curve, 512, 0.04, 8, true);
      const tubeMat = new THREE.MeshBasicMaterial({ color: 0x61AEEF, transparent: true, opacity: 1 });
      const tube = new THREE.Mesh(tubeGeom, tubeMat);
      tube.name = 'ion-path';
      scene.add(tube);
    }

    // Initial rendering
    updateVisualization();

    // --- MathJax rendering -------------------------------------------------
    MathJax.typesetPromise();

    // --- Animation loop ----------------------------------------------------
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();
  </script>
