  #control-panel output {margin-left:8px;}
  #potential-energy-plot-container {height:400px;padding:10px;}
  #density-plots-container {display:flex;justify-content:space-between;padding:10px;}
  #bonding-plot-container,#antibonding-plot-container {width:48%;height:400px;}
  @media (max-width:768px){
    #density-plots-container {flex-direction:column;align-items:center;}
    #bonding-plot-container,#antibonding-plot-container {width:90%;margin-bottom:20px;}
  }
</style>
</head>

<body>
<div id="app-container">
  <!-- Control Panel -->
  <div id="control-panel">
    <label for="slider-r">R (Å)</label>
    <input type="range" id="slider-r" min="0.001" max="4.000" step="0.001" value="0.001">
    <output id="slider-r-value">0.001</output>
  </div>

  <!-- Potential Energy Plot -->
  <div id="potential-energy-plot-container"></div>

  <!-- Electron Density Plots -->
  <div id="density-plots-container">
    <div id="bonding-plot-container"></div>
    <div id="antibonding-plot-container"></div>
  </div>
</div>

<script>
'use strict';

document.addEventListener('DOMContentLoaded', () => {
  const slider = document.getElementById('slider-r');
  const output = document.getElementById('slider-r-value');

  // Initial Plotting
  generatePotentialEnergyPlot();
  updateSurfaces(parseFloat(slider.value) / 0.529177);

  // Event Listener
  slider.addEventListener('input', () => {
    const rAng = parseFloat(slider.value);
    output.value = rAng.toFixed(3);
    const rAu = rAng / 0.529177;
    updateSurfaces(rAu);
  });

  // Generate Potential Energy Plot
  function generatePotentialEnergyPlot() {
    const Rmax = 15.0, Rmin = 0.2, step = 0.05;
    const R_auss = [], E_b = [], E_a = [];
    for (let R = Rmin; R <= Rmax; R += step) {
      const Sbox = (1 + R + R * R / 3) * Math.exp(-R);
      const J = (1 / R) - (1 + 1 / R) * Math.exp(-2 * R);
      const K = (1 + R) * Math.exp(-R);
      const Eb = (J + K) / (1 + Sbox);
      const Ea = (J - K) / (1 - Sbox);
      R_auss.push(R * 0.529177); // R in Å
      E_b.push(Eb);
      E_a.push(Ea);
    }

    const traceBond = {
      x: R_auss,
      y: E_b,
      mode: 'lines',
      type: 'scatter',
      name: 'bonding PE',
      line: {color: 'blue'}
    };
    const traceAnti = {
      x: R_auss,
      y: E_a,
      mode: 'lines',
      type: 'scatter',
      name: 'antibonding PE',
      line: {color: 'red'}
    };

    const layout = {
      title: 'Potential Energy',
      xaxis: {title: 'R (Å)', range: [0, 8]},
      yaxis: {title: 'Energy (hartree)', range: [-0.6, 0.4]},
      legend: {x: 0.05, y: 0.95},
      margin: {l: 50, r: 20, t: 50, b: 50}
    };
    Plotly.newPlot('potential-energy-plot-container', [traceBond, traceAnti], layout);
  }

  // Update 3D Surfaces
  function updateSurfaces(R_au) {
    const gridSize = 70; // number of points per axis
    const xMin = -3, xMax = 3, yMin = -3, yMax = 3;
    const dx = (xMax - xMin) / (gridSize - 1);
    const dy = (yMax - yMin) / (gridSize - 1);
    const xVals = new Array(gridSize), yVals = new Array(gridSize);
    for (let i = 0; i < gridSize; i++) {
      xVals[i] = xMin + i * dx;
      yVals[i] = yMin + i * dy;
    }

    const S_ab = (1 + R_au + R_au * R_au / 3) * Math.exp(-R_au);
    const N_b = 1 / Math.sqrt(2 * (1 + S_ab));
    const N_a = 1 / Math.sqrt(2 * (1 - S_ab));

    const zBond = [], zAnti = [];
    for (let ix = 0; ix < gridSize; ix++) {
      const rowB = [], rowA = [];
      const x = xVals[ix];
      const distA_center = R_au / 2;
      for (let iy = 0; iy < gridSize; iy++) {
        const y = yVals[iy];
        const distA = Math.sqrt(Math.pow(x + distA_center, 2) + y * y);
        const distB = Math.sqrt(Math.pow(x - distA_center, 2) + y * y);
        const phiA = Math.exp(-distA);
        const phiB = Math.exp(-distB);
        const psiB = N_b * (phiA + phiB);
        const psiA = N_a * (phiA - phiB);
        rowB.push(psiB * psiB);
        rowA.push(psiA * psiA);
      }
      zBond.push(rowB);
      zAnti.push(rowA);
    }

    const surfaceLayout = {
      showlegend: false,
      autosize: true,
      margin: {l:10, r:10, t:10, b:10},
      scene: {
        xaxis: {range: [-3,3], backgroundcolor: 'rgb(255,255,255)', showgrid: false, ticks: ''},
        yaxis: {range: [-3,3], backgroundcolor: 'rgb(255,255,255)', showgrid: false, ticks: ''},
        zaxis: {range: [0,0.5], backgroundcolor: 'rgb(255,255,255)', showgrid: false, ticks: ''},
        aspectmode: 'manual',
        aspectratio: {x:1, y:1, z:0.5},
        camera: {eye:{x:0.8, y:0.8, z:0.8}}
      }
    };

    const surfaceDataBond = {
      type: 'surface',
      x: xVals,
      y: yVals,
      z: zBond,
      showscale: false,
      colorscale: 'YlOrBr',
      hoverinfo: 'none'
    };
    const surfaceDataAnti = {
      type: 'surface',
      x: xVals,
      y: yVals,
      z: zAnti,
      showscale: false,
      colorscale: 'YlOrBr',
      hoverinfo: 'none'
    };

    // If plot not yet created, create it
    if (!document.getElementById('bonding-plot-container').hasAttribute('data-plotly-filled')) {
      Plotly.newPlot('bonding-plot-container', [surfaceDataBond], surfaceLayout);
      Plotly.newPlot('antibonding-plot-container', [surfaceDataAnti], surfaceLayout);
      document.getElementById('bonding-plot-container').setAttribute('data-plotly-filled', '');
      document.getElementById('antibonding-plot-container').setAttribute('data-plotly-filled', '');
    } else {
      // Update existing plot with new z data
      Plotly.restyle('bonding-plot-container', {'z':[zBond]});
      Plotly.restyle('antibonding-plot-container', {'z':[zAnti]});
    }
  }
});
</script>
</body>
</html>
