      <input type="checkbox" id="check-day-night"><label for="check-day-night">day/night</label>
    </div>
    <div>
      <input type="checkbox" id="check-near-side" checked><label for="check-near-side">near side of the Moon</label>
    </div>
    <div>
      <input type="checkbox" id="check-earth-moon-line" checked><label for="check-earth-moon-line">Earth-Moon line</label>
    </div>
    <div>
      <input type="checkbox" id="check-moon-only"><label for="check-moon-only">the Moon only</label>
    </div>
  </div>
  <div id="visualization-area">
    <canvas id="main-canvas"></canvas>
  </div>
</div>

<!-- p5.js CDN -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.7.0/p5.min.js"></script>
<script>
/* ---------- Global State ---------- */
let time = 0; // hours
const MAX_TIME = 672;
const MIN_TIME = 0;
let playing = false;
let rotationMode = 'normal'; // 'normal','half','double','none'

let showDayNight = false;
let showNearSide = true;
let showEarthMoonLine = true;
let moonOnly = false;

/* ---------- UI Elements ---------- */
const sliderTime = document.getElementById('slider-time');
const btnPlayPause = document.getElementById('btn-play-pause');
const btnStepBack = document.getElementById('btn-step-back');
const btnReset = document.getElementById('btn-reset');
const btnStepForward = document.getElementById('btn-step-forward');

const btnRotHalf = document.getElementById('btn-rot-half');
const btnRotNormal = document.getElementById('btn-rot-normal');
const btnRotDouble = document.getElementById('btn-rot-double');
const btnRotNone = document.getElementById('btn-rot-none');

const chkDayNight = document.getElementById('check-day-night');
const chkNearSide = document.getElementById('check-near-side');
const chkEarthMoonLine = document.getElementById('check-earth-moon-line');
const chkMoonOnly = document.getElementById('check-moon-only');

const displayTime = document.getElementById('display-time');

/* ---------- Helper Functions ---------- */
function clamp(v, min, max) { return Math.min(Math.max(v, min), max); }
function setTime(t) {
  time = clamp(t, MIN_TIME, MAX_TIME);
  sliderTime.value = time;
  updateTimeDisplay();
}
function updateTimeDisplay() {
  const days = Math.floor(time / 24);
  const hours = Math.floor(time % 24);
  displayTime.textContent = `${days} d ${hours} h`;
}
function toggleButtonActive(btn, isActive) {
  if (isActive) btn.classList.add('active'); else btn.classList.remove('active');
}
function updateRotationMode(mode) {
  rotationMode = mode;
  boolPairs = [btnRotHalf, btnRotNormal, btnRotDouble, btnRotNone];
  boolPairs.forEach(btn => toggleButtonActive(btn, btn.id === `btn-rot-${mode}`));
}
function resetSimulation() {
  time = 0;
  sliderTime.value = 0;
  updateTimeDisplay();
  if (!moonOnly) { /* will redraw automatically */ }
}
function stepTime(delta) {
  setTime(time + delta);
}

/* ---------- Event Listeners ---------- */
sliderTime.addEventListener('input', e => setTime(parseInt(e.target.value)));

btnPlayPause.addEventListener('click', () => {
  playing = !playing;
  btnPlayPause.textContent = playing ? '❚❚' : '▶';
});
btnStepBack.addEventListener('click', () => stepTime(-1));
btnReset.addEventListener('click', resetSimulation);
btnStepForward.addEventListener('click', () => stepTime(1));

btnRotHalf.addEventListener('click', () => updateRotationMode('half'));
btnRotNormal.addEventListener('click', () => updateRotationMode('normal'));
btnRotDouble.addEventListener('click', () => updateRotationMode('double'));
btnRotNone.addEventListener('click', () => updateRotationMode('none'));

chkDayNight.addEventListener('change', e => { showDayNight = e.target.checked; });
chkNearSide.addEventListener('change', e => { showNearSide = e.target.checked; });
chkEarthMoonLine.addEventListener('change', e => { showEarthMoonLine = e.target.checked; });
chkMoonOnly.addEventListener('change', e => { moonOnly = e.target.checked; });

/* ---------- p5.js Sketch ---------- */
new p5((s) => {
  const EARTH_RADIUS = 80;
  const MOON_RADIUS = 25;
  const ORBIT_RADIUS = 250;
  const MOON_ORBITAL_PERIOD = 672; // hours
  const EARTH_ROTATIONAL_PERIOD = 24;

  let canvas;
  s.setup = () => {
    const visArea = document.getElementById('visualization-area');
    canvas = s.createCanvas(visArea.clientWidth, visArea.clientHeight);
    canvas.parent('visualization-area');
    s.noStroke();
    s.angleMode(s.RADIANS);
    s.textAlign(s.CENTER, s.CENTER);
    s.frameRate(30);
  };

  s.windowResized = () => {
    const visArea = document.getElementById('visualization-area');
    s.resizeCanvas(visArea.clientWidth, visArea.clientHeight);
  };

  s.draw = () => {
    if (playing) {
      time += 0.5; // advance time
      if (time > MAX_TIME) time = MAX_TIME;
      sliderTime.value = time;
      updateTimeDisplay();
    }
    s.background(0);
    drawScene();
  };

  function drawScene() {
    // Angles
    const earthRot = (time / EARTH_ROTATIONAL_PERIOD) * TWO_PI;
    const moonOrbit = (time / MOON_ORBITAL_PERIOD) * TWO_PI;
    let moonRot = 0;
    if (rotationMode === 'half') moonRot = moonOrbit * 2;
    else if (rotationMode === 'double') moonRot = moonOrbit / 2;
    else if (rotationMode === 'normal') moonRot = moonOrbit;
    // Earth
    if (!moonOnly) {
      s.push();
      s.rotate(earthRot);
      s.fill('#3B82F6'); // Blue
      s.ellipse(0, 0, EARTH_RADIUS * 2);
      s.fill(255); // white for north pole
      s.ellipse(0, -EARTH_RADIUS * 0.8, 10, 10);
      // Simple green landmasses
      s.fill('#22C55E'); // green
      s.beginShape();
      s.vertex(-30, -10);
      s.bezierVertex(-20, 20, 20, 20, 30, -10);
      s.endShape(s.CLOSE);
      // Red stick figure at top
      s.push();
      s.translate(0, -EARTH_RADIUS * 0.5);
      drawRedStick(0, 0, 15);
      s.pop();
      // Day/night overlay
      if (showDayNight) {
        s.fill(0, 0, 0, 128);
        s.arc(0, 0, EARTH_RADIUS * 2, EARTH_RADIUS * 2, HALF_PI, 3 * HALF_PI, s.PIE);
      }
      s.pop();
    }
    // Moon position
    let mx, my;
    if (moonOnly) {
      mx = 0; my = 0;
    } else {
      mx = ORBIT_RADIUS * Math.cos(moonOrbit - HALF_PI);
      my = ORBIT_RADIUS * Math.sin(moonOrbit - HALF_PI);
    }
    // Moon
    s.push();
    s.translate(mx, my);
    s.fill('#808080'); // gray
    s.ellipse(0, 0, MOON_RADIUS * 2);
    if (showNearSide) {
      s.push();
      s.rotate(moonRot);
      s.fill('#22C55E'); // green
      s.arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, PI, TWO_PI, s.PIE);
      // magenta stick figure
      s.push();
      s.translate(-MOON_RADIUS * 0.3, 0);
      drawMagentaStick(0, 0, 8);
      s.pop();
      s.pop();
    }
    // Moon day/night
    if (showDayNight) {
      s.fill(0, 0, 0, 128);
      s.arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, HALF_PI, 3 * HALF_PI, s.PIE);
    }
    s.pop();
    // Earth-Moon line
    if (!moonOnly && showEarthMoonLine) {
      s.push();
      s.stroke(255);
      s.strokeWeight(1);
      s.setLineDash([4, 4]);
      s.line(0, 0, mx, my);
      s.setLineDash([]); // reset
      s.pop();
    }
  }
  function drawRedStick(x, y, size) {
    s.fill('#F87171'); // red
    s.circle(x, y, size);
    s.line(x, y + size, x, y + size + 4);
    s.line(x - 2, y + size, x - 4, y + size + 2);
    s.line(x + 2, y + size, x + 4, y + size + 2);
  }
  function drawMagentaStick(x, y, size) {
    s.fill('#D946EF'); // magenta
    s.circle(x, y, size);
    s.line(x, y + size, x, y + size + 4);
    s.line(x - 2, y + size, x - 4, y + size + 2);
    s.line(x + 2, y + size, x + 4, y + size + 2);
  }
});

</script>
</body>
</html>
